const float M_PI = radians(180.0);
const float icosahedron_radius_by_edge_length = sin(M_PI*2.0/5.0);
const float inversesqrt5 = inversesqrt(5.0);
const vec3 ICOSAHEDRON_VERTICES[12] = vec3[](
    vec3(0.0, -1.0, 0.0),
    vec3(-0.27639320492744446, -inversesqrt5, -0.8506507873535156),
    vec3(-0.8944271802902222, -inversesqrt5, -1.6430361299034693e-16),
    vec3(-0.27639320492744446, -inversesqrt5, 0.8506507873535156),
    vec3(0.7236068248748779, -inversesqrt5, 0.525731086730957),
    vec3(0.7236068248748779, -inversesqrt5, -0.525731086730957),
    vec3(0.27639320492744446, inversesqrt5, -0.8506507873535156),
    vec3(-0.7236068248748779, inversesqrt5, -0.525731086730957),
    vec3(-0.7236068248748779, inversesqrt5, 0.525731086730957),
    vec3(0.27639320492744446, inversesqrt5, 0.8506507873535156),
    vec3(0.8944271802902222, inversesqrt5, 1.6430361299034693e-16),
    vec3(0.0, 1.0, 0.0)
);

const float blending_region = 0.65;
const float planet_radius = 9.0;

bool side_of_half_plane(vec3 direction, int pole_index_a, int pole_index_b) {
    vec3 aux_a = cross(ICOSAHEDRON_VERTICES[pole_index_a], ICOSAHEDRON_VERTICES[pole_index_b]);
    return dot(direction, aux_a) > 0.0;
}

float line_sphere_intersection(vec3 origin, vec3 direction, float radius, out vec3 intersections[2]) {
    float a = dot(direction, direction);
    float b = -dot(direction, origin);
    float c = dot(origin, origin) - radius*radius;
    float d = (b*b)-a*c;
    if(d < 0.0)
        return -1.0;
    d = sqrt(d);
    direction /= a;
    intersections[0] = origin+direction*(b-d);
    intersections[1] = origin+direction*(b+d);
    return b-d;
}

