}*/

vec3 cart_to_bary(vec3 direction, ivec3 pole_indices) {
    vec3 bary;
    for(int i = 0; i < 3; ++i) {
        vec3 b = ICOSAHEDRON_VERTICES[pole_indices[(i+1)%3]];
        vec3 c = ICOSAHEDRON_VERTICES[pole_indices[(i+2)%3]];
        float numerator = dot(direction, cross(b, c));
        float denominator = 1.0+inversesqrt5+dot(direction, b)+dot(direction, c);
        bary[i] = 10.0/M_PI*atan(numerator, denominator);
    }
    bary /= dot(vec3(1.0), bary);
    return bary;
}

vec3 bary_to_cart(vec3 barycentric, ivec3 pole_indices) {
    vec4 planes[3];
    for(int i = 0; i < 3; ++i) {
        vec3 b = ICOSAHEDRON_VERTICES[pole_indices[(i+1)%3]];
        vec3 c = ICOSAHEDRON_VERTICES[pole_indices[(i+2)%3]];
        float tangent = tan(M_PI/10.0*barycentric[i]);
        planes[i].x = tangent*(1.0+inversesqrt5);
        planes[i].yzw = cross(b, c)-tangent*(b+c);
    }
    vec3 l_g0 = planes[0].x * planes[1].yzw - planes[0].yzw * planes[1].x;
    vec3 l_g1 = planes[0].z * vec3(planes[1].w, 0.0, -planes[1].y) + planes[0].w * vec3(-planes[1].z, planes[1].y, 0.0) + planes[0].y * vec3(0.0, -planes[1].w, planes[1].z);
    vec4 p = l_g0.y * vec4(0.0, -planes[2].w, 0.0, planes[2].y) + l_g0.z * vec4(0.0, planes[2].z, -planes[2].y, 0.0) + l_g1.y * vec4(planes[2].z, 0.0, -planes[2].x, 0.0) + l_g1.z * vec4(planes[2].w, 0.0, 0.0, -planes[2].x) + vec4(l_g1.x, -l_g1.x, l_g0.x, -l_g0.x) * planes[2].yxwz;
    return -p.yzw/p.x;
}

vec2 bary_to_texcoord(vec3 barycentric) {
    return vec2(0.5)+0.75*vec2(barycentric.z, (barycentric.x-barycentric.y)/sqrt(3.0));
}

float checker_texture(vec2 texcoord) {
    vec2 repeated = mod(texcoord, vec2(1.0));
    vec2 chequer = smoothstep(0.4, 0.5, repeated) * smoothstep(1.0, 0.95, repeated);
    return abs(1.0-chequer.x-chequer.y);
}

float stripe_texture(float texcoord) {
    return abs(mod(texcoord, 1.0)-0.5)*2.0;
}

const vec3 color_palette[3] = vec3[3](
    vec3(0.7, 0.7, 0.3),
    vec3(0.0, 0.6, 0.0),
    vec3(0.2, 0.6, 0.8)
);
vec3 color_of_cell(ivec3 cell) {
    int residue_class = (cell.x + cell.y * 2) % 3;
    return color_palette[residue_class];
}

