#define deg2rad (3.141/360.)
#define MAX_STEPS 100
#define MAX_DIST 1e2
#define SURF_DIST 1e-4

float sphereSDF(vec3 p, float r){
	return length(p)-r;
}

float cubeSDF(vec3 p, vec3 s) {
    vec3 q = abs(p) - s;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float roundedCubeSDF(vec3 p, vec3 s, float r) {
    vec3 q = abs(p) - s;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0)-r;
}

float roundedCylinderSDF( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

float characterSDF(vec3 p){
	float d = roundedCylinderSDF(p, 0.3, 0.3, .3);//Head
	d = min(d, roundedCubeSDF(p-vec3(0,-1.6,0), vec3(1,1,.5), .05));//Torso
    //p.x = abs(p.x);
	d = min(d, roundedCubeSDF(p-vec3(1.6,-1.6,0), vec3(0.5,1,.5), .05));//left arm
	d = min(d, roundedCubeSDF(p-vec3(0.51,-3.7,0), vec3(0.46,1,.5), .05));//left leg
	d = min(d, roundedCubeSDF(p-vec3(-1.6,-1.6,0), vec3(0.5,1,.5), .05));//right arm
	d = min(d, roundedCubeSDF(p-vec3(-0.51,-3.7,0), vec3(0.46,1,.5), .05));//right leg

	return d;
}

float scene(vec3 p){
	float d = p.y+5.0;
    d = min(d, characterSDF(p));

	return d;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(SURF_DIST, 0.0);

    return normalize(
        scene(p) - vec3(
            scene(p - e.xyy),
            scene(p - e.yxy),
            scene(p - e.yyx)
        )
    );
}

float AO(vec3 p) {
	float occ = 0.0;
    float sca = 1.0;
    vec3 nor = GetNormal(p);
    for(int i = 0; i < 5; i++) {
        float h = 0.001 + 0.15*float(i)/4.0;
        float d = scene(p + h*nor);
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );
}

float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        float dS = scene(ro + rd * dO);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

mat3 rot(vec3 ang) {
    vec3 s = sin(ang);
    vec3 c = cos(ang);
    mat3 x = mat3(
        vec3(1, 0, 0),
        vec3(0, c.x,-s.x),
        vec3(0, s.x, c.x)
    );
    mat3 y = mat3(
        vec3(c.y, 0, s.y),
        vec3(0, 1, 0),
        vec3(-s.y, 0, c.y)
    );
    mat3 z = mat3(
        vec3(c.z, s.z, 0),
        vec3(-s.z, c.z, 0),
        vec3(0, 0, 1)
    );
    return x*y*z;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/max(iResolution.x, iResolution.y)*2.;
	vec3 ro = vec3(0, 0, -5);
    vec3 rd = normalize(vec3(uv, 75.*deg2rad));

    vec2 t = (iMouse.xy-0.5*iResolution.xy)/max(iResolution.x, iResolution.y);
    t *= 4.0;
    vec2 mouse_rotation = t*2.;
    //if(length(touch.xy) < 1e-3) mouse_rotation = vec2(0);

    vec2 yawpitch = vec2(mouse_rotation.x, -mouse_rotation.y);
    mat3 viewmat = rot(vec3(yawpitch.y, yawpitch.x, 0));

    ro *= viewmat;
    rd *= viewmat;

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;
    vec3 col = vec3(0);

    if(d < MAX_DIST){
    	vec3 lp = normalize(vec3(0,5,-3));
    	vec3 n = GetNormal(p);
		col = vec3(smoothstep(-0.2,1.,dot(n, lp)));
        float s = RayMarch(p+n*SURF_DIST*2.0, lp);
        col *= AO(p); 
		if(s < length(lp-p)) col *= vec3(.5);
    }else{
        col = texture(iChannel0, rd.zyx).rgb;
    }

    fragColor = vec4(col, 1.0);
}

float dist()
{
    return iMouse.z > 0.0 ? iMouse.x / iResolution.x : max(sin(iTime / 4.0 * 2.0 * 3.14), 0.0);
}

float threshold(in vec2 uv )
{
    return step(texture(iChannel0, uv).r, dist());
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel1, uv).rgb * threshold(uv);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float getIllumination(in vec3 p, in float k, in float d) {
    return k / (1.0 + dot(p, p) / (d * d));
}

// Short range (up to oclluder at t)
float volumeLightPartial(in vec3 ro, in vec3 rd, in float k, in float d, in float t) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(c - b * b), r = b / h;
    return d * d * k * (atan(t / h + r) - atan(r)) / h;
}

// Infinite range
float volumeLightFull(in vec3 ro, in vec3 rd, in float k, in float d) {
    const float rho = 1.57079632679;
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(c - b * b);
    return d * d * k * (rho - atan(b / h)) / h;
}

float iSphere(in vec3 ro, in vec3 rd, in float r) {
    float b = dot(ro, rd);
    float c = dot(ro, ro) - r * r;
    float h = sqrt(b * b - c);
    if (h < 0.0) return -1.0;
    return -b - sqrt(h);
}

// Modified from https://iquilezles.org/articles/intersectors
float iBox(in vec3 ro, in vec3 rd, in vec3 boxSize, out vec3 outNormal)  {
    vec3 n = ro / rd;
    vec3 k = boxSize / abs(rd);
    vec3 q = abs(ro) - boxSize;
    vec3 t1 = -k - n, t2 = +k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    if (tN > tF || tF < 0.0) return -1.0;
    outNormal = -sign(rd) * step(t1.yzx, t1.xyz) * step(t1.zxy, t1.xyz);
    return mix(tN, tF, step(0.0, -max(q.x, max(q.y, q.z))));
}

// Camera matrix
mat3 getCamera(in vec2 viewDir) {
    vec2 c = cos(viewDir); vec2 s = sin(viewDir);
    return mat3(       c.x, 0.0,         s.x,  // Right
                s.x * -s.y, c.y, -c.x * -s.y,  // Up
                s.x *  c.y, s.y, -c.x *  c.y); // Forward
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    fragColor = vec4(0.0);

    // Camera ray
    mat3 cam = getCamera(vec2(0.65, 0.0));
    vec3 ro = vec3(-2.0, 0.0, 4.5);
    vec3 rd = normalize(cam * vec3(uv, 1.0));

    // Light control
    float k = 0.8 + 0.1 * cos(iTime), d = 0.5;
    vec3 lightColor = vec3(1.0, 0.75, 0.15);

    vec3 lightPos = vec3(0.0);
    if (iMouse.z > 0.0) {
        vec3 md = normalize(cam * vec3(mouse, 1.0));
        lightPos = ro - vec3(md.xy * ro.z / md.z, ro.z);
    }

    // Box control
    vec3 boxSize = vec3(1.0), boxNor;//, dummy;
    vec3 boxPos = vec3(3.0, 0.0, 0.0);

    mat3 boxRot = mat3(1.0); // Identity
    float c = cos(iTime), s = sin(iTime);
    boxRot *= mat3(c, 0.0, -s, 0.0, 1.0, 0.0, s, 0.0, c); // XZ rotation
    boxRot *= mat3(1.0, 0.0, 0.0, 0.0, c, -s, 0.0, s, c); // YZ rotation

    // Intersect box
    vec3 roBox = (ro - boxPos) * boxRot;
    vec3 rdBox = rd * boxRot;
    float tBox = iBox(roBox, rdBox, boxSize, boxNor);

    if (tBox > 0.0) {
        vec3 hitPos = ro + rd * tBox;
        boxNor = boxRot * boxNor;

        // Lighting
        vec3 lightDir = normalize(lightPos - hitPos);
        float diff = max(0.0, dot(lightDir, boxNor));
        float illum = clamp(getIllumination(hitPos - lightPos, k, d), 0.0, 1.0) * diff * 10.0;
        float scatterLight = volumeLightPartial(ro - lightPos, rd, k, d, tBox);
        //if (iBox((hitPos + lightDir * 0.001 - boxPos) * boxRot, lightDir * boxRot, boxSize, dummy) > 0.0) illum = 0.0, scatterLight = 0.0;

        float checkers = 0.5 + 0.5 * mod(dot(floor((roBox + rdBox * tBox) * 2.0 + 0.001), vec3(1.0)), 2.0);
        fragColor.rgb += vec3(1.0, 0.0, 0.0) * checkers * lightColor * illum;
        fragColor.rgb = mix(fragColor.rgb, lightColor, clamp(scatterLight, 0.0, 1.0));
    }

    else {
        fragColor.rgb += volumeLightFull(ro - lightPos, rd, k, d) * lightColor;
    }

    float tSphere = iSphere(ro - lightPos, rd, 0.1);
    if (tSphere > 0.0 && (tBox > 0.0 ? (tSphere < tBox) : true)) {
        fragColor.rgb = 2.0 * lightColor;
    }
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURFACE_DIST 0.01
#define AA 1
float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

vec2 rand2(vec2 p)
{
	vec2 q = vec2(dot(p,vec2(127.1,311.7)), 
		dot(p,vec2(269.5,183.3)));
	return fract(sin(q)*43758.5453);
}

float rand(vec2 p)
{
	return fract(sin(dot(p,vec2(419.2,371.9))) * 833458.57832);
}

float iqnoise(in vec2 pos, float irregular, float smoothness)
{
	vec2 cell = floor(pos);
	vec2 cellOffset = fract(pos);

	float sharpness = 1.0 + 63.0 * pow(1.0-smoothness, 4.0);

	float value = 0.0;
	float accum = 0.0;
	// Sample the surrounding cells, from -2 to +2
	// This is necessary for the smoothing as well as the irregular grid.
	for(int x=-2; x<=2; x++ )
	for(int y=-2; y<=2; y++ )
	{
		vec2 samplePos = vec2(float(y), float(x));

  		// Center of the cell is not at the center of the block for irregular noise.
  		// Note that all the coordinates are in "block"-space, 0 is the current block, 1 is one block further, etc
		vec2 center = rand2(cell + samplePos) * irregular;
		float centerDistance = length(samplePos - cellOffset + center);

		// High sharpness = Only extreme values = Hard borders = 64
		// Low sharpness = No extreme values = Soft borders = 1
		float sam = pow(1.0 - smoothstep(0.0, 1.414, centerDistance), sharpness);

		// A different "color" (shade of gray) for each cell
		float color = rand(cell + samplePos);
		value += color * sam;
		accum += sam;
	}

	return value/accum;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    //line from a to b
    vec3 ab = b-a;

    // line from p to a
    vec3 ap = p-a;

    // as p approaches a, it will be 1
    // as p approaches b it will b 0
    float t = dot(ab,ap) / dot(ab,ab);

    // clamp values between 0 and 1
    t= clamp(t,0.,1.);

    // center point of the capsule
    // start at a (top of capsule)
    // go towards b, t steps
    vec3 c = a + t*ab;

    float d = length(p-c)-r;

    return d;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.));
}
float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
  p = abs(p);
  vec2 b = vec2(la,lb);
  float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
  vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
  return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}
vec2 colMin(vec2 res, vec2 obj){
    res.x = min(obj.x,res.x);
    if(obj.x == res.x){
        res.y = obj.y;
    }
    return res;
}
// https://iquilezles.org/articles/smin
vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// https://iquilezles.org/articles/smin
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}
float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}
float getDisp(vec2 uv){

    vec2 p = 0.5 - 0.5*sin(vec2(1.01,1.71) );
    p = vec2(1.0, 1.0);

	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);

	float f = iqnoise(24.0*uv, p.x, p.y);

	return f;
}
vec2 GetDist(vec3 p){
    vec3 hPos = p;
    hPos.y += pow(sin(iTime),2.)*.1;
    hPos.xz *= Rot(.1*sin(.001*iTime/cos(iTime)));
    //head
    vec2 res = vec2(length(hPos-vec3(0,cos(abs(p.x+3.14159))+.5,0))-0.5,1);
    vec2 ears = vec2(sdCylinder(hPos-vec3(0,-.5,0), vec3(-.6,0,0), vec3(.6,0,0), .1),1);
    res = colMin(res,ears);
    vec2 antennaBase = vec2(sdCylinder(hPos-vec3(0,-.5,0), vec3(-.55,0,0), vec3(.55,0,0), .2),1);
    res = colMin(res,antennaBase);
    vec2 antR = vec2(dBox(hPos-vec3(-.54,0,0), vec3(.005,.5,.2-(sin(p.y*p.y*.6)))),1);
    res = colMin(res,antR);
    vec2 antL = vec2(dBox(hPos-vec3(.54,0,0), vec3(.005,.5,.2-(sin(p.y*p.y*.6)))),1);
    res = colMin(res,antL);

    //eyes
    float blink = sin(iTime)*.04;
    vec3 rotP = hPos-vec3(0,-.4,-0.3);
    rotP.yz *= Rot(3.1415/2.);
    vec2 sock = vec2(sdRhombus(rotP, .3, .1-blink, .2, .001),2);

    rotP = hPos-vec3(0,-.4,-0.4);
    rotP.yz *= Rot(3.1415/2.);
    vec2 sockM = vec2(sdRhombus(rotP, .2, .08-blink, .2, .001),2);
    sock.x = opSmoothSubtraction(sockM.x,sock.x, .01);
    res = colMin(res,sock);

    vec2 eye = vec2(sdRhombus(rotP, .1, .05-blink, .1, .001),3);

    res = colMin(res,eye);

    //body
    vec3 bPos = p;
    bPos.y += cos(0.001*iTime/sin(iTime))*.01;
    vec2 body = vec2(length(bPos-vec3(0,5.*cos(abs(p.x+3.14159))+3.2,0))-0.5,1);
    res = colMin(res,body);
    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x*.5;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            dO.y= -1.;
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,5,-5);

    lightPos.xz += vec2(sin(iTime),cos(iTime));
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= 0.1;
    }
    return diff;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
vec3 headCol(vec3 p, vec3 n, vec3 rd){
        vec3 colXZ = texture(iChannel0, p.xz*.5+.5).rgb;
        vec3 colXY = texture(iChannel0, p.xy*.5+.5).rgb;
        vec3 colYZ = texture(iChannel0, p.yz*.5+.5).rgb;

        vec3 refl = texture(iChannel1, rd).rgb;
        refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);
        vec3 metal = colXZ*abs(n.y) + colXY*abs(n.z) + colYZ*abs(n.x);
        metal= mix(smoothstep(0.6,.8,metal.r)*refl*5.,metal,.8);
        return metal;


}
