// "Flag From Down Under" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Flag of Australia (derived from "Union Jack Flag")

#define AA  1   // optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec4 Loadv4 (vec2 vId);

const ivec2 nBallE = ivec2 (65, 33);

const int nsMax = 5000;

vec3 sunDir;
vec2 qgHit;
float dstFar, tCur, todCur, spLen, szFac;
const float pi = 3.14159;

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

float SheetRay (vec3 ro, vec3 rd)
{
  vec3 r, q;
  vec2 g;
  float d, dMin, sz, szMax, szMin, grLen;
  bvec2 ilt;
  bool bkTrak;
  grLen = 0.92 * spLen;
  dMin = dstFar / (grLen * szFac);
  ro /= szFac;
  rd /= grLen;
  szMax = float (nBallE.x - 1);
  szMin = 1./32.;
  sz = szMax;
  g = vec2 (0.);
  bkTrak = false;
  for (int ns = VAR_ZERO; ns < nsMax; ns ++) {
    if (sz == szMin) {
      r = GetR (g) - ro;
      q = cross (r, rd);
      if (dot (q, q) < sz * sz) {
        d = dot (r, rd);
        if (d < dMin) {
          dMin = d;
          qgHit = g;
        }
      }
      bkTrak = true;
    }
    q = cross (GetR (g + 0.5 * sz) - ro, rd);
    bkTrak = bkTrak || (dot (q, q) > sz * sz);
    if (bkTrak) {
      bkTrak = false;
      ilt = lessThan (fract (g / (2. * sz)), vec2 (0.5));
      g.x += ilt.x ? sz : - sz;
      if (! ilt.x) {
        if (sz == 0.5 * szMax) break;
        g.y += ilt.y ? sz : - sz;
        if (! ilt.y) {
          sz *= 2.;
          if (sz == szMax) break;
          bkTrak = true;
        }
      }
    } else if (sz > szMin) sz *= 0.5;
  }
  return dMin * grLen * szFac;
}

vec3 GetRC (vec2 v)
{
  return GetR (clamp (v, vec2 (0.), vec2 (nBallE - 1)));
}

vec3 SheetNf () 
{
  vec3 c;
  vec2 e = vec2 (1., 0.);
  c = GetRC (qgHit);
  return normalize (cross (GetRC (qgHit + e.yx) - c, GetRC (qgHit + e) - c));
}

float SheetSilHit (vec3 ro, vec3 rd)
{
  vec3 blkSize, pMin, pMax, v, tm, tp;
  float dn, df;
  pMin = Loadv4 (vec2 (1, nBallE.y)).xyz;
  pMax = Loadv4 (vec2 (2, nBallE.y)).xyz;
  ro /= szFac;
  ro -= 0.5 * (pMin + pMax);
  blkSize = 0.5 * (pMax - pMin) + 0.5;
  v = ro  / rd;
  tp = blkSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  return (df > 0. && dn < df) ? szFac * dn : dstFar;
}

vec4 CylHit (vec3 ro, vec3 rd, float cylRad, float cylLen)
{
  vec3 s, vn;
  float dMin, d, a, b, w, srdy;
  dMin = dstFar;
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - cylRad * cylRad);
  if (w > 0.) {
    srdy = sign (rd.y);
    if (a > 0.) {
      d = - b - sqrt (w);
      if (d > 0.) {
        d /= a;
        s = ro + d * rd;
      } else s.y = cylLen;
    } else s.y = cylLen;
    if (abs (s.y) < cylLen) {
      dMin = d;
      vn = vec3 (s.xz / cylRad, 0.).xzy;
    } else if (srdy * ro.y < - cylLen) {
      d = - (srdy * ro.y + cylLen) / abs (rd.y);
      if (d > 0.) {
        s = ro + d * rd;
        if (length (s.xz) < cylRad) {
          dMin = d;
          vn = vec3 (0., 0., - srdy).xzy;
        }
      }
    }
  }
  return vec4 (dMin, vn);
}

vec3 BgCol (vec3 rd)
{
  vec3 col;
  vec2 u;
  float el, f;
  rd.xz = Rot2D (rd.xz, 0.01 * tCur);
  el = asin (rd.y);
  u = vec2 (atan (rd.z, - rd.x) + pi, tan (2. * atan (0.5 * el))) / (2. * pi);
  f = 64.;
  col = mix (vec3 (0., 0.1, 0.3), vec3 (0.8), mix (Fbm2 (f * u),
     Fbm2 (f * (u - vec2 (1., 0.))), u.x));
  col = mix (col, vec3 (0.2, 0.3, 0.4), smoothstep (0.95, 0.98, abs (el) / (0.5 * pi)));
  return col;
}

vec3 SheetCol (float s)
{  // specs from Wikipedia
  vec4 pStar[6];
  vec3 col, colB, colR, colW;
  vec2 p, q;
  float w;
  colB = vec3 (0., 0., 139. / 255.);
  colR = vec3 (1., 0., 0.);
  colW = vec3 (1.);
  col = colB;
  p = (qgHit / (0.5 * float (nBallE.y - 1)) - 1.);
  if (abs (p.x) < 1. && abs (p.y - 0.5) < 0.5) {
    p *= 2.;
    p.y -= 1.;
    w = 1./15.;
    q = Rot2D (p, atan (0.5));
    col = mix (colW, col, smoothstep (0., 0.02, abs (q.y) - 3. * w));
    col = mix (colR, col, smoothstep (0., 0.02, abs (q.y - sign (p.x) * w) - w));
    q = Rot2D (p, atan (-0.5));
    col = mix (colW, col, smoothstep (0., 0.02, abs (q.y) - 3.* w));
    col = mix (colR, col, smoothstep (0., 0.02, abs (q.y - sign (p.x) * w) - w));
    q = p;
    col = mix (colW, col, smoothstep (0., 0.02, Minv2 (abs (q)) - 5.* w));
    col = mix (colR, col, smoothstep (0., 0.02, Minv2 (abs (q)) - 3.* w));
    col = mix (col, colB, smoothstep (-0.01, 0., Maxv2 (abs (q) - vec2 (2., 1.))));
  } else {
    p += 1.;
    pStar[0] = vec4 (1./4., 1./4., 3./20., 7.);
    pStar[1] = vec4 (3./4., 1./6., 1./14., 7.);
    pStar[2] = vec4 (5./8., 9./16., 1./14., 7.);
    pStar[3] = vec4 (3./4., 5./6., 1./14., 7.);
    pStar[4] = vec4 (31./36., 151./240., 1./14., 7.);
    pStar[5] = vec4 (4./5., 11./24., 1./24., 5.);
    for (int k = 0; k < 6; k ++) {
      q = p - vec2 (4., 2.) * pStar[k].xy;
      if (length (q) < 2. * pStar[k].z + 0.02) {
        q = Rot2D (q, ((pStar[k].w == 7.) ? pi : 0.) + 0.5 * pi / pStar[k].w);
        q = Rot2D (q, 2. * pi * (floor (pStar[k].w * ((length (q) > 0.) ? atan (q.y, - q.x) : 0.) /
           (2. * pi) + 0.5) / pStar[k].w));
        col = mix (colW, col, smoothstep (0., 0.01, dot (vec2 (abs (q.y), - q.x - 2. * pStar[k].z),
           sin (0.11 * pi + vec2 (0.5 * pi, 0.)))));
      }
    }
  }
  col = mix (col, vec3 (1.), 0.1);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, dc4;
  vec3 col, vn, q;
  float dstObj, dstSheet, s, flSize;
  szFac = 4. / float (nBallE.y - 1);
  flSize = szFac * 0.5 * float (nBallE.y + 1);
  q = ro;
  q.y -= - 2. * flSize;
  dc4 = CylHit (q, rd, 0.06, 3. * flSize + 0.2);
  dstObj = dc4.x;
  q = ro;
  q.x -= 2. * flSize + 0.06;
  dstSheet = (SheetSilHit (q, rd) < dstFar) ? SheetRay (q, rd) : dstFar;
  if (min (dstSheet, dstObj) < dstFar) {
    if (dstSheet < dstObj) {
      vn = SheetNf ();
      s = sign (dot (rd, vn));
      if (s > 0.) vn = - vn;
      col4 = vec4 (SheetCol (s), 0.03);
    } else {
      ro += dstObj * rd;
      vn = dc4.yzw;
      col4 = vec4 (0.8, 0.8, 0.85, 0.3);
      if (abs (ro.y) < flSize + 0.1) col4 *= (0.5 + 0.5 * smoothstep (0.05, 0.1,
         abs (mod (4. * (ro.y + 0.02) / flSize + 0.5, 1.) - 0.5)));
    }
    col = col4.rgb * (0.3 + 0.7 * max (dot (vn, sunDir), 0.)) +
       col4.a * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
  } else col = BgCol (rd);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2.2 * pi * mPtr.x;
    el += 0.4 * pi * mPtr.y;
  }
  el = clamp (el, -0.2 * pi, 0.2 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -0.7, -20.);
  ro.x += 4.;
  stDat = Loadv4 (vec2 (0, nBallE.y));
  spLen = stDat.x;
  dstFar = 60.;
  sunDir = vuMat * normalize (vec3 (-0.5, 1., -1.));
  col = vec3 (0.);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 5.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

