// "Flag From Down Under" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
float Fbm1 (float p);
vec4 Loadv4 (vec2 vId);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);

const ivec2 nBallE = ivec2 (65, 33);

vec3 pMin, pMax;
ivec2 idNeb[4], idNebD[4];
float tCur, spLen, nStep;
const float pi = 3.14159;

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

vec3 GetV (vec2 v)
{
  return Loadv4 (vec2 (v.x + float (nBallE.x), v.y)).xyz;
}

vec3 SpringForce (ivec2 iv, vec3 r, vec3 v)
{
  vec3 dr, f;
  ivec2 ivn;
  float spLenD, fSpring, fSpringD, fDamp;
  fSpring = 400.;
  fDamp = 1.;
  f = vec3 (0.);
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNeb[n];
    if (ivn.y >= 0 && ivn.y < nBallE.y && ivn.x >= 0 && ivn.x < nBallE.x) {
      dr = r - GetR (vec2 (ivn));
      f += fSpring * (spLen - length (dr)) * normalize (dr) -
         fDamp * (v - GetV (vec2 (ivn)));
    }
  }
  fSpringD = 3. * fSpring;
  spLenD = spLen * sqrt(2.);
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNebD[n];
    if (ivn.y >= 0 && ivn.y < nBallE.y && ivn.x >= 0 && ivn.x < nBallE.x) {
      dr = r - GetR (vec2 (ivn));
      f += fSpringD * (spLenD - length (dr)) * normalize (dr) -
         fDamp * (v - GetV (vec2 (ivn)));
    }
  }
  return f;
}

vec3 BendForce (ivec2 iv, vec3 r)
{
  vec3 dr1, dr2, rt, f;
  ivec2 ivd, ivp, ivm;
  float s, c11, c22, c12, cd;
  bool doInt;
  f = vec3 (0.);
  for (int nd = 0; nd < 2; nd ++) {
    ivd = (nd == 0) ? ivec2 (1, 0) : ivec2 (0, 1);
    for (int k = 0; k < 3; k ++) {
      doInt = false;
      if (nd == 0) {
        if (k == 0 && iv.x > 1) {
          ivp = iv - ivd;
          ivm = ivp - ivd;
          doInt = true;
        } else if (k == 2 && iv.x < nBallE.x - 2) {
          ivm = iv + ivd;
          ivp = ivm + ivd;
          doInt = true;
        } else if (k == 1 && (iv.x > 0 && iv.x < nBallE.x - 1)) {
          ivp = iv + ivd;
          ivm = iv - ivd;
          doInt = true;
        }
      } else {
        if (k == 0 && iv.y > 1) {
          ivp = iv - ivd;
          ivm = ivp - ivd;
