vec3 camera_rd(vec3 camera_p, vec3 camera_t, float camera_fov_h, vec2 uv, vec2 resolution_uv) {
    vec3 t_p = camera_t - camera_p;
    float length_t_p = length(t_p);

    t_p = normalize(t_p);
    vec3 x_axis = normalize(cross(t_p, vec3(0.,-1.,0.)));
    vec3 y_axis = normalize(cross(t_p, x_axis));


    float length_x_axis = tan(camera_fov_h/2.0) * length_t_p;
    float length_y_axis = resolution_uv.y/resolution_uv.x * length_x_axis;

    vec3 rayTarget = camera_t + x_axis * (uv.x / resolution_uv.x * length_x_axis) + y_axis * (uv.y / resolution_uv.y * length_y_axis);


    return normalize(rayTarget - camera_p);
}



bool iAABB(in vec3 ro, in vec3 rd, in vec3 minBound, in vec3 maxBound)
{
    float tmin = (minBound.x - ro.x) / rd.x; 
    float tmax = (maxBound.x - ro.x) / rd.x; 

    if (tmin > tmax) {
        //swap
        float t = tmin;
        tmin = tmax;
        tmax = t;
    } 

    float tymin = (minBound.y - ro.y) / rd.y; 
    float tymax = (maxBound.y - ro.y) / rd.y; 

    if (tymin > tymax) {
        //swap
        float ty = tymin;
        tymin = tymax;
        tymax = ty;
    }

    if ((tmin > tymax) || (tymin > tmax)) 
        return false; 

    if (tymin > tmin) 
        tmin = tymin; 

    if (tymax < tmax) 
        tmax = tymax; 

    float tzmin = (minBound.z - ro.z) / rd.z; 
    float tzmax = (maxBound.z - ro.z) / rd.z; 

    if (tzmin > tzmax) {
        //swap
        float tz = tzmin;
        tzmin = tzmax;
        tzmax = tz;
    }

    if ((tmin > tzmax) || (tzmin > tmax)) 
        return false; 

    if (tzmin > tmin) 
        tmin = tzmin; 

    if (tzmax < tmax) 
        tmax = tzmax; 

    return true; 
}

bool iBox( in vec3 ro, in vec3 rd, in vec3 rad, out float tNear, out float tFar ) 
{
    // convert from ray to box space
	//vec3 rdd = (txx*vec4(rd,0.0)).xyz;
	//vec3 roo = (txx*vec4(ro,1.0)).xyz;

	// ray-box intersection in box space
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

	if( tN > tF || tF < 0.0) return false;

	//vec3 nor = -sign(rdd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);

    // convert to ray space

	//nor = (txi * vec4(nor,0.0)).xyz;

    tNear = tN;
    tFar = tF;

	return true;
}


float noiseNew( in vec3 x )
{
    return texture( iChannel0, x/32.0 ).x;    // <---------- Sample a 3D texture!
}


vec3 average(vec3 nearPoint, vec3 farPoint, float steps) {
    vec3 col = vec3(0.);

    vec3 d = farPoint - nearPoint;
    float t = 0.;
    for (float i=0.0; i < steps; i++) {
        vec3 p = nearPoint + (i/steps) * d;
        float density = noiseNew(p*10.);
        //if (density > 0.5) {
        //    t+=1.;
        //}
        t += density * (1.-length(p)*1.);
    }

    return vec3(t/steps*2.);
}



vec3 volRayCast(vec3 ro, vec3 rd)
{
    float tN,tF;
    bool inter = iBox(ro, rd, vec3(1.), tN, tF);

    if (inter){
        vec3 nearPoint = ro + tN*rd;
        vec3 farPoint = ro + tF*rd;
        //float d = distance(nearPoint, farPoint);
        //return vec3(d/6.);
        return average(nearPoint, farPoint, 100.);
    }

    return vec3(0.);

}


bool iCloud(in vec3 ro, in vec3 rd, out float nearInt)
{
    float epsilon = 0.01;
    nearInt = 100000.; //Big Number
    float tN,tF;
    bool inter = iBox(ro, rd, vec3(1.), tN, tF);

    for (float t = tN; t < tF; t += epsilon) {
        vec3 p = ro + t * rd;
        float density = noiseNew(p*2.);
        if (density > 0.8) {
            nearInt = t;
            return true;
        }
    }
    return false;
}


vec3 pointNormal(vec3 p, vec3 rd)
{
    float epsilone = 0.001;

    float tX;
    vec3 roX = p + vec3(epsilone, 0., 0.);
    iCloud(roX, rd, tX);
    vec3 pX = roX + rd * tX;

    float tY;
    vec3 roY = p + vec3(0., epsilone, 0.);
    iCloud(roY, rd, tY);
    vec3 pY = roY + rd * tY;

    float tZ;
    vec3 roZ = p + vec3(0., 0., epsilone);
    iCloud(roZ, rd, tZ);
    vec3 pZ = roZ + rd * tZ;

    vec3 x = normalize(pX - p);
    vec3 y = normalize(pY - p);
    vec3 z = normalize(pZ - p);

    return cross(x, y);

}

vec3 getShading(vec3 p, vec3 n) {
    vec3 light1_p = vec3(10.,10.,10.);

    vec3 col;

    col += clamp(0.,1.,dot(n, normalize(light1_p-p)));

    return col;
}


