/*
Real time PBR Volumetric Clouds by robobo1221.
Single scattering
Also includes volumetric light.
http://shadertoy.com/user/robobo1221

First ever somewhat PBR effect I decided to work on.
It uses the same algorithm to calculate the worldPosition as in: https://www.shadertoy.com/view/lstfR7

Feel free to fork and edit it. (Credit me please.)
Hope you enjoy!
*/

#define CLOUDS 0
#define WAVE_DETAIL 1
#define cloudSpeed 0.02
#define cloudHeight 3000.0
#define cloudThickness 1000.0
#define cloudDensity 0.01

#define fogDensity 0.0001

#define volumetricCloudSteps 32			//Higher is a better result with rendering of clouds.
#define volumetricLightSteps 1			//Higher is a better result with rendering of volumetric light.

#define cloudShadowingSteps 1			//Higher is a better result with shading on clouds.


#define rayleighCoeff (vec3(0.27, 0.5, 1.0) * 1e-5)	//Not really correct
#define mieCoeff vec3(0.5e-6)						//Not really correct

const float sunBrightness = 10.0;

#define earthRadius 6371000.0
float waterlevel = 70.0;        // height of the water
float wavegain   = 1.0;       // change to adjust the general water wave level
float large_waveheight = 1.0; // change to adjust the "heavy" waves (set to 0.0 to have a very still ocean :)
const float small_waveheight = 1.0; // change to adjust the small waves



///////////////////////////////////////////////////////////////////////////////////

#define d0(x) (abs(x)+ 1e-8 )
#define d02(x) (abs(x) + 1e-3)

const vec3 totalCoeff = rayleighCoeff + mieCoeff;

vec3 scatter(vec3 coeff, float depth){
	return coeff * depth;
}

vec3 absorb(vec3 coeff, float depth){
	return exp2(scatter(coeff, -depth));
}

float calcParticleThickness(float depth){

    depth = depth * 2.0;
    depth = max(depth + 0.01, 0.01);
    depth = 1.0 / depth;

	return 100000.0 * depth;   
}

float calcParticleThicknessH(float depth){

    depth = depth * 2.0 + 0.1;
    depth = max(depth + 0.01, 0.01);
    depth = 1.0 / depth;

	return 100000.0 * depth;   
}

float calcParticleThicknessConst(const float depth){

	return 100000.0 / max(depth * 2.0 - 0.01, 0.1);   
}

float rayleighPhase(float x){
	return 0.375 * (1.0 + x*x);
}

float hgPhase(float x, float g)
{
    float g2 = g*g;
	return 0.25 * ((1.0 - g2) * pow(1.0 + g2 - 2.0*g*x, -1.5));
}

float miePhaseSky(float x, float depth)
{
 	return hgPhase(x, exp2(-0.000003 * depth));
}

float powder(float od)
{
	return 1.0 - exp2(-od * 2.0);
}

float calculateScatterIntergral(float opticalDepth, float coeff){
    float a = -coeff * rLOG2;
    float b = -1.0 / coeff;
    float c =  1.0 / coeff;

    return exp2(a * opticalDepth) * b + c;
}

vec3 calculateScatterIntergral(float opticalDepth, vec3 coeff){
    vec3 a = -coeff * rLOG2;
    vec3 b = -1.0 / coeff;
    vec3 c =  1.0 / coeff;

    return exp2(a * opticalDepth) * b + c;
}


vec3 calcAtmosphericScatter(positionStruct pos, out vec3 absorbLight){
    const float ln2 = log(2.0);

    float lDotW = dot(pos.sunVector, pos.worldVector);
    float lDotU = dot(pos.sunVector, vec3(0.0, 1.0, 0.0));
    float uDotW = dot(vec3(0.0, 1.0, 0.0), pos.worldVector);
    float m = 1.;
    if(uDotW<0.){
     m = 0.5*(1.+abs(uDotW));
     uDotW*=-1.;

    }

	float opticalDepth = calcParticleThickness(uDotW);
    float opticalDepthLight = calcParticleThickness(lDotU);

    vec3 scatterView = scatter(totalCoeff, opticalDepth);
    vec3 absorbView = absorb(totalCoeff, opticalDepth);

    vec3 scatterLight = scatter(totalCoeff, opticalDepthLight);
         absorbLight = absorb(totalCoeff, opticalDepthLight);

    vec3 absorbSun =  abs(absorbLight - absorbView) / d0((scatterLight - scatterView) * ln2);

    vec3 mieScatter = scatter(mieCoeff, opticalDepth) * miePhaseSky(lDotW, opticalDepth);
    vec3 rayleighScatter = scatter(rayleighCoeff, opticalDepth) * rayleighPhase(lDotW);

    vec3 scatterSun = mieScatter + rayleighScatter;

    vec3 sunSpot = smoothstep(0.9996, 0.99993, lDotW) * absorbView * sunBrightness;

    return (scatterSun * absorbSun + sunSpot) * sunBrightness * m;
}

vec3 calcAtmosphericScatterTop(positionStruct pos){
    const float ln2 = log(2.0);

    float lDotU = dot(pos.sunVector, vec3(0.0, 1.0, 0.0));

	float opticalDepth = calcParticleThicknessConst(2.5);
    float opticalDepthLight = calcParticleThickness(lDotU);

    vec3 scatterView = scatter(totalCoeff, opticalDepth);
    vec3 absorbView = absorb(totalCoeff, opticalDepth);

    vec3 scatterLight = scatter(totalCoeff, opticalDepthLight);
    vec3 absorbLight = absorb(totalCoeff, opticalDepthLight);

    vec3 absorbSun = d02(absorbLight - absorbView) / d02((scatterLight - scatterView) * ln2);

    vec3 mieScatter = scatter(mieCoeff, opticalDepth) * 0.25;
    vec3 rayleighScatter = scatter(rayleighCoeff, opticalDepth) * 0.375;

    vec3 scatterSun = mieScatter + rayleighScatter;

    return (scatterSun * absorbSun) * sunBrightness;
}

float Get3DNoise(vec3 pos) 
{
    //return 1.;
    float p = floor(pos.z);
    float f = pos.z - p;

    const float invNoiseRes = 1.0 / 64.0;

    float zStretch = 17.0 * invNoiseRes;

    vec2 coord = pos.xy * invNoiseRes + (p * zStretch);

    vec2 noise = vec2(texture(iChannel3, coord).x,
					  texture(iChannel3, coord + zStretch).x);

    return mix(noise.x, noise.y, f);
}

float getClouds(vec3 p)
{
    p = vec3(p.x, length(p + vec3(0.0, earthRadius, 0.0)) - earthRadius, p.z);



    float time = iTime * cloudSpeed;
    vec3 movement = vec3(time, 0.0, time);

    vec3 cloudCoord = (p * 0.001) + movement;
    vec4 noise_weight = vec4(0.5, 0.25, 0.125, 0.0625);
	vec4 noise = vec4(
            Get3DNoise(cloudCoord),
    	  Get3DNoise(cloudCoord * 2.0 + movement),
    	  Get3DNoise(cloudCoord * 7.0 - movement),
    	  Get3DNoise((cloudCoord + movement) * 16.0));

    //noise *= 1./0.8;
    const float top = 0.004;
    const float bottom = 0.01;

    float horizonHeight = p.y - cloudMinHeight;
    float treshHold = (1.0 - exp2(-bottom * horizonHeight)) * exp2(-top * horizonHeight);

    float clouds = smoothstep(0.55, 0.6, dot(noise, noise_weight));
          clouds *= treshHold;

    return clouds * cloudDensity;
}


float getSunVisibility(vec3 p, positionStruct pos)
{
	const int steps = cloudShadowingSteps;
    const float rSteps = cloudThickness / float(steps);

    vec3 increment = pos.sunVector * rSteps;
    vec3 position = increment * 0.5 + p;

    float transmittance = 0.0;

    for (int i = 0; i < steps; i++, position += increment)
    {
		transmittance += getClouds(position);
    }

    return exp2(-transmittance * rSteps);
}

float phase2Lobes(float x)
{
    const float m = 0.6;
    const float gm = 0.8;

	float lobe1 = hgPhase(x, 0.8 * gm);
    float lobe2 = hgPhase(x, -0.5 * gm);

    return mix(lobe2, lobe1, m);
}

vec3 getVolumetricCloudsScattering(
    float opticalDepth, float phase, vec3 p, vec3 sunColor, vec3 skyLight, positionStruct pos)
{
    float intergal = calculateScatterIntergral(opticalDepth, 1.11);

    float beersPowder = powder(opticalDepth * log(2.0));

	vec3 sunlighting = (sunColor * getSunVisibility(p, pos) * beersPowder) * phase * hPi * sunBrightness;
    vec3 skylighting = skyLight * 0.25 * rPi;

    return (sunlighting + skylighting) * intergal * pi;
}

float getHeightFogOD(float height)
{
	const float falloff = 0.001;

    return exp2(height * falloff) * fogDensity;
}


vec3 calculateVolumetricClouds(positionStruct pos, vec3 color, float dither, vec3 sunColor)
{
	const int steps = volumetricCloudSteps;
    const float iSteps = 1.0 / float(steps);

    if (pos.worldVector.y < 0.0)
       return color;

    float bottomSphere = rsi(vec3(0.0, 1.0, 0.0) * earthRadius, pos.worldVector, earthRadius + cloudMinHeight).y;
    float topSphere = rsi(vec3(0.0, 1.0, 0.0) * earthRadius, pos.worldVector, earthRadius + cloudMaxHeight).y;

    vec3 startPosition = pos.worldVector * bottomSphere;
    vec3 endPosition = pos.worldVector * topSphere;

    vec3 increment = (endPosition - startPosition) * iSteps;
    vec3 cloudPosition = increment * dither + startPosition;

    float stepLength = length(increment);

    vec3 scattering = vec3(0.0);
    float transmittance = 1.0;

    float lDotW = dot(pos.sunVector, pos.worldVector);
    float phase = phase2Lobes(lDotW);

    vec3 skyLight = calcAtmosphericScatterTop(pos);

    for (int i = 0; i < steps; i++, cloudPosition += increment)
    {
        float opticalDepth = getClouds(cloudPosition) * stepLength;

        if (opticalDepth <= 0.0)
            continue;

		scattering += getVolumetricCloudsScattering(opticalDepth, phase, cloudPosition, sunColor, skyLight, pos) * transmittance;
        transmittance *= exp2(-opticalDepth);
    }

    return mix(color * transmittance + scattering, color, clamp(length(startPosition) * 0.00001, 0.0, 1.0));
}

vec3 robobo1221Tonemap(vec3 color)
{
    #define rTOperator(x) (x / sqrt(x*x+1.0))

    float l = length(color);

    color = mix(color, color * 0.5, l / (l+1.0));
    color = rTOperator(color);

    return color;
}
float voronoise( in vec2 p, in float t_metric)
{
    const vec2 temp = 0.5 - 0.5 * -1.8*vec2(1.0,0.5) ;
    const float u = temp.x;
    const float v = temp.y;
	float k = 1.0+63.0*pow(1.0-v,6.0);

    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 t = 10.*vec2(cos(0.001*t_metric), cos(0.002*t_metric+0.34));
	vec2 a = vec2(0.0,0.0);
    const int stepr = 2;
    for( int y=-stepr; y<=stepr; y++ )
    for( int x=-stepr; x<=stepr; x++ )
    {
        vec2  g = vec2( x, y );
		vec3  o = texture(iChannel1, (i + g)* 0.5/64.+ 0.5*t).rgb;
		vec2  d = g - f + o.xy;
		float w = pow( 1.0-smoothstep(0.0,1.414,length(d)), k );
		a += vec2(o.z*w,w);
    }
    return a.x/a.y;
}
vec3 calcFlatClouds(positionStruct pos,in vec3 color, in vec3 sunColor)
{
    float distance_metric = tan(0.4*3.1415926*length(pos.worldVector.xz))+1.;
    vec2 p = distance_metric*pos.worldVector.xz*24.;
    float time_metric = 0.1*(iTime+0.);

    vec2 un = vec2(0.01,0.00432)*p;
    float mixer = .8-length(sin(un+vec2(0.+0.005*time_metric))+sin(un+vec2(2.+0.005*time_metric)));

    return mix(color,10.*sunColor,smoothstep(0.9-smoothstep(0.,1.3,abs(mixer)),1.1,mix(voronoise(p*10., 0.),voronoise(p, time_metric),0.7)));
}
vec3 getSky(in positionStruct pos )
{

    float dither = bayer16(pos.texcoord*iResolution.xy);

    vec3 lightAbsorb = vec3(0.0);

    vec3 color = vec3(0.0);
         color = calcAtmosphericScatter(pos, lightAbsorb);
#if CLOUDS
         color = calcFlatClouds(pos, color, lightAbsorb);
         color = calculateVolumetricClouds(pos, color, dither, lightAbsorb);
#endif

    return color;
}

float hash( vec2 p ) {
    return fract(sin(dot(p,vec2(1177.1,2711.7)))*43758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f * f * (3.0-2.0 * f);
    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


float wv(in vec2 uv, vec2 d, float t, float A)
{
    return sin ( dot(d, uv) / 4.0 + t) * A;
}

// water
float fbm(in vec2 uv, float lod)
{	
    float f =  textureLod(iChannel2, uv, lod).r * 0.5;
    	  f += textureLod(iChannel2, uv*2.0, lod).r * 0.5*0.5;
    	  f += textureLod(iChannel2, uv*4.0, lod).r * 0.5*0.5*0.5;
    	  f += textureLod(iChannel2, uv*8.0, lod).r * 0.5*0.5*0.5*0.5;

    f = f*f*f*f;
    return f;
}
const mat2 m2 = mat2(1.6,-1.2,1.2,1.6);
float noiseo(vec2 p)
{
  return textureLod(iChannel0,p*vec2(1./256.),0.0).x;
}
// Fractional Brownian motion
float fbm( vec2 p )
{
  float f = 0.5000*noiseo( p ); p = m2*p;
  f += 0.2500*noiseo( p ); p = m2*p;
  f += 0.1666*noiseo( p ); p = m2*p;
  f += 0.0834*noiseo( p );
  return f;
}
float wavesLo(const in vec3 rp, float t, float A)
{
    vec2 p = rp.xz*100.1;

  vec2 shift2 = 0.001*vec2( iTime*190.0*2.0, -iTime*130.0*2.0 );

  // coarse crossing 'ocean' waves...
  float wave = 0.0;
  wave += sin(p.x*0.021  + shift2.x)*4.5;
  wave += sin(p.x*0.0172+p.y*0.010 + shift2.x*1.121)*4.0;
  wave -= sin(p.x*0.00104+p.y*0.005 + shift2.x*0.121)*4.0;
  // ...added by some smaller faster waves...
  wave += sin(p.x*0.02221+p.y*0.01233+shift2.x*3.437)*5.0;
  wave += sin(p.x*0.03112+p.y*0.01122+shift2.x*4.269)*2.5 ;
  wave *= 0.01;//large_waveheight;
  //wave -= fbm(p*0.004-shift2*.5)*small_waveheight*24.;
  return wave;
}

// high detail waves
const mat2 m_0 = mat2(1.6,-1.2,1.2,1.6)*0.9331;
float waves(const in vec3 rp, float t, float A)
{
#if WAVE_DETAIL
    const float small_waveheight = 0.015;
     vec2 shift1 = 0.3*0.001*vec2( iTime*160.0*2.0, iTime*120.0*2.0 );
     mat4x2 shift2 = mat4x2(shift1, shift1, shift1, shift1);
     const mat4x2 shiftmul = mat4x2(
         vec2(1.),
         vec2(1.841),
         vec2(1.841)*vec2(1.841),
         vec2(1.841)*vec2(1.841)*vec2(1.841));
    shift2 = matrixCompMult(shift2, shiftmul);
    vec2 p = rp.xz;
  mat4x2 pesa = mat4x2(p, p*m2, p*(m2*m2), p*(m2*m2*m2));
  const float amp = 6.*small_waveheight;
  vec4 noisevals = vec4(0.);
  const vec4 amps = vec4(amp, amp*.51, amp*(.51*.51),amp*(.51*.51*.51));
  mat4x2 key = 1.1*pesa + shift2;
  for (int i=0; i<4; i++)
  {
    noisevals[i] = noise(key[i]);
  }
  float wave = dot(abs(sin((noisevals-.5)*3.14))*amps, vec4(1.));
    return wavesLo(rp, t, A) - wave;// +w3+w4+w7+w8+w5+w6;
#else
    return wavesLo(rp, t, A);
#endif
}

float mapLo(in vec3 rp)
{
    return rp.y - wavesLo(rp, 2.0+iTime*.5, .04);
}


float map(in vec3 rp)
{
    return rp.y - waves(rp, 2.0+iTime*.5, .04);
}


vec3 grad(vec3 rp, float preci)
{
    vec2 off = vec2(preci, 0.0);
    vec3 g = vec3(map(rp + off.xyy) - map(rp - off.xyy),
				  map(rp + off.yxy) - map(rp - off.yxy),                  
				  map(rp + off.yyx) - map(rp - off.yyx));
    return normalize(g);
}

// scanning the surroundings for differences in heights on the heightmap
vec2 sub(vec3 rp, float preci)
{
    vec2 off = vec2(preci, 0.0);
    vec2 g = vec2(map(rp + off.xyy) + map(rp - off.xyy),
				  map(rp + off.yyx) + map(rp - off.yyx));
    return g;
}




const vec3 up = vec3(0.0, 1.0, 0.0);


void trace(in positionStruct pos, in vec3 rp, in vec3 rd, inout vec4 color)
{

    if (dot(rd, up)>0.0){
        color.rgb = getSky(pos);
        return;
    }

    // trace to plane on top of waves
    vec3 ro = rp;
    float t = -(0.155+dot(rp, -up)) / dot(-up, rd);
    if (t < 0.0) 
    {
        return;
    }

	rp += rd*t;    
    bool hit = false;
    float dist = 0.0;

    // actual tracing

    // low freq
    for (int i = 0; i < 10; ++i)
    {
        float travelledSq=dot(ro-rp, ro-rp);
        dist = mapLo(rp);

        if(dist < 0.01)
        {
            hit = true;
            break;
        }
        rp += rd * max(dist * (log2(2.+travelledSq)), 0.01);
        if(travelledSq > 9000.0) break;
    }

    //detail tracing
    for (int i = 0; i < 10; ++i)
    {
        rp += dist * rd;
        if (abs(dist) < 0.0001) break;
        dist = map(rp);
    }

    // shadings    
        vec3 sun = pos.sunVector;
        vec3 g = grad(rp, 0.002 * (1.0+log2(length(ro-rp) * 100.)));
        // base color
        float rddg = dot(rd, g);
        pos.worldVector = rd-2.*rddg*g;
        pos.worldVector = normalize(pos.worldVector);
        float F = clamp(max(0.0, 1.+rddg), 0.0, 1.);
        vec3 water_color = 0.5*vec3(.05, 0.22, .4);

        color.rgb = F* getSky(pos) + (1.-F)*water_color;




    color.a = 1.;//length(ro-rp);

}


mat3 lookat(vec3 from, vec3 to)
{
    vec3 f = normalize(to - from);
    vec3 _tmpr = normalize(cross(f, vec3(0.0, .999, 0.0)));
    vec3 u = normalize(cross(_tmpr, f));
    vec3 r = normalize(cross(u, f));
    return mat3(r, u, f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    gatherPositions(pos, fragCoord, iMouse.xy, iResolution.xy);
;
    vec2 uv = (fragCoord.xy-iResolution.xy*.5) / iResolution.x;

    //ray direction 
    vec3 rd = pos.worldPosition;

    //ray position
    vec3 rp = vec3(0.0, 2.3, 0.0);
    vec3 _rp = rp;

    trace(pos, rp, rd, fragColor);
    vec3 color = fragColor.rgb;
    color = pow(color, vec3(1.0 / 2.2)); //Fitting to regular colorspace
    color = robobo1221Tonemap(color);
    fragColor.rgb = clamp(color, 0.0, 1.0);
}

