void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 10.*fragCoord/iResolution.x;

    //the 30 60 90 triangle with sides 1, 2 and sqrt(3)
    //sqrt(3) is 1.731: https://www.redblobgames.com/grids/hexagons/
    vec2 s = vec2(1.,1.732);

    //we create two grids. Both have the dimentions of s.
    //mod with s gives us the coordinates like fract does
    vec2 a = mod(u,s)*2.-s;//stuff after mod just changes value not cell dimention
    //floor of u/s gives us an id system that is strechted to the s dimentions
    vec2 idA = floor(u/s);
    //get a random value for each id for coloring if I wanted
   // float A = rnd(idA);

    //we do the same again but with +s*0.5 so we move this system up
    //diagonally by half the dimentions of s
    vec2 b = mod(u+s*.5,s)*2.-s;//stuff after mod just changes value not cell dimention
    vec2 idB = floor((u+s*.5)/s);
    //I add +4 to give B some randomness
   // float B = rnd(idB+4.);


    //we take the lengths because a comparison for a given pixel
    //between length (within a given grid rectangle) will tell us
    //the coordinate system where the pixel is closest to 0.,0.
    //this tells us then which system (and which hexagon) to use.
    float la = length(a);
    float lb = length(b);
  //  float C = la < lb ? A*la : B*lb;

    //choose which hex system to use
    u = la < lb ? a : b;
    //we go a step further, get abs u so whatever we do
    //in the top right quadrant will be mirrored in top left, bottom left,
    //and bottom right
    vec2 st = abs(u);
    //what we do with this is get the dopt product.
    //if you remove the max(st.x portion you get a diamond.
    //This is percentage every pixel's vector (st) is aligned with
    //the normalized dimention vector (s). and that vector being 1.,1.732
    //is on a 60 degree angle already from the x axis. All these ratios
    //act as distances since s is normalized, and that give us that diamond shape.
    //on top of that max(st.x...) cut's things off whenever x > than the dot
    //dot product. That gives us an even hex shape.
    //(in the chosen system)
    float q = max(st.x, dot(st,normalize(s)));


    //Then I jut add a color pallette
	fragColor = vec4(0.5+0.5*cos(vec3(1.,2.,4.)/10. + floor(q*8.) ), 1.);//vec4(1.0-floor(C*8.)/8.*.75);
}

