}
mat2 m2 = mat2(1.6,-1.2,1.2,1.6);
float noiseo(vec2 p)
{
  return textureLod(iChannel0,p*vec2(1./256.),0.0).x;
}
// Fractional Brownian motion
float fbm( vec2 p )
{
  float f = 0.5000*noiseo( p ); p = m2*p;
  f += 0.2500*noiseo( p ); p = m2*p;
  f += 0.1666*noiseo( p ); p = m2*p;
  f += 0.0834*noiseo( p );
  return f;
}
float wavesLo(const in vec3 rp, float t, float A)
{
    vec2 p = rp.xz*100.1;

  vec2 shift2 = 0.001*vec2( iTime*190.0*2.0, -iTime*130.0*2.0 );

  // coarse crossing 'ocean' waves...
  float wave = 0.0;
  wave += sin(p.x*0.021  + shift2.x)*4.5;
  wave += sin(p.x*0.0172+p.y*0.010 + shift2.x*1.121)*4.0;
  wave -= sin(p.x*0.00104+p.y*0.005 + shift2.x*0.121)*4.0;
  // ...added by some smaller faster waves...
  wave += sin(p.x*0.02221+p.y*0.01233+shift2.x*3.437)*5.0;
  wave += sin(p.x*0.03112+p.y*0.01122+shift2.x*4.269)*2.5 ;
  wave *= 0.01;//large_waveheight;
  //wave -= fbm(p*0.004-shift2*.5)*small_waveheight*24.;
  return wave;
}

// high detail waves

float waves(const in vec3 rp, float t, float A)
{

    float small_waveheight = 0.015;
      vec2 shift1 = 0.001*vec2( iTime*160.0*2.0, iTime*120.0*2.0 );
    vec2 p = rp.xz;
  float amp = 6.*small_waveheight;
  shift1 *= .3;
    float wave = 0.0;
  for (int i=0; i<6; i++)
  {
    wave -= abs(sin((noise(p*1.1+shift1)-.5)*3.14))*amp;
    amp *= .51;
    shift1 *= 1.841;
    p *= m2*0.9331;
  }
    return wavesLo(rp, t, A) + wave;// +w3+w4+w7+w8+w5+w6;
}

float mapLo(in vec3 rp)
{
    return rp.y - wavesLo(rp, 2.0+iTime*.5, .04);
}


float map(in vec3 rp)
{
    return rp.y - waves(rp, 2.0+iTime*.5, .04);
}


vec3 grad(vec3 rp, float preci)
{
    vec2 off = vec2(preci, 0.0);
    vec3 g = vec3(map(rp + off.xyy) - map(rp - off.xyy),
				  map(rp + off.yxy) - map(rp - off.yxy),                  
				  map(rp + off.yyx) - map(rp - off.yyx));
    return normalize(g);
}

// scanning the surroundings for differences in heights on the heightmap
vec2 sub(vec3 rp, float preci)
{
    vec2 off = vec2(preci, 0.0);
    vec2 g = vec2(map(rp + off.xyy) + map(rp - off.xyy),
				  map(rp + off.yyx) + map(rp - off.yyx));
    return g;
}




