// Desmos graph (not very functional, just the workthrough):
// https://www.desmos.com/calculator/lrcijiosth
//
// Left: analytically integrated
// Right: numerically integrated
//
// The only difference I notice is that the numerically
// integrated one flickers as you move the camera due to
// it missing some areas between steps which creates some
// inconsistency as the ray moves.
//
// Some parts of the calculations look suspiciously
// like the quadratic formula simplified for a sphere
// intersection.
//
// The functions below are just specializations for different cases.
// Feel free to use any of them (as long as you give credit of course :)).

// Indefinite integral of the light volume over the ray at t
// Lighting at distance x is k/(1+(x/d)^2)
// k: brightness
// d: spread/diffusion
float integrateLightIndefinite(in vec3 ro, in vec3 rd, in float k, in float d, in float t) {
    float a = dot(rd, rd);
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(a * c - b * b);
    return d * d * k * atan((a * t + b) / h) / h;
}

// Definite integral of the light volume over any finite segment of the ray (ta to tb)
float integrateLightDefinite(in vec3 ro, in vec3 rd, in float k, in float d, in float ta, in float tb) {
    float a = dot(rd, rd);
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(a * c - b * b);
    float ah = a / h, bh = b / h;
    return d * d * k * (atan(ah * tb + bh) - atan(ah * ta + bh)) / h;
}

// Definite integral of the light volume over a partial view of the ray (0 to t)
float integrateLightPartialView(in vec3 ro, in vec3 rd, in float k, in float d, in float t) {
    float a = dot(rd, rd);
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(a * c - b * b), r = b / h;
    return d * d * k * (atan(a * t / h + r) - atan(r)) / h;
}

// Definite integral of the light volume over the entire view of the ray (0 to â)
// I computed the limit based on the fact that atan() approaches Ï/2 (90 degrees)
// as x (the ratio of y over x) goes to infinity
float integrateLightFullView(in vec3 ro, in vec3 rd, in float k, in float d) {
    float a = dot(rd, rd);
    float b = dot(ro, rd);
    float c = dot(ro, ro) + d * d;
    float h = sqrt(a * c - b * b);
    return d * d * k * (RHO - atan(b / h)) / h;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    fragColor = vec4(0.0);

    // Camera ray
    vec2 viewDir = ivec2(iMouse.xy) == ivec2(0) ? vec2(0.0) : (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y * TAU * ROTATION_SPEED;
    mat3 cam = getCamera(viewDir);

    vec3 ro = getPosition();
    vec3 rd = normalize(cam[0] * uv.x + cam[1] * uv.y + cam[2]);

    // Analytically integrated light on left
    float k = 0.8, d = 0.6;
    vec3 lightColor = vec3(1.0, 0.75, 0.15);

    fragColor.rgb += integrateLightFullView(ro, rd, k, d) * lightColor;
    //fragColor.rgb += integrateLightPartialView(ro, rd, k, d, 1000000.0) * lightColor;

    // Numerically integrated light on right
    if (uv.x > 0.0) {
        fragColor = vec4(0.0);
        float tMax = 100.0;
        float dt = tMax / 100.0;
        for (float t=0.0; t < tMax; t += dt) {
            vec3 p = ro + rd * t;
            fragColor.rgb += k / (1.0 + dot(p, p) / (d * d)) * dt; // Naive euler integration
        }

        fragColor.rgb *= lightColor;
    }

    // Divider
    float unit = 2.0 / iResolution.y;
    fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(uv.x) - 0.001));
}

