// Camera position buffer
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 5.0, 0.0);
    if (iFrame > 0) {
        ivec2 addr = ivec2(fragCoord);
        if (addr == ivec2(0)) {
            fragColor = vec4(getPosition(), 1.0);

            vec2 viewDir = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y * TAU * ROTATION_SPEED;
            mat3 cam = getCamera(viewDir);

            if (keyUpPressed()) fragColor.xyz += cam[2] * STEP_SIZE;
            if (keyDownPressed()) fragColor.xyz -= cam[2] * STEP_SIZE;
            if (keyRightPressed()) fragColor.xyz += cam[0] * STEP_SIZE;
            if (keyLeftPressed()) fragColor.xyz -= cam[0] * STEP_SIZE;
        }
    }
}

