    p = vec2(1.0, 1.0);

	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);
	p = p*p*(3.0-2.0*p);

	float f = iqnoise(24.0*uv, p.x, p.y);

