}
vec2 GetDist(vec3 p){
    //ground
    vec2 res = vec2(p.y+10.,0);
    float e = 2.71828182846;
    float offs = (pow(e,cos(p.y))/pow(e,sin(p.y*p.y*10.))*.03);
    vec2 blade = vec2(sdCapsule(p-vec3(0,0,clamp(abs(p.x-.5)*.5,0.,.7)), vec3(0,0,0), vec3(1,0,0), 1.),1);
    float mask = dBox(p-vec3(0,0,0), vec3(4,1,.1));

    blade = max(blade,mask);
    blade= max(blade,1.-length(p-vec3(.5,1,0))-.1);
    blade= max(blade,1.-length(p-vec3(.5,-1.4,0))-.1);
    res= colMin(res,blade);

    vec2 pole = vec2(sdCylinder(p-vec3(.5,0,0), vec3(0,-5,0), vec3(0,1,0), .2),2);
    vec2 uv = vec2(atan(p.x,p.z)/(3.14159*2.),p.y/5.)+.5;

    float disp = 0.1*iqnoise( 30.0*uv, 1., .1 );
    pole.x-=disp*.1;
    mask = dBox(p-vec3(.5,0,-.1), vec3(.1,1,.5));
    pole.x = opSmoothSubtraction(mask,pole.x,.2);
    res= colMin(res,pole);

    vec2 socket = vec2(sdCylinder(p-vec3(.5,-.15,0), vec3(0,0,.2), vec3(0,0,-.2), .3),3);
    res= colMin(res,socket);
    mask = sdCylinder(p-vec3(.5,-.15,0), vec3(0,0,.2), vec3(0,0,-.2), .1);
    res.x = opSmoothSubtraction(mask,res.x,.1);

    vec2 dec1 = vec2(sdCylinder(p-vec3(.5,-.7,0), vec3(0,0,.2), vec3(0,0,-.2), .2),4);
    vec2 dec2 = vec2(sdCylinder(p-vec3(.5,-.7,0), vec3(.3,0,0), vec3(-.3,0,0), .2),4);
    dec1 = min(dec1,dec2);
    vec2 dec3 = vec2(sdCylinder(p-vec3(.5,-.9,0), vec3(0,0,0), vec3(0,-.1,0), .25),4);
    dec1 = min(dec1,dec3);

    vec2 dec4 = vec2(sdCylinder(p-vec3(.5,-1.2,0), vec3(0,0,.2), vec3(0,0,-.24), .2),4);
    vec2 dec5 = vec2(sdCylinder(p-vec3(.5,-1.2,0), vec3(.3,0,0), vec3(-.3,0,0), .2),4);
    dec1 = min(dec1,dec4);
    dec1 = min(dec1,dec5);
    vec2 dec6 = vec2(sdCylinder(p-vec3(.5,-1.4,0), vec3(0,0,0), vec3(0,-.1,0), .25),4);
    dec1 = min(dec1,dec6);
    res= colMin(res,dec1);
    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,5,-5);

    lightPos.xz += vec2(sin(iTime),cos(iTime));
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= 0.1;
    }
    return diff;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
vec3 axeCol(vec3 p, vec3 n, vec3 rd){
        vec3 colXZ = texture(iChannel1, p.xz*.5+.5).rgb;
        vec3 colXY = texture(iChannel1, p.xy*.5+.5).rgb;
        vec3 colYZ = texture(iChannel1, p.yz*.5+.5).rgb;

        vec3 refl = texture(iChannel2, rd).rgb;
        refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);
        vec3 metal = colXZ*abs(n.y) + colXY*abs(n.z) + colYZ*abs(n.x);
        metal*=smoothstep(0.1,.4,metal.b)*refl*5.;
        return metal;

}
vec3 decCol(vec3 p, vec3 n, vec3 rd){
        vec3 colXZ = texture(iChannel0, p.xz*.5+.5).rgb;
        vec3 colXY = texture(iChannel0, p.xy*.5+.5).rgb;
        vec3 colYZ = texture(iChannel0, p.yz*.5+.5).rgb;

        vec3 refl = texture(iChannel2, rd).rgb;
        refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);
        vec3 metal = colXZ*abs(n.y) + colXY*abs(n.z) + colYZ*abs(n.x);
        metal= mix(smoothstep(0.7,.8,metal.r)*refl*5.,metal,.7);
        return metal;

}
vec3 poleCol(vec3 p, vec3 n, vec3 rd){
        vec3 colXZ = texture(iChannel3, p.xz*.5+.5).rgb;
        vec3 colXY = texture(iChannel3, p.xy*.5+.5).rgb;
        vec3 colYZ = texture(iChannel3, p.yz*.5+.5).rgb;

        vec3 refl = texture(iChannel2, rd).rgb;
        refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);
        vec3 metal = colXZ*abs(n.y) + colXY*abs(n.z) + colYZ*abs(n.x);
        //metal= mix(smoothstep(0.7,.8,metal.r)*refl*5.,metal,.7);
        return metal;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 mo = iMouse.xy/iResolution.xy;



    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ta = vec3( 0.5, -1, .5);
    vec3 ro = ta + vec3( 4.5*cos(7.0*mo.x), 1.3 + 2.0*mo.y, 4.5*sin(7.0*mo.x) );
    mat3 ca = setCamera( ro, ta, 0.0 );
//    vec3 ro = vec3(0,3,-5);


    //ray dir
    //controls rotation
    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 pt = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 pt = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif
    vec3 rd = ca * normalize( vec3(pt,2.5) );
    //------------------
    vec2 d = RayMarch(ro,rd);

    // position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d.x;

    //get diffuse lighting
    float diff = GetLight(p);
    vec3 col = vec3(0);
    col = vec3(diff)*2.;

    vec3 n = GetNormal(p);

    if(d.y == 1.){
        col *= axeCol(p, n, rd);
    }
    else if(d.y==2.){
        col *= poleCol(p,n,rd);
    }
    else if(d.y==3.){
        col*=decCol(p, n, rd)*vec3(0,0.6,.9);
    }
    else if(d.y==4.){
        col*=decCol(p, n, rd)*vec3(1,1,0);
    }
    else{
    col *=texture(iChannel2, rd).rgb * 5.;
    }
    fragColor = vec4(col,1.0);
}

