#define PI 3.14159265359

float maxIterationsC = 100.;
float minIterationsC = 10.;

vec2 offset = vec2(-30., 0.);

struct Coordinates
{
    float real;
    float imagenary;
};

struct Frac_Return_Val
{
	float iterations;
    float real;
    float imaginary;
};

Frac_Return_Val MandelbrotFunc(float maxIterations, Coordinates coordinates)
{
    float real;
    float imaginary;
    float c_real;
    float c_imaginary;

    //Mandelbrot
    real = 0.;
    imaginary = 0.;
    c_real = coordinates.real;
    c_imaginary = coordinates.imagenary;

    //Julia
    if (iMouse.z > 0.)
    {
        real = coordinates.real;
        imaginary = coordinates.imagenary;
        c_real = (iMouse.x - iResolution.x / 2.) / iResolution.x * 4.;
        c_imaginary = (iMouse.y - iResolution.y / 2.) / iResolution.y * 4.;
    }

    //Julia Mandel
    /*
    real = coordinates.real;
    imaginary = coordinates.imagenary;
    c_real = coordinates.real;
    c_imaginary = coordinates.imagenary;
    */

    float iterations = 0.;    

   	for (float i = 0.; i < maxIterations; i++)
    {
        iterations++;

        if (pow(real, 2.) + pow(imaginary, 2.) > 4.) 
        {
            Frac_Return_Val r_Val;
    		r_Val.iterations = iterations;
    		r_Val.real = real;
    		r_Val.imaginary = imaginary;

            return r_Val;
        }              

        float temp = real;
        real = pow(real, 2.) - pow(imaginary, 2.) + c_real;
        imaginary = 2. * temp * imaginary + c_imaginary;
    } 

    Frac_Return_Val r_Val;
    r_Val.iterations = iterations;
    r_Val.real = real;
    r_Val.imaginary = imaginary;

    return r_Val;
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float atan2(in float y, in float x)
{
    bool s = (abs(x) > abs(y));
    return mix(PI/2.0 - atan(x,y), atan(y,x), s);
}

