void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef animateIterations
    float maxIterations = maxIterationsC * abs(sin(iTime)) + minIterationsC;    
    #else
    float maxIterations = maxIterationsC;
    #endif

    Coordinates coordinates;

    #ifdef constantCoordinate
    coordinates.real = 1. / 100.;
    coordinates.imagenary = 1. / 100.;
    #else
    coordinates.real = (fragCoord.x - iResolution.x / 2. + offset.x) / iResolution.y * 3.;
    coordinates.imagenary = (fragCoord.y - iResolution.y / 2. + offset.y) / iResolution.y * 3.;
    #endif

    #ifdef zoom
    coordinates.real /= (sin(iTime * 0.1) * 1000.);
    coordinates.imagenary /= (sin(iTime * 0.1) * 1000.);
    #endif

    Frac_Return_Val r_Val = MandelbrotFunc(maxIterations, coordinates);

    vec4 color;

    if (r_Val.iterations >= maxIterations) {
        float modulus = sqrt(pow(r_Val.real, 2.) + pow(r_Val.imaginary, 2.));
        float a = (atan(r_Val.imaginary, r_Val.real) + 2.*PI) / (2.*PI);
        float mod_2 = 2./PI*atan(modulus* 10.);
        color = vec4(hsv2rgb(vec3(a, 1., mod_2)), 1.);
    }
    else {
    	color = vec4(r_Val.iterations / 64., r_Val.iterations / 128., r_Val.iterations / 32., 1.);
        //color = vec4(0,0,0,0);
    }

    #ifdef SHOW_ID
    float modulus = sqrt(pow(coordinates.real, 2.) + pow(coordinates.imagenary, 2.));
    float a = (atan(coordinates.imagenary, coordinates.real)+2.*PI) / (2.*PI);
    float mod_2 = 2./PI*atan(modulus* 10.);
    color = vec4(hsv2rgb(vec3(a, 1., mod_2)), 1.);
    #endif

    #ifdef animateColor
    float st = abs(sin(iTime)) * 2. + 1.;
    color *= vec4(1. * st, 1. * st , 1. * st , 1.);
    #endif
    fragColor = color;
}

vec3 to_rgb(vec3 ycbcr)
{
    // full range bt709 matrix
    const mat3 c709=mat3(1.0000000000, 1.0000000000, 1.0000000000,
                      0.0000000000, -0.1873242729, 1.8556000000,
                      1.5748000000, -0.4681242729, 0.0000000000);
    return c709 * ycbcr;
}

vec2 rot(vec2 uv, float r)
{
    float s=sin(r);
    float c=cos(r);

    return vec2(uv.x*c-uv.y*s,uv.x*s+uv.y*c);
}

const float pi = acos(-1.);
const float pi2 = pi*2.;

vec2 vsphere(float x)
{
    return vec2(sin(x*pi),cos(x*pi))*.25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.xy;

    float t = iDate.w;

    uv.y = uv.y*2.5+sin(t*3.+uv.x*6.)*sin(uv.x*4.)*.3;

    const float fm[]=float[](1.,60.,3600.,43200.);
    float m = floor(clamp(uv.y+2.5,1.,4.)-1.);
    float r = mod(t,fm[int(m)])*(1./fm[int(m)])*pi2;

    vec2 cbcr=mix(rot(vsphere(uv.x),sin(t)*pi2),rot(vec2(0.0,uv.x*.5+.5),-r)*(0.25-pow(m/3.,.8)*.1),float(uv.y>-1.5));

    fragColor = vec4(to_rgb(vec3(.5,cbcr)),1.0);
}

