float opRepSphere( vec3 p, vec3 c)
{
    vec3 q = mod(p,c)-0.5*c;
    return sphereF( q , vec3(0.,0.,0.), 1.);
}

float opRepCube( vec3 p, vec3 c)
{
    vec3 q = mod(p,c)-0.5 * c;
    return udBox( q , vec3(0.,0.,0.), vec3(1., 1., 1.));
}

float opDisplace( vec3 p )
{
    float d1 = opRepCube(p, vec3(20., 20., 20.));
    float d2 = (sin(2.*p.x)*sin(2.*p.y)*sin(2.*p.z)) * 2.;
    return d1+d2;
}
