vec3 RotateX(vec3 toRotate, vec3 camRot)
{
    mat3 rX = mat3 (
        1.0, 0.0, 0.0,
        0.0, cos(camRot.x), -sin(camRot.x),
        0.0, sin(camRot.x), cos(camRot.x)
        );  

        return toRotate * rX;
}

vec3 RotateY(vec3 toRotate, vec3 camRot)
{
    mat3 rY = mat3 (
        cos(camRot.y), 0.0,sin(camRot.y),
        0.0, 1.0, 0.0,
        -sin(camRot.y), 0.0, cos(camRot.y)
        );  

        return toRotate * rY;
}

vec3 RotateZ(vec3 toRotate, vec3 camRot)
{
    mat3 rZ = mat3 (
        cos(camRot.z), -sin(camRot.z), 0.0,
        sin(camRot.z), cos(camRot.z), 0.0,
        0.0, 0.0, 1.0
        );  

        return toRotate * rZ;
}

vec3 Rotate(vec3 toRotate, vec3 camPos, vec3 camRot)
{
    return RotateZ(RotateY(RotateX(toRotate, camRot), camRot), camRot);    
}
