vec3 calcNormal(vec3 p)
{
    vec3 n= vec3(scene(vec3(p.x + NORMALEPSILON, p.y, p.z)) - scene(vec3(p.x - NORMALEPSILON, p.y, p.z)),
                 scene(vec3(p.x, p.y + NORMALEPSILON, p.z)) - scene(vec3(p.x, p.y - NORMALEPSILON, p.z)),
                 scene(vec3(p.x, p.y, p.z + NORMALEPSILON)) - scene(vec3(p.x, p.y, p.z - NORMALEPSILON))
               );

    return normalize(n);
}

HitData raymarch(vec3 rayOrigin, vec3 rayDirection)
{
    float t = 0.;
    HitData hd;

    rayDirection = normalize(rayDirection);

    for (int i = 0; i < MAXRAYSTEPS; i++)
    {
        float d = scene(rayOrigin + rayDirection * t);
        if (d < RAYEPSILON)
        {
            hd.depth = d;
            hd.hitP = rayOrigin + rayDirection * t;            
            return hd;
        }
        t += d;
    }
    hd.depth = t;
    hd.hitP = rayOrigin + rayDirection * t;
    return hd;
}

