#define T(uv) texture(iChannel0,(uv)/iResolution.xy).xyz
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pmod(p,x) (mod(p,x) - (x)*0.5)
void mainImage( out vec4 fragColor, in vec2 U )
{
    vec2 R = iResolution.xy;
    float t=iTime*speed;
    //t = (floor(iTime*60.0)/60.0)*speed; //Testing how it looks at 60fps
    vec2 uv = (U-0.5*R.xy)/R.y; 
    vec3 col = vec3(0);

    float aa = 1.0/R.y; //aa constant based on resolution

    float wdth = 0.011;//cross size (nice value to play with)
    float lngth = wdth*9.0;//cross length

    //Warp edges of screen 
    float screenWarp = 1.0+pow(length(uv),10.0)*0.3;
    uv*=screenWarp;
    aa*=screenWarp;

    //domain repitition IDs and size
    float rep = lngth*2.5;
    vec2 id = floor(uv/rep);

    //4 cycle uv shifting based on ID
    float cycle = floor(mod(t,4.0));
    float smoothTime = smoothstep(0.0,1.0,fract(t))+floor(t);
    if(cycle == 0.0)uv.x+=(id.y+2.0)*rep*smoothTime;
    if(cycle == 1.0)uv.y+=(id.x+4.0)*rep*smoothTime;
    if(cycle == 2.0)uv.x+=(id.y-1.0)*rep*smoothTime;
    if(cycle == 3.0)uv.y+=(id.x-3.0)*rep*smoothTime;

    //Infinite crosses
    uv = pmod(uv,rep);

    //Rotate crosses but alternate direction every other time
    uv*=rot(smoothTime*pi*sign(sin(t*pi)));

    //Making 2 lines to form a cross
    float crs = smoothstep(wdth+aa,wdth-aa,abs(uv.x));
    crs = max(crs,smoothstep(wdth+aa,wdth-aa,abs(uv.y)));

    //cutting it after a certain distance from center
    float dist = max(abs(uv.x),abs(uv.y));
    crs*=smoothstep(lngth+aa,lngth-aa,dist);

    //Stack this frame with the previous for motion blur 
    //It's perhaps unrealistcally high but I like the effect
    col=T(U)*0.75 + vec3(crs);

    col = clamp(col,0.0,1.0);

    fragColor = vec4(col,1.0);
}

