    vec2 p3 = vec2((cos(3. * iTime) / 6.0 + 0.5) * p1.x, 1.0);
    vec2 p4 = vec2(cos(2. * iTime) / 8.0 + 0.8, sin(iTime + 1.) / 4.0 + 0.5); 
    vec2 p5 = vec2(mix(p1.x, p4.x, 0.5), max(p1.y, p4.y));
    vec2 p6 = vec2(1.0, mix(p5.y, p3.y, 0.3));
    vec2 p7 = vec2(p5.x, min(p4.y, p5.y));
    vec2 p8 = vec2(p5.x, p6.y);
    vec2 p9 = vec2(mix(p5.x, 1.0, 0.5 + cos(iTime +2.) / 4.), 1.);

    vec4 color = vec4(1.);

    drawRectangle(st, vec2(0.0, 0.0), p1, b_w, mn_g, mn_y, color);
    drawRectangle(st, vec2(0.0, p1.y / 3.0), p1, b_w, mn_g, mn_w, color); 
    drawRectangle(st, p2, p3, b_w, mn_g, mn_b, color);
    drawRectangle(st, vec2(p1.x, 0.0), p4, b_w, mn_g, mn_w, color);
    drawRectangle(st, vec2(mix(p1.x, p4.x, 0.7), 0.0), p4, b_w, mn_g, mn_r, color);
    drawRectangle(st, vec2(p1.x, 0.0), vec2(mix(p1.x, p4.x, 0.7), 2. * p4.y / 3. * (cos(2. * iTime + 2.) / 3. + 0.6666)), b_w, mn_g, mn_b, color);
    drawRectangle(st, p5, p3, b_w, mn_g, mn_w, color);
    drawRectangle(st, vec2(p3.x, p1.y), vec2(p1.x, p5.y), b_w, mn_g, mn_r, color);
    drawRectangle(st, p6, p7, b_w, mn_g, mn_w, color);
    drawRectangle(st, p7, vec2(p1.x, max(p1.y, p4.y)), b_w, mn_g, mn_y, color);
    drawRectangle(st, p8, p9, b_w, mn_g, mn_y, color);
    drawRectangle(st, vec2(p9.x, p6.y), vec2(1.), b_w, mn_g, mn_w, color);
    drawRectangle(st, p4, vec2(1., p4.y / 2.), b_w, mn_g, mn_b, color);
    drawRectangle(st, vec2(p4.x, p4.y / 2.), vec2(1., 0.), b_w, mn_g, mn_w, color);

    fragColor = vec4(color);
}

/*
AMD FidelityFX Contrast Adaptive Sharpening
https://gpuopen.com/fidelityfx-cas/
Potentially useful to upscale under-resolution renders.

This is a slimmed down reimplementation based on the paper.

Best viewed in fullscreen.
Drag left right to change switchover position. CAS on left.
Drag up and down to set CAS level.
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv;
    if (iMouse.z > 0.) {
        muv = iMouse.xy/iResolution.xy;
    } else {
        muv = vec2(.5+.3*sin(iTime*.3),.5);
    }

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).xyz;

    // CAS algorithm
    float max_g = col.y;
    float min_g = col.y;
    vec4 uvoff = vec4(1,0,1,-1)/iChannelResolution[0].xxyy;
    vec3 colw;
    vec3 col1 = texture(iChannel0, uv+uvoff.yw).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw = col1;
    col1 = texture(iChannel0, uv+uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    col1 = texture(iChannel0, uv+uvoff.yz).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    col1 = texture(iChannel0, uv-uvoff.xy).xyz;
    max_g = max(max_g, col1.y);
    min_g = min(min_g, col1.y);
    colw += col1;
    float d_min_g = min_g;
    float d_max_g = 1.-max_g;
    float A;
    if (d_max_g < d_min_g) {
        A = d_max_g / max_g;
    } else {
        A = d_min_g / max_g;
    }
    A = sqrt(A);
    A *= mix(-.125, -.2, muv.y);
    vec3 col_out = (col + colw * A) / (1.+4.*A);
    if (uv.x > (muv.x-.002)) {
        if (uv.x > (muv.x+.002)) {
            col_out = col;
        } else {
            col_out = vec3(0);
        }
    }
    // Output to screen
    fragColor = vec4(col_out,1);
}

/*
Still haven't figured out a way to compute the tightest bbox of a
quadratic bezier that seems surefire. Instead using analytical
linear regression of the bezier curve to try guessing an orientation.

Based on and inspired by @fizzer's approach:
"Cubic Bezier Analytic OBB": https://www.shadertoy.com/view/stfSR7

When the OBB is red, it has a larger area than the axis aligned bbox.
When the OBB is green, it has a smaller area than the axis aligned bbox.

The axis aligned bbox is yellow.
(I think I need to modify my color scheme, somewhat low contrast :))
*/

// Utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

struct AxisAlignedBBox { vec2 bmin; vec2 bmax; };
struct OrientedBBox { mat2 ori; vec2 bmin; vec2 bmax; };

// SDFs
float sdBezier(in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3) {
    vec2 c1 = p - v1;
    vec2 c2 = 2.0 * v2 - v3 - v1;
    vec2 c3 = v1 - v2;

    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0 / t3;
    float t1 = (dot(c1, c2) + 2.0 * dot(c3, c3)) / t3;
    float t0 = dot(c1, c3) / t3;

    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    if (qq * 0.25 + ppp / 27.0 > 0.0) {
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = clamp(-2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0, 0.0, 1.0);
        return length(p - mix(mix(v1, v2, root), mix(v2, v3, root), root));
    }

    else {
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0;
        vec2 roots = clamp(2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0, 0.0, 1.0);
        vec2 p1 = p - mix(mix(v1, v2, roots.x), mix(v2, v3, roots.x), roots.x);
        vec2 p2 = p - mix(mix(v1, v2, roots.y), mix(v2, v3, roots.y), roots.y);
        return sqrt(min(dot(p1, p1), dot(p2, p2)));
    }
}

float sdBBox(in vec2 p, in AxisAlignedBBox bbox) {
    p = abs(p - 0.5 * (bbox.bmin + bbox.bmax)) - 0.5 * (bbox.bmax - bbox.bmin);
    return abs(length(max(p, 0.0)) + min(0.0, max(p.x, p.y)));
}

float sdBBox(in vec2 p, in OrientedBBox bbox) {
    return sdBBox(p * bbox.ori, AxisAlignedBBox(bbox.bmin, bbox.bmax));
}

float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

// Best linear fit of discrete points on a qudratic bezier curve (not optimal)
//#define N 50
//vec2 linearFitBezierDiscrete(in vec2 v1, in vec2 v2, in vec2 v3) {
