//}

// Integrals instead of summations of discrete points
vec2 linearFitBezierContinuous(in vec2 v1, in vec2 v2, in vec2 v3) {
    vec2 a = v1 - 2.0 * v2 + v3, b = 2.0 * (v2 - v1), c = v1;

    vec4 w;
    w.x = a.x / 3.0 + 0.5 * b.x + c.x;
    w.y = a.y / 3.0 + 0.5 * b.y + c.y;
    w.z = (0.2 * a.x + 0.5 * b.x) * a.x + (2.0 * a.x * c.x + b.x * b.x) / 3.0 + (b.x + c.x) * c.x;
    w.w = 0.2 * (a.x * a.y) + 0.25 * dot(a, b.yx) + (dot(a, c.yx) + b.x * b.y) / 3.0 + 0.5 * dot(b, c.yx) + c.x * c.y;

    float rb = (w.y * w.z - w.x * w.w) / (w.z - w.x * w.x);
    return vec2((w.w - w.x * rb) / w.z, rb);
}

// Axis aligned
AxisAlignedBBox bezierAABB(in vec2 v1, in vec2 v2, in vec2 v3) {
    AxisAlignedBBox bbox;
    vec2 a = v1 - 2.0 * v2 + v3, b = 2.0 * (v2 - v1), c = v1; // Coefficients

    vec2 extrema = -0.5 * b / a;
    bbox.bmin = min(v1, v3), bbox.bmax = max(v1, v3);
    if (abs(extrema.x - 0.5) < 0.5) {
        vec2 p = (a * extrema.x + b) * extrema.x + c;
        bbox.bmin = min(bbox.bmin, p), bbox.bmax = max(bbox.bmax, p);
    }

    if (abs(extrema.y - 0.5) < 0.5) {
        vec2 p = (a * extrema.y + b) * extrema.y + c;
        bbox.bmin = min(bbox.bmin, p), bbox.bmax = max(bbox.bmax, p);
    }

    return bbox;
}

// Oriented
OrientedBBox bezierOBB(in vec2 v1, in vec2 v2, in vec2 v3) {
    OrientedBBox bbox;
    vec2 a = v1 - 2.0 * v2 + v3, b = 2.0 * (v2 - v1), c = v1; // Coefficients

    // Linear regression
    vec4 w;
    w.x = a.x / 3.0 + 0.5 * b.x + c.x;
    w.y = a.y / 3.0 + 0.5 * b.y + c.y;
    w.z = (0.2 * a.x + 0.5 * b.x) * a.x + (2.0 * a.x * c.x + b.x * b.x) / 3.0 + (b.x + c.x) * c.x;
    w.w = 0.2 * (a.x * a.y) + 0.25 * dot(a, b.yx) + (dot(a, c.yx) + b.x * b.y) / 3.0 + 0.5 * dot(b, c.yx) + c.x * c.y;

    float rb = (w.y * w.z - w.x * w.w) / (w.z - w.x * w.x);
    vec2 fit = vec2((w.w - w.x * rb) / w.z, rb);

    // Compute the orientation matrix from the fitting
    vec2 p1 = vec2(0.0, fit.y), p2 = vec2(1.0, fit.x + fit.y);
    vec2 dir = normalize(p2 - p1);
    bbox.ori = mat2(dir, -dir.y, dir.x);

    // Reposition and reorient, then compute new a, b, and c
    v1 -= p1, v2 -= p1, v3 -= p1;
    v1 *= bbox.ori, v2 *= bbox.ori, v3 *= bbox.ori;
    a = v1 - 2.0 * v2 + v3, b = 2.0 * (v2 - v1), c = v1;

    // Axis aligned bbox in new coordinate frame
    vec2 extrema = -0.5 * b / a;
    bbox.bmin = min(v1, v3), bbox.bmax = max(v1, v3);
    if (abs(extrema.x - 0.5) < 0.5) {
        vec2 p = (a * extrema.x + b) * extrema.x + c;
        bbox.bmin = min(bbox.bmin, p), bbox.bmax = max(bbox.bmax, p);
    }

    if (abs(extrema.y - 0.5) < 0.5) {
        vec2 p = (a * extrema.y + b) * extrema.y + c;
        bbox.bmin = min(bbox.bmin, p), bbox.bmax = max(bbox.bmax, p);
    }

    vec2 offs = p1 * bbox.ori;
    bbox.bmin += offs, bbox.bmax += offs;

    return bbox;
}

