    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
    return c.z * mix( vec3(1.0), rgb, c.y);
}

const float gridSize = 3.;
const float ballSize = 1.2;
float getVal( in vec3 p) {
    p.xy = (fract(p.xy/gridSize)-.5)*gridSize;
    return length(p)-ballSize;
}

int maxIt = 16;
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.;
    for (int i = 0; i<maxIt; i++) {
        float v = getVal(ro + rd*t);
        t += v;
        if (abs(v)<0.01) { return abs(t); }
    }
    return -1.;
}

//normals for raymarching
vec2 eps = vec2(0.0001,0.);
vec3 findNormals(in vec3 pos) {
    float v0 = getVal(pos);
    float v1 = getVal(pos+eps.xyy);
    float v2 = getVal(pos+eps.yxy);
    float v3 = getVal(pos+eps.yyx);
    return normalize(vec3(v1,v2,v3)-v0);
}

//distance on a wheel
float dist2(in float a, in float b) {
    float d = abs(a - b)*2.;
    return d < 1. ? d : 2. - d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;
    vec2 mouse = vec2(iMouse.x/iResolution.x,1.-iMouse.y/iResolution.y);      

    //ray origin + direction
	vec3 ro = camPos;
	vec3 rd = normalize( vec3(p,-4.)-vec3(0.,0.5,0.) );

    //we find the objects distance and color:

    float tmin = 1e10;

    vec3 hit, normals, objColHSV;

    //floor
    float t1 = -ro.y/rd.y;
    if( t1>0.0 ) {
        tmin = t1;
        hit = ro + tmin*rd;
        objColHSV = vec3(0.0,0.0,0.5 + 0.5*floor(fract(hit.x*0.5)+0.8)*floor(fract(hit.z*0.5)+0.8)); //vec3(3.0/t1);
        normals = vec3(0.0,1.0,0.0);
    }

    //spheres
    float t3 = raymarch(ro,rd);
    if( t3>0. && t3<tmin ) {
        vec3 hit2 = ro + rd*t3;    
        if (abs(hit2.x)<18. && abs(hit2.y)<18.) {
            tmin = t3;
            hit = hit2; 

            vec2 hs = (floor(hit.xy/gridSize)-6.)/vec2(12.,-6.);
            objColHSV = vec3( fract(hs.x), clamp(hs.y,0.,1.),1.) ;
            normals = findNormals(hit);
        }    
    }
    vec3 objColRGB = pow(hsv2rgb(objColHSV),vec3(2.2)) ;


    // shading :

    //actual light color
    vec3 lightColHSV = vec3(mouse,1.0);
    vec3 lightColRGB  = pow(hsv2rgb(lightColHSV),vec3(2.2));
    //light color with maximum saturation
    vec3 lightColRGB2 = pow(hsv2rgb(vec3(mouse.x,1.0,1.0)),vec3(2.2));

    vec3 lightVec = lightPos - hit;
    vec3 lightDir = normalize(lightVec);
    float lightPow = lightIntensity/(1.+length(lightVec)*lightRange);


    //we compute some reflection parameters

    //is our point in the shadow ?
    float shadowT   = raymarch(hit+normals*0.1,lightDir);
    vec3  shadowHit = hit + lightDir*shadowT;
    float shadow = float(shadowT<0. || abs(shadowHit.x)>18. || abs(shadowHit.y)>18.);

    float diffuse = max(dot(lightDir,normals),0.0);
    diffuse = mix(min(diffuse, shadow),1.,ambient);

    vec3 reflectDir = reflect(-lightDir,-normals);
    float specular = pow (max (dot (-rd, reflectDir), 0.0), 3.0)*0.125;

    float fog = pow(exp(-tmin*fogDepth),2.2);

    //and finally the lighting :

    vec3 col;

    if (standardSolution) {
        col = (objColRGB*diffuse + specular)*lightColRGB*lightPow*fog;
    } else {

        //the reflectivity variable determines how much the "pure" light color frequency is reflected.
        //the idea is that, for a blue light, a blue or white object will be reflective,
        //but an orange or black one will absorbs most of the light, and appear darker.

        float ref = dist2(objColHSV.x, lightColHSV.x);

        //ref = exp(-ref*objColHSV.y*3. );        
        ref = pow(2.5/(2.5+ref*objColHSV.y),8.);

        ref *= pow(objColHSV.z,2.2);

        //mixing the white light with the colored light
        col = diffuse * mix(objColRGB, lightColRGB2*ref, pow(lightColHSV.y,0.25));

        //specular light is simple
        col += specular*lightColRGB;

        col *= lightPow*fog;

    }    

    //gamma correction
    col = pow(col,vec3(1./2.2));

    if (fragCoord.x<80. && fragCoord.y<80.) { col = lightColRGB; }

    fragColor = vec4( col, 1.0 );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);

}

