vec3 grad(vec3 rp, float preci)
{
    vec2 off = vec2(preci, 0.0);
    vec3 g = vec3(map(rp + off.xyy) - map(rp - off.xyy),
				  map(rp + off.yxy) - map(rp - off.yxy),                  
				  map(rp + off.yyx) - map(rp - off.yyx));
    return normalize(g);
}

