void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float sinePeaks = 3.;
    float waitPixelRows = 300.;
    int speed = 1; // higher values will reduce quality
    int scanPos = int(mod(float(iFrame * speed), iResolution.y + waitPixelRows));
    int yoffs = int((-1.+cos(fragCoord.x * sinePeaks * 6.28/ iResolution.x)) * iResolution.y / 22.);
    if(int(fragCoord.y) + yoffs > int(iResolution.y) - scanPos)
        discard;
    else if(int(fragCoord.y) + yoffs ==  int(iResolution.y) - scanPos - speed)
        fragColor = vec4(0.,1.,0.,1.);
    else
        fragColor = texture(iChannel0, vec2(iResolution.x - fragCoord.x, fragCoord.y) / iResolution.xy);
}

