const vec4  planet_sph    = vec4(50.0*normalize(light1_dir+vec3(0.025, -0.025, 0.0)), 10.0);

float g_hf;

vec2 g_vx = vec2(0.0);
vec2 g_vy = vec2(0.0);

vec2 g_wx = vec2(0.0);
vec2 g_wy = vec2(0.0);

vec4 alphaBlend(vec4 back, vec4 front) {
  float w = front.w + back.w*(1.0-front.w);
  vec3 xyz = (front.xyz*front.w + back.xyz*back.w*(1.0-front.w))/w;
  return w > 0.0 ? vec4(xyz, w) : vec4(0.0);
}

vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, vec3(1.0/2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// https://iquilezles.org/articles/distfunctions2d
float vesica(vec2 p, vec2 sz) {
  if (sz.x < sz.y) {
    sz = sz.yx;
  } else {
    p  = p.yx; 
  }
  vec2 sz2 = sz*sz;
  float d  = (sz2.x-sz2.y)/(2.0*sz.y);
  float r  = sqrt(sz2.x+d*d);
  float b  = sz.x;
  p = abs(p);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

// IQ's ray sphere intersect: https://iquilezles.org/articles/intersectors
vec2 raySphere(vec3 ro, vec3 rd, vec4 sph) {
  vec3 oc = ro - sph.xyz;
  float b = dot( oc, rd );
  float c = dot( oc, oc ) - sph.w*sph.w;
  float h = b*b - c;
  if (h < 0.0) return vec2(-1.0);
  h = sqrt(h);
  return vec2(-b - h, -b + h);
}

// IQ's ray plane  intersect: https://iquilezles.org/articles/intersectors
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return p.x*vec2(cos(p.y), sin(p.y));
}

vec3 toSpherical(vec3 p) {
  float r   = length(p);
  float t   = acos(p.z/r);
  float ph  = atan(p.y, p.x);
  return vec3(r, t, ph);
}

vec3 toRect(vec3 p) {
  return p.x*vec3(cos(p.z)*sin(p.y), sin(p.z)*sin(p.y), cos(p.y));
}

// https://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

// https://mercury.sexy/hg_sdf/
vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

// https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float pabs(float a, float k) {
  return -pmin(-a, a, k);
}

const vec2 iris_center = vec2(0.0, 0.28);

float noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

// https://iquilezles.org/articles/fbm
float fbm(vec2 p, float aa) {
  const mat2 frot = mat2(0.80, 0.60, -0.60, 0.80);

  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0;
  for (int x = 0; x < 4; ++x) {
    f += a*noise(p); 
    p = frot*p*m;
    m += 0.01;
    s += a;
    a *= aa;
  }
  return f/s;
}

// https://iquilezles.org/articles/warp
float warp(vec2 p, out vec2 v, out vec2 w) {
  float d2 = DOT2(p);
//  p.x = pabs(p.x, 0.1);
  p = -p.yx;
  p = toPolar(p);
  p.y -= -0.125*TIME+p.x*1.25;
  vec2 pp = p;

  vec2 vx = g_vx;
  vec2 vy = g_vy;

  vec2 wx = g_wx;
  vec2 wy = g_wy;

  const float r = 0.5;
  const float rr = 0.25;
  float f = smoothstep(r, r+rr, pp.x);
  g_hf = f;

  //float aa = mix(0.95, 0.25, tanh_approx(pp.x));
  float aa = 0.5;

  v = vec2(fbm(p + vx, aa), fbm(p + vy, aa))*f;
  w = vec2(fbm(p + 3.0*v + wx, aa), fbm(p + 3.0*v + wy, aa))*f;

  return -tanh_approx(fbm(p + 2.25*w, aa)*f);
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = warp(p + e.xy, v, w) - warp(p - e.xy, v, w);
  n.y = 2.0*e.x;
  n.z = warp(p + e.yx, v, w) - warp(p - e.yx, v, w);

  return normalize(n);
}

void compute_globals() {
  vec2 vx = vec2(0.0, 0.0);
  vec2 vy = vec2(3.2, 1.3);

  vec2 wx = vec2(1.7, 9.2);
  vec2 wy = vec2(8.3, 2.8);

  vx *= ROT(TTIME/1000.0);
  vy *= ROT(TTIME/900.0);

  wx *= ROT(TTIME/800.0);
  wy *= ROT(TTIME/700.0);

  g_vx = vx;
  g_vy = vy;

  g_wx = wx;
  g_wy = wy;
}

vec3 iris(vec2 p) {
  const vec3 up  = vec3(0.0, 1.0, 0.0);
  const vec3 lp1 = 1.0*vec3(1.0, 1.25, 1.0);
  const vec3 lp2 = 1.0*vec3(-1.0, 2.5, 1.0);

  compute_globals();

  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec2 v;
  vec2 w;

  float h  = warp(p, v, w);
  float hf = g_hf;
  vec3  n  = normal(p);

  const vec3 lcol1 = HSV2RGB(vec3(0.7, 0.5, 1.0)); 
  const vec3 lcol2 = HSV2RGB(vec3(0.4, 0.5, 1.0));
  vec3 po  = vec3(p.x, 0.0, p.y);
  vec3 rd  = normalize(po - ro);

  vec3 ld1 = normalize(lp1 - po);
  vec3 ld2 = normalize(lp2 - po);

  float diff1 = max(dot(n, ld1), 0.0);
  float diff2 = max(dot(n, ld2), 0.0);

  vec3  ref   = reflect(rd, n);
  float ref1  = max(dot(ref, ld1), 0.0);
  float ref2  = max(dot(ref, ld2), 0.0);

  float a = length(p);
  vec3 col = vec3(0.0);
  col += hsv2rgb(vec3(fract(-0.1*TIME+0.125*a+0.5*v.x+0.125*w.x), abs(0.5+tanh_approx(v.y*w.y)), tanh_approx(0.1+abs(v.y-w.y))));
  col += 0.5*lcol1*pow(ref1, 20.0);
  col += 0.5*lcol2*pow(ref2, 10.0);
  col *= hf;

  return col;
}

vec3 render_iris(vec3 ro, vec3 rd, vec3 nrd) {
  const vec4 plane = vec4(normalize(vec3(1.0, 0.3, 0.0)), -2.0);
  const vec3 tnor  = plane.xyz;
  const vec3 tup   = normalize(vec3(0.0, -1.0, 0.0));
  const float aa   = 0.001;
  float t = rayPlane(ro, rd, plane);

  vec3 tpos = ro + t*rd;
  vec3 txx = normalize(cross(tnor, tup));
  vec3 tyy = normalize(cross(tnor, txx));

  vec2 tpos2 = vec2(dot(txx, tpos), dot(tyy, tpos));

  vec3 col = iris(tpos2);

  return col;
}

vec3 df(vec2 p) {
  const float iris_outer = 0.622;
  const float iris_inner = 0.285;

  float a  = mix(0.0, 1.0, smoothstep(0.995, 1.0, cos(TTIME/PERIOD)));
  const float w = 1.14;
  float h = mix(0.48, 0.05, a);

  float t0 = abs(0.9*p.x);
  t0 *= t0;
  t0 *= t0;
  t0 *= t0;
  t0 = clamp(t0, 0.0, 1.0);
  float dt0 = mix(0.0125, -0.0025, t0);

  vec2 p0 = p;
  float d0 =  vesica(p0, vec2(w, h));
  float d5 = d0;

  vec2 p1 = p;
  p1 -= iris_center;
  float d1 = circle(p1, iris_outer);
  d1 = max(d1,d0+dt0);
  float d6 = d1;

  vec2 p2 = p;
  p2 -= vec2(-0.155, 0.35);
  float d2 = circle(p2, 0.065);

  vec2 p3 = p;
  p3 -= iris_center;
  p3 = toPolar(p3);
  float n3 = mod1(p3.x, 0.05);
  float d3 = abs(p3.x)-0.0125*(1.0-1.0*length(p1));

  vec2 p4 = p;
  p4 -= iris_center;
  float d4 = circle(p4, iris_inner);

  d3 = max(d3,-d4);

  d1 = pmax(d1,-d2, 0.0125);
  d1 = max(d1,-d3);

  d0 = abs(d0)-dt0;


  float d = d0;
  d = pmin(d, d1, 0.0125);
  return vec3(d, d6, d5);
}

vec3 render_background(vec3 ro, vec3 rd, vec3 nrd) {
  rd.xy *= ROT(-PI/2.0+0.6);
  vec3 srd = toSpherical(rd.xzy);
  srd.z += 0.025*TIME;
  vec2 pg  = srd.yz;
  float f  = sin(pg.x); 
  float lf2= ceil(log(f)/log(2.0)-0.505);
  float mf = pow(2.0, lf2);

  const float aa = 0.005;
  const float count = 20.0;
  const vec2 sz = vec2(2.0*PI/count);
  vec2 ng = mod2(pg, vec2(mf, 1.0)*sz);

  float dg = min(abs(pg.y)*f, abs(pg.x))-aa*0.0;
  vec3 lines = grid_color*smoothstep(-aa, aa, -dg)*f*f;

  vec3 sky  = smoothstep(1.0, 0.0, rd.y)*sky1_color+smoothstep(0.5, 0.0, rd.y)*sky0_color;

  vec2 pi = raySphere(ro, rd, planet_sph);

  float lf1 = 1.0;
  if (pi.x > 0.0) {
    vec3 ppos = ro+rd*pi.x;
    float t = 1.0-tanh_approx(1.5*(pi.y - pi.x)/planet_sph.w);
    sky *= mix(0.5, 1.0, t);
    lf1 = t;
  } else {
    sky += lines;
  }

  sky += pow(max(dot(rd, light0_dir), 0.0), 800.0)*light0_color; 
  sky += pow(max(dot(rd, light0_dir), 0.0), 80.0)*light1_color*0.1; 
  sky += lf1*pow(max(dot(rd, light1_dir), 0.0), 150.0)*light1_color; 
  sky += lf1*pow(max(dot(rd, light1_dir), 0.0), 50.0)*light0_color*0.1; 


  return sky;
}

vec4 render_body(vec2 p, vec2 q, vec3 dd) {
//  p -= iris_center;
  float aa = 2.0/RESOLUTION.y;

  float a = smoothstep(-aa, aa, -dd.z);
  float b = smoothstep(0.0, mix(0.075, 0.0025, smoothstep(0.5, 1., abs(p.x))), -dd.z);
  float c = smoothstep(-aa, aa, -dd.y);
  float d = smoothstep(0.00, 0.1, -dd.y);

  //vec3 ro = vec3(1.5, 1.9, -1.85);
  vec3 ro = vec3(2.0, 0.0, 0.0);
//  ro.xz *= ROT(TIME);
  vec3 la = vec3(0.0, 0.0, 0.0); 

  vec2 np   = p + vec2(4.0/RESOLUTION.y); 

  vec3 ww   = normalize(la - ro);
  vec3 uu   = normalize(cross(vec3(0.0,1.0,0.0), ww));
  vec3 vv   = normalize(cross(ww,uu));
  float rdd = 2.0;
  vec3 rd   = normalize(p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd  = normalize(np.x*uu + np.y*vv + rdd*ww);

  vec4 sph  = vec4(vec3(0.0), 1.0);

  vec2 si   = raySphere(ro, rd, sph);

  vec3 pos  = ro + rd*si.x;

  vec3 nor  = normalize(pos - sph.xyz);

  vec3 ref  = reflect(rd, nor); 
  vec3 nref = reflect(nrd, nor); 

  vec3 refr = refract(rd, nor, 0.9); 
  vec3 nrefr= refract(nrd, nor, 0.9); 

  vec3 rbkg = render_background(pos, ref, nref);

  vec3 col = vec3(0.0);
  col += rbkg;
  if (d > 0.0) {
    vec3 riris= render_iris(pos, ref, nref);
    col += riris*d;
  }

  col += mix(vec3(0.0), vec3(0.75), 1.0-c); 
  col.xyz*=b;

  return vec4(col, a);
}

vec3 effect(vec2 p, vec2 q) {
  float aa = 2.0/RESOLUTION.y;
  vec3 d   = df(p);

  vec4 dcol = vec4(0.0, 0.0, 0.0, 0.5);
  dcol.w    = mix(0.0, 0.95, smoothstep(-aa, aa, -d.x));
  vec4 scol = render_body(p, q, d); 

  vec3 col  = vec3(1.0);
  col = alphaBlend(col, dcol);
#ifdef VARIANT  
  if (fract(TIME/(2.0*PERIOD)) >= 0.5) {
    col = alphaBlend(col, scol);
  }
#else
  col = alphaBlend(col, scol);
#endif
  return col;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  vec3 col = effect(p, q);
  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

// License CC0: Retro cube effect with twist
//  We made a retro demo some time ago and in one of the effects
//  We wanted to recreate the classic "Amiga Cube that intersects a translucent plane"
//  But having accesss to a few more Teraflops than the Amiga we wanted to add a bit of a twist to it
//  Music: Hyperbased by Firefox: https://soundcloud.com/firefox-amigamusician/hyperbased

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            (iTime+155.0)
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(a)         (0.5+0.5*cos(a))
#define PSIN(a)         (0.5+0.5*sin(a))
#define L2(x)           dot(x, x)
#define SCA(a)          vec2(sin(a), cos(a))
#define MISS            1E6
#define BTIME(n)        (n*beat+start)

#define CUBE_TOLERANCE       0.0001
#define CUBE_MAX_RAY_LENGTH  8.0
#define CUBE_MAX_RAY_MARCHES 80
#define CUBE_NORM_OFF        0.0005

const float beat            = 0.48;
const float start           = 41.1;
const float bounce_freq     = 0.5/beat;

const mat2 rot0             = ROT(0.00);
const vec3 std_gamma        = vec3(2.2);

const float cube_begin      = BTIME(240.0); // ~156
const float cube_flash0     = BTIME(334.0);
const float cube_flash1     = BTIME(335.0);
const float cube_end        = BTIME(336.0);

// GLOBAL MUTABLES

vec4  cube_g_plane       = vec4(normalize(vec3(1.0, 0.0, 0.0)), 0.0);
float cube_g_pw          = 0.0;
mat2 cube_g_rotxy        = rot0;
mat2 cube_g_rotxz        = rot0;
mat2 cube_g_rotxw        = rot0;
mat2 cube_g_rotyw        = rot0;
mat2 cube_g_rotzw        = rot0;


// -----------------------------------------------------------------------------
// COMMON
// -----------------------------------------------------------------------------

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

float plane(vec3 p, vec4 plane) {
  return dot(plane.xyz, p)+plane.w;
}

float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float box(vec4 p, vec4 b) {
  vec4 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(max(q.x, q.w),max(q.y,q.z)),0.0);
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Classic post processing
vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/std_gamma);
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

// -----------------------------------------------------------------------------
// CUBE
// -----------------------------------------------------------------------------

vec2 cube_mengerSponge(vec4 p) {
  float db = box(p, vec4(1.0));
  if(db > .125) vec2(db, db);

  float d_ = db;
  float res = d_;

  float s = 1.0;
  for(int m = 0; m < 4; ++m) {
    float ss = 0.75;
    vec4 a = mod(p*s, 2.0)-1.0;
    s *= 3.0;
    vec4 r = abs(1.0 - 3.0*abs(a));

    float da = max(max(r.x,r.y),r.w);
    float db = max(max(r.y,r.z),r.w);
    float dc = max(max(r.z,r.x),r.w);
    float dd = max(max(r.z,r.x),r.y);
    float df = length(r)-2.16;

    float du = da;
    du = min(du, db);
    du = min(du, dc);
    du = pmin(du, dd, ss); // Soften the edges a bit
    du = max(du, -df);
    du -= 1.0;
    du /= s;

    res = max(res, du);
  }

  return vec2(db, res);
}

float cube_intersectTransformPlane(vec3 ro, vec3 rd) {
  return rayPlane(ro, rd, cube_g_plane);
}

float cube_dtransformPlane(vec3 p) {
  return plane(p, cube_g_plane);
}

float cube_df(vec3 p) {
  float dp = cube_dtransformPlane(p);
  const float s = 1.0/3.0;
  p /= s;
  p.xy *= cube_g_rotxy;
  p.xz *= cube_g_rotxz;
  vec4 pp = vec4(p, cube_g_pw);
  pp.xw *= cube_g_rotxw;
  pp.yw *= cube_g_rotyw;
  pp.zw *= cube_g_rotzw;

  // TODO: Optimize
  vec2 dms = cube_mengerSponge(pp);

  float d0 = dms.x*s;
  float d2 = d0;
  d0 = max(dp, d0);

  float d1 = dms.y*s;
  d1 = max(-dp, d1);
  return max(d2, pmin(d0, d1, 0.05));
}

float cube_rayMarch(vec3 ro, vec3 rd, out int iter) {
  float t = 0.0;
  int i = 0;
  for (i = 0; i < CUBE_MAX_RAY_MARCHES; i++) {
    float d = cube_df(ro + rd*t);
    if (d < CUBE_TOLERANCE || t > CUBE_MAX_RAY_LENGTH) break;
    t += d;
  }
  iter = i;
  return t;
}

vec3 cube_normal(vec3 pos) {
  vec2  eps = vec2(CUBE_NORM_OFF,0.0);
  vec3 nor;
  nor.x = cube_df(pos+eps.xyy) - cube_df(pos-eps.xyy);
  nor.y = cube_df(pos+eps.yxy) - cube_df(pos-eps.yxy);
  nor.z = cube_df(pos+eps.yyx) - cube_df(pos-eps.yyx);
  return normalize(nor);
}

float cube_softShadow(vec3 pos, vec3 ld, float ll, float mint, float k) {
  const float minShadow = 0.25;
  float res = 1.0;
  float t = mint;
  for (int i=0; i<24; i++) {
    float d = cube_df(pos + ld*t);
    res = min(res, k*d/t);
    if (ll <= t) break;
    if(res <= minShadow) break;
    t += max(mint*0.2, d);
  }
  return clamp(res,minShadow,1.0);
}

vec3 cube_transformPlane(vec3 ro, vec3 rd, vec4 plane, out float ttp) {
  vec3 tnor = plane.xyz;
  float t = rayPlane(ro, rd, plane);
  ttp = t;
  if (t < 0.0) return vec3(0.0);
  vec3 tp = ro + t*rd;
  float td = cube_df(tp);
  td -= 0.025;
  float otd = td;
  td = abs(td)- 0.005;
  const vec3 tup = vec3(0.0, 1.0, 0.0);
  vec3 txx = normalize(cross(tnor, tup));
  vec3 tyy = (cross(tnor, txx));
  vec2 tp2 = vec2(dot(txx, tp), dot(tyy, tp));
  float a  = mix(0.0, PI/4.0 + 0.5*(TIME-BTIME(288.0)), smoothstep(BTIME(288.0), BTIME(292.0), TIME));
  tp2 *= ROT(a);
  float tpd = box(tp2, 0.6*vec2(4.0/3.0, 1.0));
  float taa = 0.001;
  mod2(tp2, vec2(0.125));
  float tpgd = min(abs(tp2.x), abs(tp2.y));
  tpgd = max(tpgd, -otd);
  tpgd = max(tpgd, tpd);
  float tgd = tpd;
  tgd -= 0.0125;
  tgd = abs(tgd)- 0.005;
  tgd = min(tgd, td);

  const vec3 greenGlow = vec3(1.25, 2.0, 1.25);
  const vec3 redGlow = vec3(2.0, 1.25, 1.5);
  vec3 tcol = vec3(0.0125);
  tcol += greenGlow*exp(-max(tpgd, 0.0)*900.0);
  tcol += greenGlow*(1.0-abs(dot(rd, tnor)))*0.2*PSIN(500.*tp2.y);
  tcol = mix(vec3(0.0), tcol, smoothstep(-taa, taa, -(tpd-0.025)));
  tcol += redGlow*exp(-max(tgd, 0.0)*100.0);
  return tcol;
}

vec3 cube_render(in vec3 ro, in vec3 rd) {
  vec3 lightPos = 2.0*vec3(1.5, 3.0, 1.0);

  float alpha   = 0.05*TIME;
  vec3 tnor     = normalize(vec3(1.0, 0.0, 0.0));
  tnor.xy       *= ROT(PI*(1.0-cos(sqrt(0.3)*max(TIME-BTIME(296.0), 0.0))));
  tnor.xz       *= ROT(PI*(1.0-cos(sqrt(0.15)*max(TIME-BTIME(304.0), 0.0))));

  float tm      = -0.5*(cos((2.0*TAU/(4.0/beat))*max(TIME-BTIME(292.0), 0.0)));
  tm  = mix(0.75 , tm, smoothstep(BTIME(272.0), BTIME(288.0), TIME));
  tm  = mix(-0.75, tm, smoothstep(BTIME(248.0), BTIME(272.0), TIME));
  tm  = mix(-3.0 , tm, smoothstep(BTIME(244.0), BTIME(248.0), TIME));

  vec4 plane    = vec4(tnor, tm);

  cube_g_plane       = plane;
  cube_g_rotxy       = ROT(TIME);
  cube_g_rotxz       = ROT(TIME*sqrt(0.5));
  cube_g_pw          = 0.5*cos(alpha*sqrt(2.0));
  cube_g_rotxw       = ROT(alpha);
  cube_g_rotyw       = ROT(alpha*sqrt(0.5));
  cube_g_rotzw       = ROT(alpha*sqrt(2.0));

//  tnor.xy *= g_rotxy;
  // background color
  vec3 skyCol = vec3(0.0);

  int iter = 0;
  float t = cube_rayMarch(ro, rd, iter);
  float tp;
  vec3 tcol = cube_transformPlane(ro, rd, plane, tp);
  tcol = mix(vec3(0.0), tcol, float(tp < t));

  float ifade = 1.0-tanh_approx(2.0*float(iter)/float(CUBE_MAX_RAY_MARCHES));

  vec3 pos = ro + t*rd;
  vec3 nor = vec3(0.0, 1.0, 0.0);

  vec3 color = vec3(0.0);

  float dp   = -(ro.y+1.)/rd.y;

  if (dp > 0.0 && dp < t) {
    // Ray intersected plane
    t   = dp;
    pos = ro + t*rd;
    nor = vec3(0.0, 1.0, 0.0);
    vec2 pp = pos.xz*1.5;
    float m = 0.5+0.25*(sin(3.0*pp.x+TIME*2.1)+sin(3.3*pp.y+TIME*2.0));
    m *= m;
    m *= m;
    pp = fract(pp+0.5)-0.5;
    float dp = pmin(abs(pp.x), abs(pp.y), 0.025);
    vec3 hsv = vec3(0.4+mix(0.15,0.0, m), tanh_approx(mix(100.0, 10.0, m)*dp), 1.0);
    color = 2.5*hsv2rgb(hsv)*exp(-mix(30.0, 10.0, m)*dp);
  } else if (t < CUBE_MAX_RAY_LENGTH) {
    // Ray intersected object
    nor        = cube_normal(pos);
    vec3 hsv   = (vec3(-0.2+0.25*t, 1.0-ifade, 1.0));
    color = hsv2rgb(hsv);
  } else {
    // Ray intersected sky
    return (skyCol)*ifade+tcol;
  }

  vec3 lv   = lightPos - pos;
  float ll2 = dot(lv, lv);
  float ll  = sqrt(ll2);
  vec3 ld   = lv / ll;
  float sha = cube_softShadow(pos, ld, ll, 0.01, 64.0);

  float dm  = min(1.0, 40.0/ll2);
  float dif = max(dot(nor,ld),0.0)*dm;
  float spe = pow(max(dot(reflect(-ld, nor), -rd), 0.), 10.);
  float l   = dif*sha;

  float lin = mix(0.2, 1.0, l);

  vec3 col = lin*color + spe*sha;

  float f = exp(-20.0*(max(t-3.0, 0.0) / CUBE_MAX_RAY_LENGTH));

  return (mix(skyCol, col , f))*ifade+tcol;
}

vec3 cube_effect(vec2 p, vec2 q) {
  float m = smoothstep(BTIME(264.0), BTIME(272.0), TIME);
  float tm = TIME-BTIME(264.0);
  // camera
  vec3 ro = mix(1.0, 0.6, m)*vec3(2.0, 0, 0.2)+vec3(0.0, 0.25, 0.0);
  ro.xz *= ROT(mix(0.0, tm*0.25, m));
  ro.yz *= ROT(-(1.0-PCOS(tm*0.25*sqrt(0.5)))*0.25);
  vec3 ww = normalize(vec3(0.0, 0.0, 0.0) - ro);
  vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

  return cube_render(ro, rd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = cube_effect(p, q);

  col = postProcess(col, q);
  col = mix(vec3(1.0), col, smoothstep(cube_begin+0.25, cube_begin+1.5, TIME));

  fragColor = vec4(col, 1.0);
}

#define TIME       iTime
#define RESOLUTION iResolution

// IQ's box used as reference
float box(vec2 p, vec2 b) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// Variant of IQ's vesica: https://www.shadertoy.com/view/XtVfRW
// I wanted to be able to specify the dimensions of the vesica object
//  so modified the function slightly
float vesica(vec2 p, vec2 sz) {
  if (sz.x < sz.y) {
    sz = sz.yx;
  } else {
    p  = p.yx; 
  }
  vec2 sz2 = sz*sz;
  float d  = (sz2.x-sz2.y)/(2.0*sz.y);
  float r  = sqrt(sz2.x+d*d);
  float b  = sz.x;
  p = abs(p);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

float df(vec2 p) {
  vec2 sz = vec2(mix(1.0, 2.0, 0.5+0.5*cos(TIME*sqrt(0.5))), mix(1.0, 2.0, 0.5+0.5*sin(TIME)));
  float d0 = vesica(p, sz*0.5);
  float d1 = box(p, sz*0.5);
  d1 = abs(d1) - 0.01;
  float d = d0;
  if (mod(floor(TIME), 2.0) == 0.0) {
    d = min(d0, d1);
  }
  return d;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  float d = df(p);

  vec3 col = vec3(0.1);

  col = mix(col, vec3(0.9), smoothstep(-aa, aa, -d));
  col += vec3(1.0, 0.0, 0.0)*sin(100.0*d);

  fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float sdMandelbulb(vec3 pos) {
	vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
	for (int i = 0; i < 255; i++) {
		r = length(z);
		if (r>4.) break;
		// convert to polar coordinates
		float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
		dr =  pow( r, 8.)*8.*dr + 1.0;

		// scale and rotate the point
		float zr = pow( r,8.);
		theta = theta*8.;
		phi = phi*8.;

		// convert back to cartesian coordinates
		z = zr *vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z+=pos;
	}
	return 0.5*log(r)*r/dr;
}

float GetDist(vec3 p) {

    float mb = sdMandelbulb(p);
    float box = sdBox(p, vec3(1));


    float d = max(box, mb);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 Transform(vec3 p) {
    p.xy *= Rot(p.z*.15);
	p.z -= iTime*.1;
    p.y -= .3;
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        p = Transform(p);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif) * vec3(0.1, 0.4, 0.7);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define PI            3.141592654
#define TAU           (2.0*PI)
#define TIME          iTime
#define RESOLUTION    iResolution

const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
// https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
// Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const vec3  grid_color    = HSV2RGB(vec3(0.6, 0.3, 1.0)); 
const vec3  light0_color  = 16.0*HSV2RGB(vec3(0.6, 0.5, 1.0)); 
const vec3  light1_color  = 8.0*HSV2RGB(vec3(0.9, 0.25, 1.0)); 
const vec3  sky0_color    = HSV2RGB(vec3(0.05, 0.65, -0.25)); 
const vec3  sky1_color    = HSV2RGB(vec3(0.6, 0.5, 0.25)); 
const vec3  light0_pos    = vec3(5.0, 4.0, 4.0);
const vec3  light1_pos    = vec3(3.0, -1.0, -8.0);
const vec3  light0_dir    = normalize(light0_pos);
const vec3  light1_dir    = normalize(light1_pos);
const vec4  planet_sph    = vec4(50.0*normalize(light1_dir+vec3(0.025, -0.025, 0.0)), 10.0);
const vec2   iris_center  = vec2(0.0, 0.28);

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, vec3(1.0/2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

float vesica(vec2 p, float r, float d) {
  p = abs(p);
  float b = sqrt(r*r-d*d);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

// IQ's ray sphere intersect: https://iquilezles.org/articles/intersectors
vec2 raySphere(vec3 ro, vec3 rd, vec4 sph) {
  vec3 oc = ro - sph.xyz;
  float b = dot( oc, rd );
  float c = dot( oc, oc ) - sph.w*sph.w;
  float h = b*b - c;
  if (h < 0.0) return vec2(-1.0);
  h = sqrt(h);
  return vec2(-b - h, -b + h);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec3 toSpherical(vec3 p) {
  float r   = length(p);
  float t   = acos(p.z/r);
  float ph  = atan(p.y, p.x);
  return vec3(r, t, ph);
}

float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

vec2 df(vec2 p) {
  const float iris_outer = 0.622;
  const float iris_inner = 0.285;

  float a  = mix(0.0, 0.85, smoothstep(0.995, 1.0, cos(TAU*TIME/5.0)));
  const float b = 4.0;
  float rr = mix(1.6, b, a);
  float dd = mix(1.12, b, a);

  float t0 = abs(0.9*p.x);
  t0 *= t0;
  t0 *= t0;
  t0 *= t0;
  t0 = clamp(t0, 0.0, 1.0);
  float dt0 = mix(0.0125, -0.0025, t0);

  vec2 p0 = p;
  p0 = p0.yx;
  float d0 =  vesica(p0, rr, dd);
  float d5 = d0;

  vec2 p1 = p;
  p1 -= iris_center;
  float d1 = circle(p1, iris_outer);
  d1 = max(d1,d0+dt0);
  float d6 = d1;

  vec2 p2 = p;
  p2 -= vec2(-0.155, 0.35);
  float d2 = circle(p2, 0.065);

  vec2 p3 = p;
  p3 -= iris_center;
  p3 = toPolar(p3);
  float n3 = mod1(p3.x, 0.05);
  float d3 = abs(p3.x)-0.0125*(1.0-1.0*length(p1));

  vec2 p4 = p;
  p4 -= iris_center;
  float d4 = circle(p4, iris_inner);

  d3 = max(d3,-d4);

  d1 = pmax(d1,-d2, 0.0125);
  d1 = max(d1,-d3);

  d0 = abs(d0)-dt0;

  float d = d0;
  d = pmin(d, d1, 0.0125);
  return vec2(d, d6);
}

vec3 render_background(vec3 ro, vec3 rd, vec3 nrd) {
  vec3 srd = toSpherical(rd.xzy);
  srd.z += 0.02*TIME;
  vec2 pg  = srd.yz;
  float f  = sin(pg.x); 
  float lf2= ceil(log(f)/log(2.0)-0.505);
  float mf = pow(2.0, lf2);

  const float aa = 0.005;
  const float count = 20.0;
  const vec2 sz = vec2(2.0*PI/count);
  vec2 ng = mod2(pg, vec2(mf, 1.0)*sz);

  float dg = min(abs(pg.y)*f, abs(pg.x))-aa*0.0;
  vec3 lines = grid_color*smoothstep(-aa, aa, -dg)*f*f;

  vec3 sky  = smoothstep(1.0, 0.0, rd.y)*sky1_color+smoothstep(0.5, 0.0, rd.y)*sky0_color;

  vec2 pi = raySphere(ro, rd, planet_sph);

  float lf1 = 1.0;
  if (pi.x > 0.0) {
    vec3 ppos = ro+rd*pi.x;
    float t = 1.0-tanh_approx(1.5*(pi.y - pi.x)/planet_sph.w);
    sky *= mix(0.5, 1.0, t);
    lf1 = t;
  } else {
    sky += lines;
  }

  sky += pow(max(dot(rd, light0_dir), 0.0), 800.0)*light0_color; 
  sky += pow(max(dot(rd, light0_dir), 0.0), 80.0)*light1_color*0.1; 
  sky += lf1*pow(max(dot(rd, light1_dir), 0.0), 150.0)*light1_color; 
  sky += lf1*pow(max(dot(rd, light1_dir), 0.0), 50.0)*light0_color*0.1; 


  return sky;
}

vec4 render_iris(vec2 p, vec2 q, float d) {
  p -= iris_center;
  float aa = 2.0/RESOLUTION.y;

  float a = smoothstep(-aa, aa, -d);
  float b = smoothstep(0.0, 0.125, -d);

  vec3 ro = 1.0*vec3(2.0, 4.0, -1.0);
  vec3 la = vec3(0.0, 0.0, 0.0); 

  vec2 np   = p + vec2(4.0/RESOLUTION.y); 

  vec3 ww   = normalize(la - ro);
  vec3 uu   = normalize(cross(vec3(0.0,1.0,0.0), ww));
  vec3 vv   = normalize(cross(ww,uu));
  float rdd = 2.0;
  vec3 rd   = normalize(p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd  = normalize(np.x*uu + np.y*vv + rdd*ww);

  vec4 sph  = vec4(vec3(0.0), 1.78);

  vec2 si   = raySphere(ro, rd, sph);

  vec3 pos  = ro + rd*si.x;

  vec3 nor  = normalize(pos - sph.xyz);

  vec3 ref  = reflect(rd, nor); 
  vec3 nref = reflect(nrd, nor); 

  vec3 rbkg = render_background(pos, ref, nref); 

  vec4 col = vec4(rbkg, 1.0);

  col.xyz*=b;
  col.w = a;


  return col;
}

vec3 effect(vec2 p, vec2 q) {
  float aa = 2.0/RESOLUTION.y;
  vec2 d   = df(p);

  vec4 dcol = vec4(0.0, 0.0, 0.0, 0.5);
  dcol.w    = mix(0.0, 0.95, smoothstep(-aa, aa, -d.x));
  vec4 scol = render_iris(p, q, d.y); 

  vec3 col  = vec3(1.0);
  col = alphaBlend(col, dcol);
  col = alphaBlend(col, scol);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  vec3 col = effect(p, q);
  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

int bcd(int i) // 0 - 99
{
    return (i/10*6+i);
}

float digit(vec2 uv, int i)
{
    const float doff=12./16.;

    return texture(iChannel0,uv/16.+vec2(float(i)/16.,doff)).x;
}

void draw_digit(inout float c, vec2 uv, int i)
{
    if(abs(uv.x)<.5 && abs(uv.y) < .5)
    {
        c = max(c,digit(uv+.5,i));
    }
}

void draw_bcd(inout float c, vec2 uv, int i)
{
    draw_digit(c, uv,(i&0xf0)>>4);
    draw_digit(c, uv-vec2(.5,0),(i&0xf));
}

#define FRAME_RATE 50.


float draw_time(vec2 uv, vec2 o, float size)
{
    float c = 0.;
    uv=(uv-o)/size - vec2(.25,-.5);

    int hour = bcd(int(iDate.w/3600.));
    int minute = bcd(int(mod(iDate.w,3600.)/60.));
    int second = bcd(int(mod(iDate.w,60.)));

    int pal_frame = bcd(int(fract(iDate.w)*FRAME_RATE));

    draw_bcd(c,uv,hour);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,minute);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,second);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,pal_frame);
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col=mix(vec3(0),vec3(1),draw_time(uv,vec2(-.65,0.125),.3+sin(iTime)*.01));

    fragColor = vec4(col,1.0);
}

// Desmos graph: https://www.desmos.com/calculator/rpdsgju4tl

// Utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

// Number of points
#define N 50

// Minimizing Î£(p[n].y - (a*p[n].x + b))Â² wrt a and b
vec2 bestFitLinear(in vec2[N] p) {
    vec4 w = vec4(0.0);
    for (int n=0; n < N; n++) w += vec4(p[n], p[n] * p[n].xx);
    float b = (w.y * w.z - w.x * w.w) / (float(N) * w.z - w.x * w.x);
    return vec2((w.w - w.x * b) / w.z, b);
}

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

vec2 getOffset(in float id) {
    float freq = Hash11(id * 393.84 + 673.48) * 10.0 - 5.0;
    float phase = Hash11(id * 348.46 + 183.37);
    float amp = Hash11(id * 275.35 + 741.69);
    float ang = iTime * freq + phase;
    return vec2(cos(ang), sin(ang)) * amp;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec4 mouse = (abs(iMouse) - center.xyxy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec4(-2.0, cos(iTime), 2.0, sin(iTime));

    // Grid
    drawSDF(abs(mod(uv.x + 0.25, 0.5) - 0.25), vec3(0.0, 0.0, 1.0));
    drawSDF(abs(mod(uv.y + 0.25, 0.5) - 0.25), vec3(0.0, 0.0, 1.0));
    drawSDF(abs(uv.x), vec3(1.0, 0.0, 0.0));
    drawSDF(abs(uv.y), vec3(1.0, 0.0, 0.0));

    // Line drawn by the mouse
    vec2 p1 = mouse.zw, p2 = mouse.xy;
    float a = (p2.y - p1.y) / (p2.x - p1.x);
    float b = p1.y - a * p1.x;

    // Generate random points distributed on a line
    vec2[N] points;
    for (int n=0; n < N; n++) {
        float x = float(n) / float(N) * 4.0 - 2.0;
        points[n] = vec2(x, a * x + b) + getOffset(float(n));
        drawSDF(length(uv - points[n]) - 0.05, vec3(0.0));
    }

    // Draw the mouse line
    //drawSDF(abs(uv.y - (a * uv.x + b)) / sqrt(1.0 + a * a), vec3(0.0, 1.0, 0.0));

    // Draw best linear fit
    vec2 fit = bestFitLinear(points);
    drawSDF(abs(uv.y - (fit.x * uv.x + fit.y)) / sqrt(1.0 + fit.x * fit.x), vec3(1.0, 0.0, 0.0));

    fragColor = vec4(color, 1.0);
}

/*
	Full Scene Radial Blur
	----------------------

	Radial blur - as a postprocessing effect - is one of the first things I considered doing 
	when the multipass system came out. I've always loved this effect. Reminds me of the early 
	demos from Aardbei et al. 

	Anyway, Shadertoy user, Passion, did a really cool radial blur on a field of spheres that
	inspired me to do my own. Radial blurs are pretty straight forward, but it was still
    helpful to have Passion's version as a guide. 

    As for the radial blur process, there's not much to it. Start off at the pixel position, 
    then radiate outwards gathering up pixels with decreased weighting. The result is a
	blurring of the image in a radial fashion, strangely enough. :)

	Inspired by:

	Blue Dream - Passion
	https://www.shadertoy.com/view/MdG3RD

	Radial Blur - IQ
	https://www.shadertoy.com/view/4sfGRn

	Rays of Blinding Light - mu6k
	https://www.shadertoy.com/view/lsf3Dn

*/

// The radial blur section. Shadertoy user, Passion, did a good enough job, so I've used a
// slightly trimmed down version of that. By the way, there are accumulative weighting 
// methods that do a slightly better job, but this method is good enough for this example.


// Radial blur samples. More is always better, but there's frame rate to consider.
const float SAMPLES = 24.; 


// 2x1 hash. Used to jitter the samples.
float hash( vec2 p ){ return fract(sin(dot(p, vec2(41, 289)))*45758.5453); }


// Light offset.
//
// I realized, after a while, that determining the correct light position doesn't help, since 
// radial blur doesn't really look right unless its focus point is within the screen boundaries, 
// whereas the light is often out of frame. Therefore, I decided to go for something that at 
// least gives the feel of following the light. In this case, I normalized the light position 
// and rotated it in unison with the camera rotation. Hacky, for sure, but who's checking? :)
vec3 lOff(){    

    vec2 u = sin(vec2(1.57, 0) - iTime/1.);
    mat2 a = mat2(u, -u.y, u.x);

    vec3 l = normalize(vec3(1.5, 1., -0.5));
    l.xz = a * l.xz;
    l.xy = a * l.xy;

    return l;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Screen coordinates.
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Radial blur factors.
    //
    // Falloff, as we radiate outwards.
    float decay = 0.99; 
    // Controls the sample density, which in turn, controls the sample spread.
    float density = 0.2; 
    // Sample weight. Decays as we radiate outwards.
    float weight = 0.1; 

    // Light offset. Kind of fake. See above.
    vec3 l = lOff();

    // Offset texture position (uv - .5), offset again by the fake light movement.
    // It's used to set the blur direction (a direction vector of sorts), and is used 
    // later to center the spotlight.
    //
    // The range is centered on zero, which allows the accumulation to spread out in
    // all directions. Ie; It's radial.
    vec2 tuv =  uv - .5 - l.xy*.45;

    // Dividing the direction vector above by the sample number and a density factor
    // which controls how far the blur spreads out. Higher density means a greater 
    // blur radius.
    vec2 dTuv = tuv*density/SAMPLES;

    // Grabbing a portion of the initial texture sample. Higher numbers will make the
    // scene a little clearer, but I'm going for a bit of abstraction.
    vec4 col = texture(iChannel0, uv.xy)*0.25;

    // Jittering, to get rid of banding. Vitally important when accumulating discontinuous 
    // samples, especially when only a few layers are being used.
    uv += dTuv*(hash(uv.xy + fract(iTime))*2. - 1.);

    // The radial blur loop. Take a texture sample, move a little in the direction of
    // the radial direction vector (dTuv) then take another, slightly less weighted,
    // sample, add it to the total, then repeat the process until done.
    for(float i=0.; i < SAMPLES; i++){

        uv -= dTuv;
        col += texture(iChannel0, uv) * weight;
        weight *= decay;

    }

    // Multiplying the final color with a spotlight centered on the focal point of the radial
    // blur. It's a nice finishing touch... that Passion came up with. If it's a good idea,
    // it didn't come from me. :)
    col *= (1. - dot(tuv, tuv)*.75);

    // Smoothstepping the final color, just to bring it out a bit, then applying some 
    // loose gamma correction.
    fragColor = sqrt(smoothstep(0., 1., col));

    // Bypassing the radial blur to show the raymarched scene on its own.
    //fragColor = sqrt(texture(iChannel0, fragCoord.xy / iResolution.xy));
}

