#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s = 1./512.;
    if(iFrame == 0) {
        fragColor = vec4(fragCoord / iResolution.xy,1.0,1.0);
    } else {
        vec4 data = texture(iChannel0,fragCoord / iResolution.xy);
        fragColor = vec4(data.rgb,1.0);
        float self = dot(vec3(1),texture(iChannel1,data.rg).rgb);
        if(data.b == 1.0) {
            if(data.r < 1.-stepSize && abs(self - dot(vec3(1),texture(iChannel1,check(vec2(stepSize,0))).rgb)) < diff)
                fragColor = vec4(texture(iChannel0,data.rg + vec2(stepSize,0)).rg,1.0,1.0);
            else
                fragColor = vec4(data.rg,0.75,1.0);
        }
        if(data.b == 0.75) {
            if(data.g < 1.-stepSize&& abs(self - dot(vec3(1),texture(iChannel1,check(vec2(0,stepSize))).rgb)) < diff)
                fragColor = vec4(texture(iChannel0,data.rg + vec2(0,stepSize)).rg,0.75,1.0);
            else
