// 'Room 237 (The Shining)' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/NlsXR4 (YouTube: https://youtu.be/w_RCsMP70-I)
//
// Processed by 'GLSL Shader Shrinker' (Shrunk by 1,704 characters)
// (https://github.com/deanthecoder/GLSLShaderShrinker)
//
// Another scene based on 'The Shining'.
// Weird, as I don't actually like the movie too much!
// It's in Ready Player One though, and that's great...
//
// Tricks to get the performance:
// - The curtain is actually a flat plane, textured
//   when the material color is added.
// - The 'person' doesn't have an SDF - Just a subtle
//   color added to the curtain material.
// - The SDF for some objects (E.g. the toilet) is not
//   calculated if the march point is 'too far away'.
// - Use of abs() to mirror SDF content.
//   (E.g. shelves, wall lights, sinks, ...)
// - Keep the material (lights(...)) code outside of the
//   marching 'for' loop to reduce compilation times.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane,
// Blackle and a bunch of others for sharing their knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define Z0	min(iTime, 0.)
#define sat(x)	clamp(x, 0., 1.)

float g = 0.,
      furn = 1.;

#define AA // Comment out this line to speed things up!

struct Hit {
	float d;
	int id;
	vec3 uv;
};


// Thnx Dave_Hoskins - https://www.shadertoy.com/view/4djSRW
float hash31(vec3 p3) {
	p3 = fract(p3 * .1031);
	p3 += dot(p3, p3.yzx + 3.3456);
	return fract((p3.x + p3.y) * p3.z);
}

float hash21(vec2 p) { return hash31(p.xyx); }

vec4 hash44(vec4 p) { p = fract(p * .1031); p *= p + 3.3456; return fract(p * (p + p)); }

float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, s.y + s.z) + dot(ip, s);
	h = mix(hash44(h), hash44(h + s.x), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float n21(vec2 p) { return n31(vec3(p, 1)); }

float smin(float a, float b, float k) {
	float h = sat(.5 + .5 * (b - a) / k);
	return mix(b, a, h) - k * h * (1. - h);
}

#define minH(a)	if (a.d < h.d) h = a

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float sdBox2D(vec2 p, vec2 b) {
	return length(max(abs(p) - b, 0.));
}

float sdCyl(vec3 p, vec2 hr) {
	vec2 d = abs(vec2(length(p.xz), p.y)) - hr;
	return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

vec3 rayDir(vec3 ro, vec3 lookAt, vec2 uv) {
	vec3 f = normalize(lookAt - ro),
	     r = normalize(cross(vec3(0, 1, 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}

Hit map(vec3 p) {
	float bath, bulb, lid, bowl, curt,
	      d = abs(p.z) - .4;
	d = min(max(min(min(min(max(d, -(sdBox2D(p.xy, vec2(1.6, 6)) - 1.8)), min(2. - p.z, max(sdBox2D(p.yz - vec2(0, 2.4), vec2(1.78)), 3.5 - abs(p.x)))), sdBox2D(p.yz, vec2(.2, 1.6))), 4.1 - abs(p.x)), -(sdBox2D(p.zy + vec2(5.5, 0), vec2(.7, 5.2)) - 1.8)), 6.5 - abs(p.x));
	float bathRimCut = smoothstep(1., .5, abs(p.x) - 1.) * .25 + p.y - 1.7;
	bath = max(abs(sdBox(p - vec3(0, 2, 1.2), vec3(3.5, 2, .7))), p.y - 1.8);
	furn = min(max(bath, bathRimCut), length(vec2(bath, bathRimCut)) - .08);
	d = min(d, furn);
	vec3 c,
	     q = p;
	q.x = abs(q.x);
	q += vec3(-5.2, -4.6, 2.71);
	d = max(d, -sdBox(q, vec3(.8, 1.8, .3)));
	q.y = abs(q.y + .45) - .5;
	float chrome = sdBox(q, vec3(.8, .04, .3));
	chrome = min(chrome, max(abs(q.z), 4.5 - abs(p.x)));
	bulb = length(q.xz - vec2(1.15, -2.2)) - .12;
	q.y = p.y - 5.;
	chrome = min(chrome, max(bulb, abs(abs(q.y) - .76) - .15));
	bulb = max(bulb, abs(q.y) - .6);
	q = p - vec3(-5.4, 1.3, -4);
	q.x *= .7;
	lid = sdCyl(q, vec2(.7, .02)) - .03;
	if (lid < 2.) {
		furn = min(min(furn, max(sdCyl(q + vec3(0, .12, 0), vec2(.53, .1)) - .18, .03 - lid)), lid);
		furn = min(furn, max(length(q.xz) - .4 + sin(q.y * 4. - .4) * .1, q.y));
		furn = smin(furn, max(length(q.yz + vec2(.8, 0)) - .2, q.x), .2);
	}

	q = p - vec3(5.5, 2.2, -5.2);
	q.z = abs(q.z) - 1.2;
	bowl = sdBox(q, vec3(.4, .2, .4));
	if (bowl < 1.) {
		furn = min(furn, min(sdBox(q, vec3(.8, .15, .8)) - .1, max(bowl - .2, q.y)));
		furn = max(furn, .18 - bowl);
	}

	d = min(d, furn);
	chrome = min(min(chrome, sdBox(q - vec3(1.95, 2, 0), vec3(1, 1.4, .6))), smin(sdCyl(q.xzy + vec3(.8, 0, .7), vec2(.04, .6)), max(length(abs(q.xz + vec2(.2, 0)) - .6) - .04, q.y), .08));
	d = min(min(d, abs(p.y)), abs(p.y - 8.7));
	c = p;
	c.x += sin(iTime) * .1 * -(cos((c.y + 3.) * .6) - 1.) * smoothstep(-3.5, 2., c.x);
	curt = max(max(abs(c.z), c.x), 1. - c.y - sin(c.x * 8.) * .06);
	q = p - vec3(0, 8.2, -2);
	bulb = min(bulb, length(q) - .5);
	g += .01 / (.2 + bulb * bulb);
	float bulbHolder = length(q.xz) - 1.;
	d = min(d, min(max(bulbHolder, .4 - q.y), max(bulbHolder + .3, .3 - q.y)));
	Hit h = Hit(d, 1, p);
	minH(Hit(chrome, 0, p));
	minH(Hit(bulb, 2, p));
	minH(Hit(curt, 3, c));
	return h;
}

vec3 N(vec3 p) {
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .005773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * .01).d;
	}

	return normalize(n);
}

float shadow(vec3 p, vec3 ld) {
	float i, h,
	      s = 1.,
	      t = .1;
	for (i = Z0; i < 30.; i++) {
		h = map(t * ld + p).d;
		s = min(s, 15. * h / t);
		t += h;
		if (s < .001 || t > 50.) break;
	}

	return sat(s);
}

float ao(vec3 p, vec3 n, float h) { return map(h * n + p).d / h; }

vec3 vig(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *= .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	return c;
}

vec3 lights(vec3 p, vec3 rd, Hit h) {
	if (h.id == 2) return vec3(1.6);
	if (h.id == 0) return vec3(.01);
	vec3 c,
	     ld = normalize(vec3(0, 6.5, -5) - p),
	     n = N(p);
	float w, lig,
	      ss = 0.,
	      gg = g,
	      spe = 2.;
	vec2 pp;
	if (h.id == 1) {
		if (abs(n.y - 1.) < .01 && p.y < .3) {
			c = vec3(.5, .5, .4) - n21(p.xz * 20.) * .1;
			c -= .2 * smoothstep(.6, 0., furn);
			pp = p.xz;
			pp.y += 5.;
			c = mix(c, vec3(.5, .25, .25), step(abs(min(sdBox2D(pp, vec2(1)), sdBox2D(pp, vec2(2, .2))) - 1.), .15));
		}
		else if (p.y < .35 && furn > .01) c = vec3(.05);
		else if (p.y > 8.6 || p.y > 3. && (abs(p.z + .4) < .1 || abs(abs(p.x) - 4.1) < .01)) c = mix(vec3(.35, .38, .36), vec3(.4, .3, .1), step(p.y, 3.5));
		else c = vec3(.16, .35, .17) + step(furn, .0015) * .15;
	}
	else if (h.id == 3) {
		pp = h.uv.xy;
		w = sin(pp.x * 8.) * .8 + sin(pp.x * 20.) * sin(pp.y * .4);
		c = vec3(.5, .76 + w * .07, .5) + step(length(mod(pp - vec2(0, w * .02), .2) - .1), .03) * .15;
		n.x += w / 4.;
		n = normalize(n);
		spe = .04;
		pp = p.xy;
		pp.x += sin(iTime * .3 + pp.y) * .08;
		c = mix(c, -smoothstep(1., 0., min(length(pp * vec2(2, 1) - vec2(-2.8, 3)), length(pp - vec2(-1.3, 2.2)))) * vec3(.6, 1.8, 1.8), .1);
	}
	else {
		ss = 1.8;
		c = vec3(.13, .09, .09);
		c *= .5 + .5 * smoothstep(-.1 - sin(h.uv.y * 3.5) * .1, 0., h.uv.x);
	}

	float ao = mix(ao(p, n, .1), ao(p, n, .8), .5),
	      l1 = sat(.3 + .7 * dot(ld, n)) * (.7 + .3 * shadow(p, ld)) * (.6 + .4 * ao),
	      l2 = sat(n.y * -.3) + pow(sat(dot(rd, reflect(ld, n))), 2.) * spe,
	      fre = smoothstep(.7, 1., 1. + dot(rd, n)) * .5;
	lig = l1 + l2 * ao + ss;
	g = gg;
	return mix(lig * c * vec3(1.8, 1.6, 1.5), vec3(.01), fre);
}

vec4 march(inout vec3 p, vec3 rd, float s) {
	g = 0.;
	Hit h;
	for (float i = Z0; i < s; i++) {
		h = map(p);
		if (abs(h.d) < .0015) break;
		p += h.d * rd;
	}

	return vec4(g + lights(p, rd, h), h.id);
}

vec3 scene(vec3 p, vec3 rd) {
	vec4 l,
	     col = vec4(0);
	float a = 1.;
	for (int i = 0; i < 3; i++) {
		l = march(p, rd, 64.);
		col += l * a;
		if (l.w > 0.) break;
		a *= .9;
        vec3 n = N(p);
		rd = reflect(rd, n);
		p += n * .01;
	}

	return col.rgb;
}

void mainImage(out vec4 fragColor, vec2 fc) {
	float t = mod(iTime, 25.),
	      f = smoothstep(0., 14.5, t);
	vec2 uv = (fc - .5 * iResolution.xy) / iResolution.y;
	vec3 lookAt = mix(vec3(-1. + f, 4, 0), vec3(8, 5, -3), sat(1.2 * pow(sin(sat(f * 1.2 - .2) * 3.141), 4.))),
	     ro = vec3(.1, 4, -14. + f * 7.),
         col = scene(ro, rayDir(ro, lookAt, uv));
#ifdef AA
	if (fwidth(col.r) > .1) {
		for (float dx = Z0; dx <= 1.; dx++) {
			for (float dy = Z0; dy <= 1.; dy++)
				col += scene(ro, rayDir(ro, lookAt, uv + (vec2(dx, dy) - .5) / iResolution.xy));
		}

		col /= 5.;
	}

#endif
	col.g -= hash21(fc) * .04;
	fragColor = vec4(vig(pow(max(vec3(0), col), vec3(.45)) * sat(t), fc), 0);
}

// Constants
#define PI 3.14159265359

// Utilities
#define drawSDF(dist, col, opaq) color = mix(color, col, smoothstep(unit, 0.0, dist) * opaq)
struct TightBBox { mat2 ori; vec2 bmin; vec2 bmax; };

// SDFs
float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

// ax: vec2(cos(axis), sin(axis))
// ap: vec2(cos(aperture), sin(aperture))
float sdArc(in vec2 p, in vec2 ax, in vec2 ap, in float r, in float t) {
    p = vec2(dot(p, ax), abs(p.y * ax.x - p.x * ax.y));
    return (p.y * ap.x < p.x * ap.y ? abs(length(p) - r) : length(p - ap * r)) - t;
}

TightBBox arcBoundingBox(in vec2 ax, in vec2 ap, in float r, in float t) {
    float y = (ap.x < 0.0 ? r : ap.y * r) + t;
    return TightBBox(mat2(ax, -ax.y, ax.x), vec2(ap.x * r - t, -y), vec2(r + t, y));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 2.0;
    vec3 color = vec3(pow(max(0.0, 1.0 - length(fragCoord / iResolution.xy - 0.5)), 0.8));
    float unit = 4.0 / iResolution.y;

    // Animated arc parameters
    float axis = PI * sin(iTime);
    float aper = PI * (0.5 + 0.5 * cos(iTime));
    float thick = 0.025 + 0.025 * sin(iTime);
    float rad = 0.55;

    // Set to be equivalent to axis aligned bbox
    // of a circle
    if (iTime < 3.0) {
        axis = 0.5 * PI;
        aper = PI;
    }

    // Start rotating the bbox
    else if (iTime < 8.0) {
        axis = PI * sin(0.5 * iTime);
        aper = PI;
    }

    // Transition to full animation with tight bbox
    else {
        float transition = smoothstep(8.0, 13.0, iTime);
        axis = mix(PI * sin(0.5 * iTime), axis, transition);
        aper = mix(PI, aper, transition);
    }

    // Draw arc
    vec2 csAxis = vec2(cos(axis), sin(axis));
    vec2 csAper = vec2(cos(aper), sin(aper));
    drawSDF(abs(sdArc(uv, csAxis, csAper, rad, thick)), vec3(0.0), 1.0);

    // Compute tight bbox
    TightBBox bbArc = arcBoundingBox(csAxis, csAper, rad, thick);

    // Transform into bbox local coordinate frame
    vec2 bp = uv * bbArc.ori;

    // Point in bounding box test
    //if (bp.x > bbArc.bmin.x && bp.x < bbArc.bmax.x &&
    //    bp.y > bbArc.bmin.y && bp.y < bbArc.bmax.y) {
    //    color -= 0.25;
    //}

    // Draw as SDF instead for free antialiasing
    drawSDF(sdBox(bp - 0.5 * (bbArc.bmin + bbArc.bmax), 0.5 * (bbArc.bmax - bbArc.bmin)), vec3(0.0), 0.25);

    fragColor = vec4(color, 1.0);
