vec3 drawSegments(vec2 p, vec2 p1, vec2 p2, vec2 p3, vec3 col){
    vec2 g, prev = p1;
    float dt = 1./numSegments;  

    for(float t = 0.; t <= 1.+dt; t+=dt){   


        g = p3*(t*t) + p2*2.*(1.-t)*t + p1*(1.-t)*(1.-t);

