vec3 drawExtrema(vec2 p, vec2 p1, vec2 p2, vec2 p3, vec3 boxCol, vec3 col){

    vec2 g = vec2(0.0);

    float tx = clamp((p1.x - p2.x)/(p1.x - 2.*p2.x + p3.x),0.,1.);
    float x = p3.x*(tx*tx) + p2.x*2.*(1.-tx)*tx + p1.x*(1.-tx)*(1.-tx);

    float ty = clamp((p1.y - p2.y)/(p1.y - 2.*p2.y + p3.y),0.,1.);
    float y = p3.y*(ty*ty) + p2.y*2.*(1.-ty)*ty + p1.y*(1.-ty)*(1.-ty);

    //col = drawRing(segRingRadius, .003, p, vec2(x,y), col,vec3(1.,0.,0.));


    float top = min(y, min(p1.y,p3.y));
    float bottom = max(y, max(p1.y,p3.y));
    float right = min(x, min(p1.x,p3.x));
    float left = max(x, max(p1.x,p3.x));

    vec2 uv = p;
