    vec2 xAxis = vec2(1.,0.);
    //dot a*b = |a|*|b|*cos(a)

    //get the angle between p3 and x axis to put it right on the x line
    float a = acos(dot(xAxis,p3Prime) /(length(xAxis)*length(p3Prime)));

    //save p3.y for reversing the process at the end
    float p3y = p3Prime.y;


