    //because acos only gives an angle between 0 and pi as we move towards 180
    //then from 180 to 360 the angle decreases again to 0.
    //p2Prime=p2Prime*rot(a*sign(p3y));
    //p3Prime=p3Prime*rot(a*sign(p3y));

    //an optimization of above two lines by Oneshade (no trig!)
    float r = length(p3Prime);
    vec2 cosi = p3Prime / r; // Force the point onto the unit circle to get sin/cos
    p2Prime = p2Prime * mat2(cosi, -cosi.y, cosi.x); // Rotate
    p3Prime = vec2(r, 0.0); // Because we are undoing the rotation of p3Prime


    vec2 line1 = p2-p1;
