float sdParabola(in vec2 p, in float h) {
    float x = sqrt(0.25 + max(0.0, abs(p.x * h) + p.y * h)) - 0.5;
    return (x * x / h - p.y) * sign(h);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    float time = iTime * 0.5;

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(0.5, 0.5 * sin(time)) * cos(time);

    float a = sin(0.25 * iTime) * 16.0;
    float b = cos(0.25 * iTime);
    float c = 0.25 * sin(0.5 * iTime);
    vec2 vertex =  vec2(-0.5 * b / a, c - 0.25 * b * b / a);

    float d = sdParabola(uv - vertex, a);
    d *= 2.0; // Shrink isolines

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d)));

    float mDist = abs(sdParabola(mouse - vertex, a));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - mouse) - 0.008));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, abs(max(abs(uv.x - mouse.x), abs(uv.y - mouse.y)) - mDist) - 0.001));

    fragColor = vec4(color, 1.0);
}

