vec2 curve(vec2 uv)
{
	uv = (uv - 0.5) * 2.0;
	uv *= 1.1;	
	uv.x *= 1.0 + pow((abs(uv.y) / 5.0), 3.0);
	uv.y *= 1.0 + pow((abs(uv.x) / 4.0), 3.0);
	uv  = (uv / 2.0) + 0.5;
	uv =  uv *0.92 + 0.04;
