// link to video explaining below function:
// https://www.youtube.com/watch?v=ELQG5OvmAE8&ab_channel=EgoMoose
float RaySegmentDistance(vec3 start, vec3 end, vec3 ray_origin, vec3 ray_screen_intersection) { 
	vec3 q = start - ray_origin;
    vec3 r = end - start;
    vec3 s = ray_screen_intersection - ray_origin;

    float t = (dot(q, s) * dot(r, s)) - (dot(q, r) * dot(s, s));
    t = t / ((dot(r, r) * dot(s, s)) - (dot(r, s) * dot(s, r)));

    float u = dot(q, s) + (t * dot(r, s)); 
    u = u / dot(s, s);

    if (t > 1.0 || t < 0.0 || u < 0.0) { return 100.0; }

    vec3 P0 = start + t * r;
    vec3 P1 = ray_origin + u * s;

    return distance(P0, P1);
}

int BinomialCoefficient(int n, int k) {
    return factorials[n] / (factorials[k] * factorials[n-k]);
}

float DistLine(vec3 ray_origin, vec3 ray_direction, vec3 point) {
    return length(cross(point - ray_origin, ray_direction)) / length(ray_direction);
}

float DrawPoint(vec3 ray_origin, vec3 ray_direction, vec3 point) {
    float dist = DistLine(ray_origin, ray_direction, point);
    //dist = 1.0 - smoothstep(0.01, 0.09, dist);

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mo = vec2(0.0);
    if (iMouse.z > 0.0) {
        mo += (2.0 * iMouse.xy - iResolution.xy) / iResolution.yy;
    }

    vec4 camPos = textureLod( iChannel0, vec2(.5,.5)/iResolution.xy, 0.0 );
    vec4 camRot = textureLod( iChannel0, vec2(1.5,.5)/iResolution.xy, 0.0 )*6.28318530718;

    float zoom = 1.0;
    zoom = textureLod( iChannel2, vec2(1.5,.5)/iResolution.xy, 0.0 ).x;
    vec3 ray_origin = vec3(0.0,  0.0, -3.0);     // camera point

    //ray_origin = normalize( vec3( (fragCoord.xy-iResolution.xy*.5)/iResolution.x, 1.0 ) );

    ray_origin.zy = ray_origin.zy*cos(camRot.x) + sin(camRot.x)*vec2(1,-1)*ray_origin.yz;
    ray_origin.xz = ray_origin.xz*cos(camRot.y) + sin(camRot.y)*vec2(1,-1)*ray_origin.zx;

    vec3 lookat = vec3(0.5); // center of scene
    vec3 forward_vector = normalize(lookat - ray_origin);
    vec3 right_vector = cross(vec3(0.0, 1.0, 0.0), forward_vector);
    vec3 up_vector = cross(forward_vector, right_vector);

    vec3 screen_center = ray_origin + forward_vector * zoom;

    vec3 ray_screen_intersection = screen_center + uv.x * right_vector + uv.y * up_vector;
    vec3 ray_direction = ray_screen_intersection - ray_origin; // point at screen

    float t = fract(iTime / SLOW_DOWN);

    if (int(iTime / SLOW_DOWN) % 2 == 1 && BACKWARDS_ANIMATION) {
        t = 1.0 - t;
    }

    control_points[max_count - 1] = control_points[control_points.length() - 1];

    vec3 final_point = vec3(0.0);

    float t2 = (1.0 - t);

    for (int point_index = 0; point_index < max_count; ++point_index) {
        final_point += float(BinomialCoefficient(max_count - 1, point_index)) 
                 * pow(t2, float(max_count - 1 - point_index)) 
                 * pow(t, float(point_index))
                 * control_points[point_index];
     }


    float draw_start_point = DrawPoint(ray_origin, ray_direction, control_points[0]);
    float draw_end_point = DrawPoint(ray_origin, ray_direction, control_points[max_count - 1]);
    float draw_final_point = DrawPoint(ray_origin, ray_direction, final_point);

    vec3 col = vec3(1.0);

    // draw start point
    if (draw_start_point < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        draw_start_point = (EDGE - draw_start_point + EDGE / 2.0) / EDGE;
        col *= vec3(1.0,0.0,0.0) * draw_start_point;
    }

    // draw end point
    if (draw_end_point < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        draw_end_point = (EDGE - draw_end_point + EDGE / 2.0) / EDGE;
        col *= vec3(1.0,0.0,0.0) * draw_end_point;
    }

    // draw bezier curve using de Casteljau's algorithm
    if (SHOW_BLACK_LINE) {
        vec3 prev_point = control_points[0];

        float end_algorithm = t;

        if(ALWAYS_SHOW_BEZIER_CURVE) { end_algorithm = 1.0; }

        float increaser = end_algorithm / float(CASTELJAUS_STEPS);

        end_algorithm += 0.001; // without it some small values of CASTELJAUS_STEPS couses ending curve before endpoint
                                // (i don't know why)

        for (float i = 0.0; i <= end_algorithm; i += increaser) {
            float i2 = 1.0 - i;

            vec3 point = vec3(0.0);

            for (int point_index = 0; point_index < max_count; ++point_index) {
                point += float(BinomialCoefficient(max_count - 1, point_index)) 
                         * pow(i2, float(max_count - 1 - point_index)) 
                         * pow(i, float(point_index))
                         * control_points[point_index];
            }

            float dist = RaySegmentDistance(prev_point, point, ray_origin, ray_screen_intersection);

            if (dist < EDGE_LINE + SMOOTH_LINE)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE_LINE,EDGE_LINE + SMOOTH_LINE, dist);
                col *= dist;
            }

            prev_point = point;
        }
    }

    // draw control points
    for (int i = 1; i < max_count - 1; ++i) {
        float dist = DrawPoint(ray_origin, ray_direction, control_points[i]);

        if (dist < EDGE + SMOOTH && show_support_points[i])
        {
            dist = (EDGE - dist + EDGE / 2.0) / EDGE;
            col *= vec3(0.0,0.0, float(max_count - i) / float(max_count) + 0.1) * dist;
        }
    }

    // draw lines between control points
    if (SHOW_GREY_LINES) {
        for (int i = 0; i < max_count - 1; ++i) {
            float dist = RaySegmentDistance(control_points[i], control_points[i + 1], ray_origin, ray_screen_intersection);

            if (dist < EDGE_LINE + SMOOTH_LINE)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE_LINE,EDGE_LINE + SMOOTH_LINE, dist);
                col *= mix(vec3(0.8),vec3(1.0,1.0,1.0),dist);
            }
        }
    }

    // draw support lines and points
    vec3 support_points[control_points.length()] = control_points;

    for (int i = 0; i < max_count - 1; ++i) {
        support_points[0] = support_points[0] + t * (support_points[1] - support_points[0]);

        for (int j = 1; j < max_count - i - 1; ++j) {
            support_points[j] = support_points[j] + t * (support_points[j+1] - support_points[j]);

            float dist = RaySegmentDistance(support_points[j-1], support_points[j], ray_origin, ray_screen_intersection);
            if (dist < EDGE_LINE + SMOOTH_LINE && show_support_lines[i])
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE_LINE,EDGE_LINE + SMOOTH_LINE, dist);
                col *= mix(support_lines_colors[i], vec3(1.0,1.0,1.0),dist);
            }

            dist = DrawPoint(ray_origin, ray_direction, support_points[j-1]);
            if (dist < EDGE + SMOOTH && show_support_points[i])
            {
                dist = (EDGE - dist + EDGE / 2.0) / EDGE;
                col *= support_points_colors[i] * dist;
            }
        }

        float dist = DrawPoint(ray_origin, ray_direction, support_points[max_count - i - 1]);
        if (dist < EDGE + SMOOTH && show_support_points[i])
        {
            dist = (EDGE - dist + EDGE / 2.0) / EDGE;
            col *= support_points_colors[i - 1] * dist;
        }
    }

    // draw final point
    if (draw_final_point < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        draw_final_point = (EDGE - draw_final_point + EDGE / 2.0) / EDGE;
        col *= final_dot_color * draw_final_point;
    }

    fragColor = vec4(col, 1.0);
}

