#define ITER_MAX 255
#define GAMMA 0.6

vec4 bcSquare( vec4 a ) {
    return vec4(a.x*a.x - a.y*a.y - a.z*a.z + a.w*a.w,
                2.0 * (a.x*a.y - a.z*a.t),
                2.0 * (a.x*a.z - a.y*a.t),
                2.0 * (a.x*a.t + a.y*a.z));
}

vec4 bcNorm( vec4 a ) {
    return vec4(a.x*a.x - a.y*a.y + a.z*a.z - a.w*a.w,
                2.0 * (a.x*a.y + a.z*a.t),
                0.0,
                0.0);
}

vec3 iterate(vec4 inVec) {
    vec4 v = inVec;
    float iterfrac = 0.0;
    for (int i=0; i<ITER_MAX; i++) {
        v = bcSquare(v) + inVec;
        iterfrac = float(i+1)/float(ITER_MAX);
        if (length(v) > 1000.0) break;
    }
    return vec3(bcNorm(v).xy, iterfrac);
}

vec3 colrot(vec3 c, float th) {
    vec3 v = c - vec3(0.5);
    v = mat3(cos(th), 0, -sin(th), 0, 1, 0, sin(th), 0, cos(th)) * v;
    return v+vec3(0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy/2.0) /iResolution.y;
    uv *= 2.0;

    vec3 escape = iterate(vec4(uv.x, uv.y, 0.8*sin(iTime/15.7), 0.8*sin(iTime/4.007)));

    float val = pow(escape.z, GAMMA);
    float shade = smoothstep(0.1, 10.0, length(escape.xy));
    float hue = abs(escape.y*10.0);
    vec3 col = val*colrot(vec3(shade*shade, shade, 1.0), hue);
    fragColor = vec4(col,1.0);
}

// License CC0: Simple 2D eye
// Nothing fancy, just a simple distance field for an eye

#define RESOLUTION iResolution
#define TIME       iTime

#define PI  3.141592654
#define TAU (2.0*PI)

vec4 alphaBlend(vec4 back, vec4 front) {
  float w = front.w + back.w*(1.0-front.w);
  vec3 xyz = (front.xyz*front.w + back.xyz*back.w*(1.0-front.w))/w;
  return w > 0.0 ? vec4(xyz, w) : vec4(0.0);
}

vec3 alphaBlend(vec3 back, vec4 front) {
  return mix(back, front.xyz, front.w);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, vec3(1.0/2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// https://iquilezles.org/articles/distfunctions2d
float vesica(vec2 p, float r, float d) {
  p = abs(p);
  float b = sqrt(r*r-d*d);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return p.x*vec2(cos(p.y), sin(p.y));
}

// http://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

// https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float pabs(float a, float k) {
  return -pmin(-a, a, k);
}

float df(vec2 p) {
  float a  = mix(0.0, 0.85, smoothstep(0.995, 1.0, cos(TAU*TIME/5.0)));
  const float b = 4.0;
  float rr = mix(1.6, b, a);
  float dd = mix(1.12, b, a);

  vec2 p0 = p;
  p0 = p0.yx;
  float d0 =  vesica(p0, rr, dd);
  float d5 = d0;

  vec2 p1 = p;
  p1.y -= 0.28;
  float d1 = circle(p1, 0.622);
  d1 = max(d1,d0);

  vec2 p2 = p;
  p2 -= vec2(-0.155, 0.35);
  float d2 = circle(p2, 0.065);

  vec2 p3 = p;
  p3.y -= 0.28;
  p3 = toPolar(p3);
  float n3 = mod1(p3.x, 0.05);
  float d3 = abs(p3.x)-0.0125*(1.0-length(p1));

  vec2 p4 = p;
  p4.y -= 0.28;
  float d4 = circle(p4, 0.285);

  d3 = max(d3,-d4);

  d1 = pmax(d1,-d2, 0.0125);
  d1 = max(d1,-d3);

  float t0 = abs(0.9*p.x);
  t0 *= t0;
  t0 *= t0;
  t0 *= t0;
  t0 = clamp(t0, 0.0, 1.0);
  d0 = abs(d0)-mix(0.0125, -0.0025, t0);


  float d = d0;
  d = pmin(d, d1, 0.0125);
  return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
