const float pi = radians(180.);

vec4 rainbow(vec2 U)
{
    float a = atan(U.y, U.x);
    vec3 c = cos(vec3(a) - vec3(0, 2, 4)) * .5 + .5;
    return vec4(c, 1);
}

bool aa, withcolor;
void blend(inout vec4 O, vec2 R, vec2 U)
{
    float d = step(length(U), .12*R.y);
    if (aa)
    {
        float f = 1.2;
        d = smoothstep(f, 0., length(U) - .12*R.y + f / 2.);
    }

    vec4 C = vec4(1) * d;
    if (withcolor)
        C = rainbow(U) * d;

    O = O * (1. - d) + C;
}

vec4 image(vec2 R, vec2 U)
{
    vec4 O = vec4(0);
    int N = 20;
    for (int i=0 ; i<N ; ++i)
    {
        float a = (float(i) + .5) / float(N);
        vec2 M = R/2. + R/4. * cos((a - vec2(0, .25)) * 2. * pi);
        blend(O, R, U - M);
    }
    int M = 20;
    for (int i=0 ; i<M ; ++i)
    {
        blend(O, R, U - vec2(.1, .2) * R);
        blend(O, R, U - vec2(.1, 1.-.2) * R);

        float y = (float(i) + .5) / float(N);
        y = mix(.15, .25, y);
        blend(O, R, U - vec2(.9, y) * R);
        blend(O, R, U - vec2(.9, 1.-y) * R);
    }
    return O;
}

void mainImage(out vec4 O, vec2 u)
{
    withcolor = u.y > iResolution.y/2.;
    //aa = u.x < iResolution.x/2.;
    aa = u.x / iResolution.x < .5 + .5 * sin(iTime * 1.);

    int zoom = 4;
    vec2 R = iResolution.xy / float(zoom);
    vec2 U = vec2(ivec2(u) / zoom) + .5;

    O = vec4(0);
    if (aa)
        O = image(R, U);
    else
    {
        const int N = 10;
        for (ivec2 x=ivec2(0) ; x.y<N ; ++x.y)
        for (x.x=0 ; x.x<N ; ++x.x)
            O += image(R, U - .5 + (vec2(x) + .5) / float(N));
        O /= float(N * N);
    }

    O.xyz = pow(O.xyz, vec3(.45));
}

