//tree traversal stuff

uint a;
uint find_firstNode(vec3 t0, vec3 tM){
    uint first = 0u;
    if(t0.x > t0.y && t0.x > t0.z){ // entry YZ
        if(tM.y < t0.x) first |= 2u;
        if(tM.z < t0.x) first |= 1u;
    }else if(t0.y > t0.z){ // entry XZ
        if(tM.x < t0.y) first |= 4u;
        if(tM.z < t0.y) first |= 1u;
    }else{  // entry XY
        if(tM.x < t0.z) first |= 4u;
        if(tM.y < t0.z) first |= 2u; 
    }
    return first;
}

uint next_Node(vec3 t1, uint yz, uint xz,uint xy){
    if(t1.x < t1.y && t1.x < t1.z){
        return yz;
    }else if(t1.y < t1.z){
        return xz;
    }else{
        return xy;
    }
