    return 0.0;
    }

    for(int i = 0; i < 130; i ++){
        if (actualDepth < 0)return -1.0;//visited all nodes

        uint currNode =  childIdx[actualDepth];

        if(currNode == 8u){
            actualDepth -= 1;
            t0 = t0s[actualDepth];
            t1 = t1s[actualDepth];
            tM = 0.5 * (t0 + t1);
            currNode = childIdx[actualDepth];
            vec3 dt = t1 - t0;
            vec3 idx = vec3(float(currNode&4u)/4.0, float(currNode&2u)/2.0, float(currNode&1u));
            vec3 t0_ = t0 + 0.5*dt*idx;
            vec3 t1_ = t0 + 0.5*dt*(1.0 + idx);
            vec3 tM_ = 0.5*(t0_ + t1_);

            uvec3 next = uvec3[](uvec3( 4 , 2 , 1 ),
                             uvec3( 5 , 3 ,END),
                             uvec3( 6 ,END, 3 ),
                             uvec3( 7 ,END,END),
                             uvec3(END, 6 , 5 ),
                             uvec3(END, 7 ,END),
                             uvec3(END,END, 7 ),
                             uvec3(END,END,END))[currNode];

            childIdx[actualDepth] = next_Node(t1_, next.x, next.y, next.z);
            //nodeIdx[actualDepth + 1] = tree[(nodeIdx[actualDepth]>>1u) + a ^ childIdx[actualDepth]];
            nodeIdx[actualDepth + 1] = treeTex((nodeIdx[actualDepth]>>8u) + a ^ childIdx[actualDepth]);
            continue;
        }

        if (actualDepth == 7){col = vec3(1.0, 0.0, 0.0); return -1.0;}


        //bool leaf = actualDepth == 6 || ((childIdx[actualDepth]^a) != 0u && (childIdx[actualDepth]^a) != 7u);
        bool leaf = ((nodeIdx[actualDepth + 1] & 1u) == 1u) || actualDepth==8;

        if (leaf){
            t0 = t0s[actualDepth];
            t1 = t1s[actualDepth];
            tM = 0.5 * (t0 + t1);
            currNode = childIdx[actualDepth];
            vec3 dt = t1 - t0;
            vec3 idx = vec3(float(currNode&4u)/4.0, float(currNode&2u)/2.0, float(currNode&1u));
            vec3 t0_ = t0 + 0.5*dt*idx;
            vec3 t1_ = t0 + 0.5*dt*(1.0 + idx);
            vec3 tM_ = 0.5*(t0_ + t1_);


            uint data = nodeIdx[actualDepth + 1] >> 1u;
            if ((data & 1u) == 1u){ //the node is full
                t0 = t0_;
                if (shadow) return max(max(t0.x, t0.y), t0.z);
                data >>= 1u;
                material = materials[data & 7u];

                if(t0.x > t0.y && t0.x > t0.z){ // entry YZ
                    normal = vec3(1.0, 0.0, 0.0);
                    return t0.x;
                }else if(t0.y > t0.z){ // entry XZ
                    normal = vec3(0.0, 1.0, 0.0);
                    return t0.y;
                }else{  // entry XY
                    normal = vec3(0.0, 0.0, 1.0);
                    return t0.z;
                }
            }//the node is empty
            #ifdef showTree   
            col -= 0.012*float(actualDepth); 
            #endif

            uvec3 next = uvec3[](uvec3( 4 , 2 , 1 ),
                             uvec3( 5 , 3 ,END),
                             uvec3( 6 ,END, 3 ),
                             uvec3( 7 ,END,END),
                             uvec3(END, 6 , 5 ),
                             uvec3(END, 7 ,END),
                             uvec3(END,END, 7 ),
                             uvec3(END,END,END))[currNode];

            childIdx[actualDepth] = next_Node(t1_, next.x, next.y, next.z);
            //nodeIdx[actualDepth + 1] = tree[(nodeIdx[actualDepth]>>1u) + a ^ childIdx[actualDepth]];
            nodeIdx[actualDepth + 1] = treeTex((nodeIdx[actualDepth]>>8u) + a ^ childIdx[actualDepth]);

        } else {
            t0 = t0s[actualDepth];
            t1 = t1s[actualDepth];
            actualDepth += 1;

            tM = 0.5 * (t0 + t1);

            vec3 dt = t1 - t0;
            vec3 idx = vec3(float(currNode&4u)/4.0, float(currNode&2u)/2.0, float(currNode&1u));
            vec3 t0_ = t0 + 0.5*dt*idx;
            vec3 t1_ = t0 + 0.5*dt*(1.0 + idx);
            vec3 tM_ = 0.5*(t0_ + t1_);
            childIdx[actualDepth] = find_firstNode(t0_, tM_);
            //nodeIdx[actualDepth + 1] = tree[(nodeIdx[actualDepth]>>1u) + a ^ childIdx[actualDepth]];
            nodeIdx[actualDepth + 1] = treeTex((nodeIdx[actualDepth]>>8u) + a ^ childIdx[actualDepth]);
            t0s[actualDepth] = t0_;
            t1s[actualDepth] = t1_;
        }

    }
    return -1.0;
}



float ray_step(vec3 ro, vec3 rd, bool shadow){
    a = 0u;
    vec3 size = (treeUp + treeDown)*0.5;
    ro.z = size.z - ro.z;
    rd.z = -(rd.z);

    if (rd.x < 0.0){
        ro.x = size.x - ro.x;
        rd.x = -rd.x;
        a |= 4u;
    }
    if (rd.y < 0.0){
        ro.y = size.y - ro.y;
        rd.y = -rd.y;
        a |= 2u;
    }
    if (rd.z < 0.0){
        ro.z = size.z - ro.z;
        rd.z = -(rd.z);
        a |= 1u;
    }

    vec3 invDir = 1.0/rd;
    vec3 t0 = (treeDown - ro) * invDir;
    vec3 t1 = (treeUp - ro) * invDir;

    float tmin = max(max(t0.x, t0.y), t0.z);
    float tmax = min(min(t1.x, t1.y), t1.z);
    if ((tmin < tmax) && (tmax > 0.0f)){
        return procTree(t0, t1, shadow);
    }else{ //doesn't intersect
        return -1.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 camPos = vec3(-3.0*cos(iTime), 0.5 + 0.4*sin(iTime*0.7), -3.0*sin(iTime))*0.8;
    vec3 target = vec3(0.0);
    const vec3 lightPos = vec3(4.0, 2.0, -2.5)*10.0;

    vec3 dir0 = normalize(target - camPos);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(up, dir0));
         up = cross(dir0, right);

    vec3 rd = normalize(right * uv.x + up * uv.y + dir0);

    float t = ray_step(camPos, rd, false);
    //back ground
    vec3 col2 = 0.5*vec3(135.0/255.0, 206.0/255.0, 235.0/255.0);
    col2 += 0.1*(1.0 - pow(abs(rd.y)+0.01, 0.2)); 
    vec3 camLight = normalize(lightPos - camPos);
    col2 += 0.05*(0.5 + 0.5*dot(rd, camLight));
    col2 += 0.05*pow(0.5 + 0.5*dot(rd, camLight), 10.0);

    if (t > 0.0){
        vec3 p = camPos + t*rd;
        vec3 voxCoord = floor((p+2.0)*16.0 - vec3(0.00001, 0.00001, 0.00001));
        // material texture variation
        material.Kd *= 0.95 + 0.1*rand(voxCoord);
        material.Kd.r *= 0.95 + 0.6*rand(abs(voxCoord - 56.0));
        if(material.id == 0)material.Kd.b *= 0.95 + 0.3*rand(abs(voxCoord - 36.0));
        if(material.id == 1)material.Kd *= 0.8 + 0.5*fract(sin(floor((voxCoord - 3.0)/6.0)*39.0)*45.0);
        //lighting
        col2 = material.Kd;
        normal *= -sign(rd);

        const vec3 lightPos = vec3(4.0, 2.0, -2.5)*10.0;
        vec3 lightDir = normalize(lightPos - p);
        //ambiant
        float AO = 0.9;
        AO *= 0.3 + 0.7*smoothstep(0.0, 0.7, max(max(abs(p.x*0.8), max(0.0, p.y)), abs(p.z)));
        col2 = material.Kd * AO * vec3(0.2, 0.8, 0.9);

        //shadow
        float shadow = ray_step(lightPos, - lightDir, true);
        if (shadow > 0.0){
            vec3 p2 = lightPos - lightDir * shadow;
            shadow = 1.0 - 0.95*step(0.00001, dot(p-p2, p-p2));
        }else{shadow = 1.0;};

        normal = mix(normal, 2.0*vec3(rand(abs(p)*60.0), rand(abs(p)*60.0 + 10.0), rand(abs(p)*60.0 + 20.0)) - 1.0, 0.02);
        //diffuse
        col2 += shadow * material.Kd * max(0.0, dot(normal, lightDir));
        //specular
        vec3 r = reflect(rd, normal);
        col2 += shadow * material.Ks * pow(max(0.0,dot(r, lightDir)), material.Ns);

    }
    uv = fragCoord/iResolution.xy;

    // color stuff
    //col2 = pow( col2, vec3(0.4545) );
    col2 = 1.0 - exp(-col2*3.2);
    col2 = clamp(col2, 0.0, 1.0);
    col2 = col2*col2*(3.0-2.0*col2);

    col2 *= vec3(1.2, 1.2, 1.0);

    col2 *= 0.6 + 0.4*pow(16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.4 );
    fragColor = vec4(col + col2,1.0);
}

// "Five Metallic Tetrahedra" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// (Based on "Ten Intersecting Tetrahedra")

#define AA  1

float PrSphDf (vec3 p, float r);
float Maxv2 (vec2 p);
mat3 VToRMat (vec3 v, float a);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir;
float tCur, dstFar;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

float TetFrameDf (vec3 p, float w)
{
  vec3 q;
  q = p;
  p = abs (p);
  q = mix (q, q.yzx, step (Maxv2 (p.yz), p.x));
  q = mix (q, q.zxy, step (Maxv2 (p.zx), p.y));
  q = mix (q, vec3 (q.x, - q.yz).yxz, step (q.z, 0.)) - vec3 (-1., 1., 1.) / sqrt (3.);
  return length (vec2 (0.5 * (q.x + q.y), q.z)) - w;
}

float ObjDf (vec3 p)
{
  mat3 m;
  vec3 q;
  float dMin, d, a;
  dMin = dstFar;
  p.y -= 0.86;
  q = p;
  d = PrSphDf (q, 1.1);
  if (d < 0.1) {
    a = atan (2. / (sqrt (5.) + 1.));
    m = VToRMat (vec3 (0., sin (a + vec2 (0.5 * pi, 0.))), 2. * pi / 5.);
    q.yz = Rot2D (q.yz, a);
    for (int k = 0; k < 5; k ++) {
      d = TetFrameDf (q, 0.05);
      dMin = min (dMin, d);
      q = m * q;
    }
    dMin = max (dMin, PrSphDf (p, 1.05));
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, dstGrnd, sh;
  bool isBg;
  const int nRefl = 1;
  isBg = false;
  dstGrnd = dstFar;
  for (int k = VAR_ZERO; k <= nRefl; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k < nRefl && dstObj < dstFar) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
    }
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
  } else {
    isBg = true;
    col = SkyBgCol (ro, rd);
  }
  if (! isBg) {
    sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    if (dstObj < dstFar) {
      col = vec3 (0.4, 0.5, 0.9) * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         0.2 * step (0.95, sh) * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
    } else {
      col = 0.5 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
         0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)));
      vn = VaryNf (16. * ro, vec3 (0., 1., 0.), 1. - smoothstep (0.5, 0.8, dstGrnd / dstFar));
      col *= (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.));
      col = mix (col, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
    }
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= pi * mPtr.y;
  }
  el = clamp (el, -0.45 * pi, -0.05 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -6.);
  ro.y += 0.8;
  zmFac = 5.;
  dstFar = 30.;
  sunDir = normalize (vec3 (0., 2., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.02 * pi * tCur);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec3 w, b1, b2, bp, bm;
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  w = (1. - cs.x) * v * v + cs.x;
  b1 = (1. - cs.x) * v.xzy * v.yxz;
  b2 = - cs.y * v.zyx;
  bp = b1 + b2;
  bm = b1 - b2;
  m[0][0] = w.x;  m[1][1] = w.y;  m[2][2] = w.z;
  m[0][1] = bp.x;  m[1][0] = bm.x;
  m[2][0] = bp.y;  m[0][2] = bm.y;
  m[1][2] = bp.z;  m[2][1] = bm.z;
  return m;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

//https://raytracing.github.io/books/RayTracingInOneWeekend.html

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec3 hash32(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 random_in_unit_sphere(vec2 p) {
    float test = 0.;
    while (test < 9999.) {
        vec3 pt = hash32(p * 10000. + test) * 2. - 1.;
        float len = length(pt);
        if (len * len >= 1.) {
            test += 1.;
            continue;
        }
        return pt;
    }
}

vec3 random_unit_vector(vec2 p) {
    return normalize(random_in_unit_sphere(p));
}

vec3 random_in_unit_disk(vec2 p) {
    float test = 0.;
    while (test < 9999.) {
        vec3 pt = vec3(hash22(p * 10000. + test) * 2. - 1., 0);
        float len = length(pt);
        if (len * len >= 1.) {
            test += 1.;
            continue;
        }
        return pt;
    }
}


const int material_lambertian = 0;
const int material_metal = 1;
const int material_dielectric = 2;

struct ray {
    vec3 origin;
    vec3 dir;
};

struct material {
    int type;
    vec3 albedo;
    float metal_fuzz;
    float dielectric_index_of_refraction;
};

struct hit_record {
    vec3 p;
    vec3 normal;
    float t;
    material material;
};

struct sphere {
    vec3 center;
    float radius;
    material material;
};

sphere[] spheres = sphere[](
    // ground
    sphere(vec3( 0.0, -1000., -1.0), 1000., material(material_lambertian, vec3(0.5), 0., 0.)),

    sphere(vec3(-4.0,    1.0, 2.),   1.0, material(material_dielectric, vec3(0), 0., 1.5)),
    sphere(vec3( 0.0,    1.0, 0.),   1.0, material(material_metal, vec3(0.7, 0.6, 0.5), 0., 0.)),
