#define res iResolution
#define thresh 20.
#define stop 200

vec3 rgb(float a){
    a *=8.;
    return(cos(4.+a+vec3(0.,2.,4.)*.7)*.5+.5);
}

vec2 cmul(vec2 a, vec2 b){
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 cdiv(vec2 v1, vec2 v2){
    float a = (v1.x*v2.x+v1.y*v2.y)/(v2.x*v2.x+v2.y*v2.y);
    float b = (v1.y*v2.x-v1.x*v2.y)/(v2.x*v2.x+v2.y*v2.y);
    return vec2(a,b);
}

vec2 cexp(vec2 z){
    return exp(z.x)*vec2(cos(z.y),sin(z.y));
}

vec2 clog(vec2 z){
    return vec2(log(length(z)),atan(z.y,z.x));
}

vec2 cpow(vec2 z, float c){
    return cexp(c*clog(z));
}


vec2 clerp(vec2 a, vec2 b, float m){
    m = clamp(m,0.,1.);
    return cmul(vec2(1.-m),a)+cmul(vec2(m),b);
}


vec2 rot(vec2 v, float t){
    float a = (1.*atan(v.x,v.y))+t;
    return vec2(cos(a),sin(a))*length(v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 z = .8*(2.*fragCoord.xy-res.xy)/res.y;
    // z = rot(z,time);
    //vec2 zz = vec2(0);
    z = clerp(.3*clog(vec2(2)+z), (cdiv(vec2(.5),z)), .1);
    // z = cexp(cmul(z,vec2(.4)));
    z=rot(z,iTime*.3);
    vec2 mouse = .5*iMouse.xy/res.xy;
    vec2 c = mouse.xy==vec2(0)? vec2(-.77,-.22)/*vec2(cos(iTime*.4)-.3,-.66)*/: (2.*mouse.xy-1.);
    int i = 0;
    for(i; i < stop; i++){
        z = cmul(z,z)+c;
        if(length(z) > thresh)
            break;
    }
    vec3 col = rgb(log(.9+float(i)/380.)*2.);
    fragColor = vec4(col, 1.0 );
}

// To get a better measurement in actual units, you'll need to
// manually set PIXEL_AREA to the area of your screens' pixels
// in millimetersÂ² or better yet, directly measure the shader window.
// You can change the mandelbrot set by dragging with the mouse.

// Likely not accurate
#define PIXEL_AREA 0.07000433851

// Maximum string length
#define MAX_LEN 7

// Hue to RGB function from Fabrice's shadertoyunofficial blog
#define hue2rgb(hue) (0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0)))

// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 pixel, in vec3 color, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel1, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        pixel = mix(pixel, color, val);
    }
}

void drawFloat(inout vec3 pixel, in vec3 color, in vec2 uv, in vec2 anchor, in vec2 size, in float spacing, in float num) {
    num = round(num * 100.0) * 0.01;
    for (float place=-2.0; place < 3.0; place++) {
        int digit = int(num * pow(10.0, place)) - 10 * int(num * pow(10.0, place - 1.0));
        drawChar(pixel, color, uv, anchor, size, ZERO + digit);
        anchor.x += spacing;
    }
}

void drawString(inout vec3 pixel, in vec3 color, in vec2 uv, in vec2 anchor, in vec2 size, in float spacing, in int[MAX_LEN] string, in int strLen) {
    for (int char=0; char < strLen; char++) {
        drawChar(pixel, color, uv, anchor, size, string[char]);
        anchor.x += spacing;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y; // Centered, aspect correct
    vec2 st = fragCoord / iResolution.xy; // Normalize to [0...1] range

    vec2 buf = texture(iChannel0, st).xy;
    fragColor = vec4(pow(max(0.0, 1.0 - length(st - 0.5)), 0.8));

    vec3 color = 0.5 * (hue2rgb(0.01 * buf.y) + cos(0.25 * buf.y + iTime));
    fragColor.rgb = mix(fragColor.rgb, color, buf.x); // Display mandelbrot color

    buf = texture(iChannel0, st, 100.0).xz;
    float relativeArea = buf.x / buf.y; // Decimal percentage of screen area

    vec2 anchor = vec2(-0.8, 0.35);

    // Text message "Pixels:"
    drawString(fragColor.rgb, vec3(0.0), uv, anchor, vec2(0.1), 0.08, int[MAX_LEN](P, i, x, e, l, s, COLON), 7);
    anchor.y = 0.25;

    // Relative area
    drawFloat(fragColor.rgb, vec3(0.0), uv, anchor, vec2(0.1), 0.08, relativeArea * 100.0);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.20, 0.0), vec2(0.1), PERIOD);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.40, 0.0), vec2(0.1), PERCENT);

    // Area (approximate)
    float screenArea = iResolution.x * iResolution.y * PIXEL_AREA;
    float mandelbrotArea = screenArea * relativeArea;
    mandelbrotArea *= 0.01; // Convert to centimeters to shorten the number (100 millimeterÂ² = 1 centimeterÂ²)

    anchor = vec2(-0.8, -0.25);

    // Text message "Area~"
    drawString(fragColor.rgb, vec3(0.0), uv, anchor, vec2(0.1), 0.08, int[MAX_LEN](A, r, e, a, TILDE, 0, 0), 5);
    anchor.y = -0.35;

    // Area (not relative)
    drawFloat(fragColor.rgb, vec3(0.0), uv, anchor, vec2(0.1), 0.08, mandelbrotArea);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.20, 0.0), vec2(0.1), PERIOD);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.40, 0.0), vec2(0.1), c);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.48, 0.0), vec2(0.1), m);
    drawChar(fragColor.rgb, vec3(0.0), uv, anchor + vec2(0.54, 0.0), vec2(0.1), SQUARE);
}

