// Complex power
vec2 cpow(in vec2 z, in float n) {
    float a = atan(z.y, z.x) * n;
    return vec2(cos(a), sin(a)) * pow(dot(z, z), 0.5 * n);
}

// Mandelbrot set iterator
vec2 mandelbrot(in vec2 pixel) {
    vec2 c = (pixel - 0.5 * iResolution.xy) / iResolution.y * 3.0;
    float n = iMouse.z > 0.0 ? iMouse.x / iResolution.x * 10.0 : mix(2.0, 8.0, 0.5 + 0.5 * sin(0.5 * iTime));
    vec2 z = vec2(0.0, 0.0);
    float a = 0.0;
    for (float i=0.0; i < 50.0; i++) {
        z = cpow(z, n) + c;
        if (i > 15.0) {
            return vec2(1.0, a);
        }

        if (length(z) > 2.0) break;
        a += (c.y - z.y) / (length(z - c) + 0.001); // cross(normalize(z - c), vec2(1, 0))
    }

    return vec2(0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0);
    float spp = 4.0; // Really sqrt(SPP)

    // Accumulate and average pixel samples to approximate local area
    for (float x=0.0; x < spp; x++) {
        for (float y=0.0; y < spp; y++) {
            fragColor.xy += mandelbrot(fragCoord + vec2(x, y) / spp);
        }
    }

    fragColor.xy /= spp * spp;
    fragColor.z = 1.0; // To count total pixels
}

