/**
Gaz smol raymarch + yolo


*/

#define R(p,a,t) (mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a))
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
vec3 pal(float t){ return vec3(.5,.5,.5)+vec3(.5,.5,.5)*cos(6.28*(1.*t+vec3(.0,.3,.7)));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 p,c=vec3(0.);
    vec3 d = normalize(vec3(fragCoord.xy -.5*iResolution.xy,iResolution.y));
    for(float i=0.,e,g=0.,t=iTime;i++<50.;){
        p=g*d;
        p = R(p,normalize(vec3(.0,1.1,1.)),t*.2);
        p.z +=+t;

         p= asin(sin(p));
         p=R(p,normalize(vec3(.0,1.5+sin(p.x),-cos(p.x*3.)+1.7)),-.5);
        p.zx -=1.;
        vec4 pp = vec4(p,1.);
        float gy = 0.;
        for(float j=0.;j<8.;j++){

            gy = mix(-gy,dot(sin(pp.xyz*3.),cos(pp.yzx*5.)),.75)/5.;
            pp.xyz = abs(pp.zxy)-.5;
            pp.xy*=rot(-.785*.5);
            pp.xz = pp.x < pp.z ? pp.zx:pp.xz;
            pp.xy*=rot(-.785);

            pp*=1.2;
        }
        g+=e=max(.00005+.001*fract(length(p)+iTime*.5),min(length(p)-.15,max(gy/pp.a,-(length(p)-3.))));
        c+=pal(p.z*.1+(i*i*e))*.02/exp(i*i*e);
    }
    fragColor = vec4(c,1.0);
}

// Spiky Gyroid Zoom by Leon Denise 2022/02/28
// Variation of Spiky Gyroid https://www.shadertoy.com/view/NsBfW1
// exploring gyroid shape as an organic pattern

// Inspired by Martijn Steinrucken "Math Zoo - Alien Orb"
// https://www.youtube.com/watch?v=b0AayhCO7s8
// https://www.shadertoy.com/view/tlcXWX

// Using code from Martijn Steinrucken, Dave Hoskins,
// Inigo Quilez, Antoine Zanuttini and many more

// Rotation matrix
mat2 rot (float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }
float map(vec3 p);

// Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z); }

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// Antoine Zanuttini
// https://www.shadertoy.com/view/3sBGzV
vec3 getNormal (vec3 pos) {
    vec2 noff = vec2(0.001,0);
    return normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));
}

// Inigo Quilez
// https://www.shadertoy.com/view/Xds3zN
float getAO( in vec3 pos, in vec3 nor, in float scale) {
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ) {
        float h = 0.01 + scale*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

// geometry
float map(vec3 p) {
    float scale = 3.;
    float dist = 100.;
    float a = 1.0;
    // fractalish gyroid accumulation
    for (float i = 0.; i < 3.; ++i) {
        vec3 s = p*scale/a;
        s.z += iTime/a+p.x;
        dist = smin(dist, abs(dot(sin(s),cos(s.yzx))/scale*a)-.01, .15);
        a /= 3.;
    }
    dist = smin(abs(length(p)-1.0)-.4, dist, -.55);
    return dist;
}


void mainImage( out vec4 color, in vec2 pixel )
{
    color = vec4(0,0,0,1);

    // coordinates
    vec2 p = (pixel.xy - iResolution.xy / 2.)/iResolution.y;
    vec3 pos = vec3(0);
    vec3 ray = normalize(vec3(p, .2));
    float rng = hash12(gl_FragCoord.xy);

    // raymarch
    float shade = 1.;
    for (shade; shade > 0.; shade -= 1./20.) {
        float d = map(pos);
        if (d < .001) break;
        d *= .9+.1*rng;
        pos += ray * d;
    }

    // color
    if (shade > 0.) {
	    vec3 normal = getNormal(pos);
	    vec3 tint = .75+.25*-normal;
	    tint += 2.*vec3(0.9,.7,.3)*getAO(pos, normal, 0.15);
	    color = vec4(tint*shade, 1.);
    }
}

