// 252 chars
void mainImage( out vec4 C, vec2 u )
{
    u-=C.rg=iResolution.xy/2.;
    u/=C.g/12.; 
    u.y-=5.; 
    C+=1.-C; 
    C.a=u.x=abs(u.x);      
    for( int t=0; ++t<5; u = u*mat2(.5,-.86,.84,.5) - vec2(6,3))
                    C.b=C.g = min(t>3 && (u*=2.4).y>-13.? 1.:C.g, 
                    min(3.-u.y+cos(u.x*2.5),2.5)>abs(u.x) || C.a>10.?0.:1.);
}

