//Chance of subdividing
#define SPLIT_CHANCE 0.01

//Chance of creating random cell
#define NEW_CELL_CHANCE 0.0000001

//show raw buffer values
#define SHOW_RAW false

float hash(vec2 p) {
    p *= 0.01;
    p = sin(p*vec2(138.512415, 535.2451124))*24.51323234234;
    return fract(sin((p.x*p.y)*23.51245)*234.5512+0.1);
}

