//resolution and frame tracker

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord.xy), 0);
    ivec2 fc = ivec2(fragCoord);
    if (fc == ivec2(0, 0))fragColor = vec4(fragColor.zw, iResolution.xy);
    if (fc == ivec2(1, 0)) {
        ivec4 r = ivec4(texelFetch(iChannel0, ivec2(0), 0));
        fragColor += 1.0;
        if (r.xy != r.zw) fragColor = vec4(0);
    }
}

buffer_b_code:
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    ivec2 fc = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0, fc, 0);
    ivec4 r = ivec4(texelFetch(iChannel2, ivec2(0), 0));
    if (r.xy != r.zw) { //resolution changes: 
    	fragColor = vec4(-1);
        return;
    }
    if (hash(fragCoord+0.0051+iTime) < NEW_CELL_CHANCE && iFrame%2 == 0) {
        fragColor = vec4(fragCoord, normalize(vec2(hash(fragCoord+0.002), hash(fragCoord+0.003))-0.5)*hash(fragCoord+0.152));
    } else {
        if (iFrame == 0) fragColor = vec4(-1);
    }
    if (fragColor.w >= 1.0) {
    	fragColor.w += 1.0; // add to life time
        fragColor.x = -hash(vec2(fragColor.z)); //component Z stays to velocity, so random value can be generated
    } else {
        for (int x = -1; x <= 1; x += 1) {
            for (int y = -1; y <= 1; y += 1) {
                //find pixel to move to
                vec4 p = texelFetch(iChannel0, fc+ivec2(x, y), 0);
                if (p.x > 0.0 && p.w < 1.0) {
                    vec2 np = p.xy+p.zw;
                    if ((fc == ivec2(round(np))||fc==ivec2(ceil(np)))&&(texelFetch(iChannel0, ivec2(np), 0).w<=5.0)) {
                        fragColor = vec4(np, p.zw);
                        return;
                    } else {
                        vec2 c = vec2(fragColor.zw);
                        if (c.y != -1.)c = vec2(c.x, 2);
                        fragColor = vec4(vec2(-1), c);
                        if (hash(fragCoord.xy+0.125+iTime) < SPLIT_CHANCE) {
                            //make perpindicular ray from point left or right
                            mat2 m = hash(fragCoord.xy+0.1255+iTime) < 0.5 ? mat2(0, 1, -1, 0) : mat2(0, -1, 1, 0);
                            fragColor = vec4(ivec2(fragCoord.xy), p.zw*m);
                        } return;
                    }
                }
            }
        }
    }
}

