	int c = int(p*5.);
    float f = fract(p*5.);
    return vec3(mix(r[c], r[(c+1)%5], f));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec4 r = ivec4(texelFetch(iChannel2, ivec2(0), 0));
    if (r.xy != r.zw) {
    	fragColor = vec4(1);
        return;
    }
    vec2 of = fragCoord;

    float t = texelFetch(iChannel2, ivec2(1, 0), 0).x/60.;

    //multipass box blur
    fragCoord += (vec2(hash(fragCoord+iTime), hash(fragCoord+0.1+iTime))-0.5)*float(pow(t*0.2, 2.0));

    fragColor = texelFetch(iChannel1, ivec2(of), 0);

    //
    float l = texelFetch(iChannel0, ivec2(fragCoord.xy), 0).x;
    float h = abs(l); //decode random ID
    vec3 c = vec3(1);
    if (h != 1.0)c = color(h); //map color to ray
    fragColor += vec4(c, 1.);
}

