int sqr(int n) {
    return n * n;
}

// thanks to @Envy24 for optimized version of this routine.
bool isPrime(int N) {
    switch (N)
    {
    case 0:
    case 1:
        return false;
    case 2:
    case 3:
    case 5:
    case 7:
        return true;
    default:
        break;
    }

    if ((N & 1) == 0) { return false; }

    int n = 3;
    bool r = true;
    int limit = int(sqrt(float(N)));

    do 
    { 
        r = (N % n) != 0;
        n += 2;
    } while (r &&
        n <= limit);

    return r;
}

int getNForPixel_Cartesian(in ivec2 IJ, int width) {    
    return IJ.x + (IJ.y * width);
}

int getNForPixel_Diagnolize(in ivec2 IJ) {
    int I = IJ.x;
    int J = IJ.y;

    int i_j = I + J;
    return (i_j * 1) * i_j / 2 + J + 1;    
}

int getNForPixel_Spiral(in ivec2 IJ) {
    int I = IJ.x;
    int J = IJ.y;

    int n = 0;

    // center
    if (I == 0 && J == 0) {
        n = 1;
    }
    // squares diagonal - bottom right
    else if (I > 0 && I == -J) {
        n = sqr((1 - 2 * J));
    }
    // right region
    else if (I > 0 && I >= abs(J)) {
        n = sqr(2 * I - 1) + I + J;
    }
    // top region
    else if (J > 0 && J >= abs(I)) {
        n = sqr(2 * J - 1) + 3 * J - I;
    }
    // left region
    else if (I < 0 && J >= I) {
        n = sqr(-2 * I) - I - J + 1;
    }
    // bottom region
    else {
        n = sqr( - 2 * J) + I - 3 * J + 3;
    }

    return n;
}

void mainImage(out vec4 RGBA, in vec2 XY) {
    int pixelsPerPixel = iMouse.z > 1.0 ? 1 : 3;
    ivec2 IJ_centered = ivec2(XY) / pixelsPerPixel - ivec2(iResolution.xy) / 2 / pixelsPerPixel;
    ivec2 IJ_cornered = ivec2(XY) / pixelsPerPixel;

    int n;

    if (int(iTime / 10.0) % 2 == 0) {
        n = getNForPixel_Spiral(IJ_centered);
    }
    else {
        n = getNForPixel_Diagnolize(IJ_cornered);
    }

    float c = isPrime(n) ? 0.0 : 1.0;

    c = mix(0.15 * c, c, smoothstep(5.0, -5.0, sqrt(float(n)) - 3.0 * float(iFrame)));

    float f = float(n * pixelsPerPixel) / (iResolution.x * iResolution.y);
    f = 0.8 + 0.2 * smoothstep(-0.1, 0.1, cos(f * 100.0 - iTime * 4.0));

    // indication the XY -> N mapping is right:
    if (int(iTime / 20.0) % 2 == 0) {
        f = c;
    }

    RGBA = vec4(vec2(c), f, 1.0);
}

