const float EPS = 1.0e-4;
const float FLOOR_INTERVAL = 2.0;
const Wall LEFT_WALL = Wall(vec3(-1, 0, 0), vec3(1, 0, 0));
const Wall RIGHT_WALL = Wall(vec3(1, 0, 0), vec3(-1, 0, 0));
const vec3 SHELF_UNIT = vec3(0.3, 0.3, 0.9);
const float SHELF_THICK = 0.05;
const vec3 SHELF_UNIT_WITH_THICK = SHELF_UNIT + SHELF_THICK;

const vec2 NUM_BOOK_RANGE = vec2(12.0, 36.0);
const vec2 BOOK_MIN_SIZE = vec2(0.105, 0.148); // A6
const vec2 BOOK_MAX_SIZE = vec2(0.210, 0.297); // A4
const vec3 BOOK_COLOR_RATIO = vec3(1.0, 0.8, 0.8);

// walk settings
