const float WALK_TIME = WALK_PERIOD * 5.0;
const float WALK_AMPT = 0.05;
const float WALK_HEIGHT_AMPT = 0.01;

// look around
const float LOOKAROUND_TIME = 10.0;

// gaze from far
const vec3 GAZE_POSITION = vec3(-5.0, 100.0, 100.0);
const float GAZE_TIME = (WALK_TIME + LOOKAROUND_TIME) * 2.0 - WALK_TIME - 0.12 * LOOKAROUND_TIME;

const float SCENE_PERIOD = (WALK_TIME + LOOKAROUND_TIME) * 2.0 + WALK_TIME + 0.12 * LOOKAROUND_TIME + GAZE_TIME;

/*** 3D toolkit ***/
const float PI = 3.141592653;

struct Camera {
    vec3 position;
    vec3 direction;
    vec3 up_direction; // not require dot(direction, up_direction) == 0
    float fov;
    float aspect; // x / y
};

struct Ray {
    vec3 origin;
    vec3 direction;
};

// perspective camera ray, uv = fragCoord / iResolution.xy
// cf: https://qiita.com/aa_debdeb/items/301dfc54788f1219b554
Ray cameraRay(in Camera camera, in vec2 uv) {
    uv = uv * 2.0 - 1.0;
    float h = tan(camera.fov * 0.5);
    float w = h * camera.aspect;
    vec3 right = normalize(cross(camera.direction, camera.up_direction));
    vec3 up = normalize(cross(right, camera.direction));
    vec3 direction = normalize(right * w * uv.x + up * h * uv.y + camera.direction);
    Ray ray;
    ray.origin = camera.position;
    ray.direction = direction;
    return ray;
}

vec2 dCosTheta(in vec2 uv, in float fov) {
    Camera cam = Camera(
        vec3(0, 0, 0),
        vec3(0, 0, 1),
        vec3(0, 1, 0),
        fov,
        iResolution.x / iResolution.y
    );
    vec3 e = vec3(1, 1, 0) / iResolution;
    Ray ray = cameraRay(cam, uv);
    Ray rayx = cameraRay(cam, uv + e.xz);
    Ray rayy = cameraRay(cam, uv + e.zy);
    return vec2(
        dot(ray.direction, rayx.direction),
        dot(ray.direction, rayy.direction)
    );
}

float microfacet(vec3 normal, vec3 cameraDir, vec3 lightDir, float metal, float roughness) {
    vec3 middle = normalize(cameraDir + lightDir);
    float dotCN = clamp(-dot(cameraDir, normal), 0.0, 1.0);
    float dotLN = clamp(-dot(lightDir, normal), 0.0, 1.0);
    float dotNM = clamp(-dot(normal, middle), 0.0, 1.0);
    float dotCM = clamp(dot(cameraDir, middle), 0.0, 1.0);
    float alpha = roughness * roughness;

    // diffuse BRDF
    float diffuse = 1.0 - metal;

    // microfacet distribution
    float alpha2 = alpha * alpha;
    float tmp = 1.0 - dotNM * dotNM * (1.0 - alpha2);
    float distribution = alpha2 / (tmp * tmp);

    // schlick approxy & geometric decay
    float alp = alpha * 0.5;
    float sal = dotLN / (dotLN * (1.0 - alp) + alp);
    float sac = dotCN / (dotCN * (1.0 - alp) + alp);
    float decay = sal * sac;

    // fresnel
    float c = 1.0 - dotCM;
    c = c * c * c * c * c;
    float fresnel = metal + (1.0 - metal) * c;

    // specular BRDF
    tmp = 4.0 * dotCN * dotLN;
    float specular = distribution * decay / tmp * fresnel;
    if (tmp < 0.0001) specular = 0.0;

    return (diffuse + specular) * dotLN;
}

/*** integer hash ***/
// https://www.shadertoy.com/view/XlXcW4
vec3 hash(uvec3 x) {
    const uint k = 1103515245U;
    x.y = ((x.y>>8U)^x.y)*k;
    x.z = ((x.z>>8U)^x.z)*k;
    x.z = ((x.z>>8U)^x.z)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    return vec3(x)/float(0xffffffffU);
}

vec3 noise31(float x) {
    uint s = uint(int(floor(x)) + 2147483647);
    float t = fract(x);
    return mix(hash(uvec3(s)), hash(uvec3(s + 1u)), t);
}


vec3 noise32(vec2 x) {
    uvec3 s = uvec3(ivec2(floor(x)) + 2147483647, 1);
    vec2 t = fract(x);
    uvec2 e = uvec2(0, 1);
    return mix(
        mix(hash(s + e.xxx), hash(s + e.yxx), t.x),
        mix(hash(s + e.xyx), hash(s + e.yyx), t.x),
        t.y
    );
}

vec2 noise22(vec2 x) {
    return noise32(x).xy;
}

vec3 noise33(vec3 x) {
    uvec3 s = uvec3(ivec3(floor(x)) + 2147483647);
    vec3 t = fract(x);
    uvec2 e = uvec2(0, 1);
    return mix(
        mix(
            mix(hash(s + e.xxx), hash(s + e.yxx), t.x),
            mix(hash(s + e.xyx), hash(s + e.yyx), t.x),
            t.y
        ),
        mix(
            mix(hash(s + e.xxy), hash(s + e.yxy), t.x),
            mix(hash(s + e.xyy), hash(s + e.yyy), t.x),
            t.y
        ),
        t.z
    );
}

float noise13(vec3 x) {
    return noise33(x).x;
}

#define three_mix(a0, a1, a2, t) (\
      a0 * (1.0 - t) * (1.0 - t) * 0.5\
    + a1 * ((t + 1.0) * (1.0 - t) + t * (2.0 - t)) * 0.5\
    + a2 * t * t * 0.5\
)

vec3 smoothnoise32(vec2 x) {
    const int k = 2147483647;
    uvec2 s = uvec2(ivec2(floor(x)) + k);
    vec2 t = fract(x);
    uvec3 e = uvec3(0, 1, 2);
    return three_mix(
        three_mix(
            hash(uvec3(s - e.zz, k)),
            hash(uvec3(s - e.yz, k)),
            hash(uvec3(s - e.xz, k)),
            t.x
        ),
        three_mix(
            hash(uvec3(s - e.zy, k)),
            hash(uvec3(s - e.yy, k)),
            hash(uvec3(s - e.xy, k)),
            t.x
        ),
        three_mix(
            hash(uvec3(s - e.zx, k)),
            hash(uvec3(s - e.yx, k)),
            hash(uvec3(s - e.xx, k)),
            t.x
        ),
        t.y
    );
}

vec3 fbm325(in vec2 uv) {
    float a = 0.5;
    vec3 res = vec3(0);
    for (int i = 0; i < 5; i++) {
        res += noise32((a *= 2.0) * uv);
    }
    return res;
}

vec3 smoothfbm325(in vec2 uv) {
    float a = 0.5;
    vec3 res = vec3(0);
    for (int i = 0; i < 5; i++) {
        res += smoothnoise32((a *= 2.0) * uv);
    }
    return res;
}

/*** main ***/
float dCosThetax;

vec2 bookSize(in vec3 p) {
    vec2 r = noise22(floor(p.yz / SHELF_UNIT_WITH_THICK.yz));
    return mix(BOOK_MIN_SIZE, BOOK_MAX_SIZE, r);
}

struct Hit {
    vec3 position;
    vec2 uv;
    vec3 normal;
};

const Hit UNHIT = Hit(vec3(0), vec2(0), vec3(0));

Hit hitBox(in Ray ray, vec3 boxSize) {
    vec3 m = 1.0 / ray.direction, n = m * ray.origin,
    k = abs(m) * boxSize, t1 = - n - k, t2 = - n + k;
    float tN = max(max(t1.x, t1.y), t1.z),
    tF = min(min(t2.x, t2.y), t2.z);
    vec3 p = ray.origin + tN * ray.direction,
    uv = (1.0 + p / boxSize) / 2.0,
    e = sign(p);
    if(tN > tF || tF < 0.0) return UNHIT;
    else if (t1.x == tN) return Hit(p, uv.yz, vec3(e.x, 0.0, 0));
    else if (t1.y == tN) return Hit(p, uv.zx, vec3(0, e.y, 0));
    else return Hit(p, uv.xy, vec3(0, 0, e.z));
}

Hit hitFloor(in Ray ray) {
    if (abs(ray.direction.y) < EPS) return UNHIT;
    float t = -(ray.origin.y + SHELF_THICK) / ray.direction.y;
    vec3 p = ray.origin + t * ray.direction;
    if (t > 0.0 && abs(p.x) < FLOOR_INTERVAL * 0.5)
        return Hit(p, p.zx, vec3(0, 1, 0));
    else return UNHIT;
}

Hit hitWall(in Ray ray, in Wall wall) {
    float dotRN = dot(ray.direction, wall.normal);
    if (abs(dotRN) < EPS) return UNHIT;
    float distRW = dot(wall.origin - ray.origin, wall.normal),
    t = distRW / dotRN;
    if (t < 0.0) return UNHIT;
    vec3 p = ray.origin + t * ray.direction;
    return Hit(p, vec2(0), wall.normal);
}

Hit hitUniformBook(in Ray ray, in Hit wall_hit) {
    vec3 p = wall_hit.position;
    vec2 bookSize = bookSize(p);
    vec3 booksCenter = vec3(
        sign(p.x) * (FLOOR_INTERVAL * 0.5 + SHELF_UNIT.x - bookSize.x / 2.0),
        floor(p.y / SHELF_UNIT_WITH_THICK.y) * SHELF_UNIT_WITH_THICK.y + bookSize.y / 2.0,
        floor(p.z / SHELF_UNIT_WITH_THICK.z) * SHELF_UNIT_WITH_THICK.z + SHELF_UNIT.z / 2.0
    );
    ray.origin -= booksCenter;
    Hit hit = hitBox(ray, vec3(bookSize, SHELF_UNIT.z) / 2.0);
    if (hit != UNHIT) hit.position += booksCenter;
    return hit;
}

Hit hitShelf(in Ray ray, in Hit wall_hit) {
    vec3 d = ray.direction, p = wall_hit.position, e = sign(d),
    u = mod(p, SHELF_UNIT_WITH_THICK),
    t = vec3(
        SHELF_UNIT.x / abs(d.x),
        abs(SHELF_UNIT.y * 0.5 / d.y) - (u.y - SHELF_UNIT.y * 0.5) / d.y,
        abs(SHELF_UNIT.z * 0.5 / d.z) - (u.z - SHELF_UNIT.z * 0.5) / d.z
    );
    if (u.z > SHELF_UNIT.z || u.y > SHELF_UNIT.y)
        return Hit(p, u.zy / SHELF_UNIT_WITH_THICK.yz, vec3(-e.x, 0, 0));
    float tMin = min(min(t.x, t.y), t.z);
    p += tMin * d;
    u = fract(vec3(abs(p.x) - FLOOR_INTERVAL * 0.5, p.yz) / SHELF_UNIT_WITH_THICK);
    if (t.x == tMin) return Hit(p, u.zy, vec3(-e.x, 0, 0));
    else if (t.y == tMin) return Hit(p, u.zx, vec3(0, -e.y, 0));
    else return Hit(p + t.z * ray.direction, u.xy, vec3(0, 0, -e.z));
}

Hit hitScene(in Ray ray) {
    Hit hit = hitFloor(ray);
    if (hit != UNHIT) return hit;
    hit = hitWall(ray, RIGHT_WALL);
    if (hit != UNHIT) {
        Hit hBook = hitUniformBook(ray, hit);
        Hit hShelf = hitShelf(ray, hit);
        float distBook = distance(ray.origin, hBook.position);
        float distShelf = distance(ray.origin, hShelf.position);
        if (hBook != UNHIT && distBook < distShelf) return hBook;
        else return hShelf;
    }
    hit = hitWall(ray, LEFT_WALL);
    if (hit != UNHIT) {
        Hit hBook = hitUniformBook(ray, hit);
        Hit hShelf = hitShelf(ray, hit);
        float distBook = distance(ray.origin, hBook.position);
        float distShelf = distance(ray.origin, hShelf.position);
        if (hBook != UNHIT && distBook < distShelf) return hBook;
        else return hShelf;
    }
    return UNHIT;
}

float shadow(in vec3 p, in vec3 lightPos) {
    Ray ray = Ray(lightPos, normalize(p - lightPos));
    Hit hit = hitScene(ray);
    return distance(hit.position, p) < EPS ? 1.0 : 0.0;
}

float cloth(vec3 normal, vec3 cameraDir, vec3 lightDir, float metal, float roughness) {
    vec3 middle = normalize(cameraDir + lightDir);
    float dotCN = clamp(-dot(cameraDir, normal), 0.0, 1.0);
    float dotLN = clamp(-dot(lightDir, normal), 0.0, 1.0);
    float dotNM = clamp(-dot(normal, middle), 0.0, 1.0);
    float dotCM = clamp(dot(cameraDir, middle), 0.0, 1.0);
    float alpha = roughness * roughness;

    // diffuse BRDF
    float diffuse = 1.0 - metal;

    // distribution by Imageworks
    float distribution = dotNM * (1.0 + 0.5 / alpha) * pow(1.0 - dotNM * dotNM, 0.5 / alpha);

    // schlick approxy & geometric decay
    float alp = alpha * 0.5;
    float sal = dotLN / (dotLN * (1.0 - alp) + alp);
    float sac = dotCN / (dotCN * (1.0 - alp) + alp);
    float decay = sal * sac;

    // fresnel
    float c = 1.0 - dotCM;
    c = c * c * c * c * c;
    float fresnel = metal + (1.0 - metal) * c;

    // specular BRDF
    float tmp = 4.0 * dotCN * dotLN;
    float specular = distribution * decay / tmp * fresnel;
    if (tmp < 0.0001) specular = 0.0;

    return (diffuse + specular) * dotLN;
}

vec3 colorFloor(in Ray ray, in Hit hit, in vec3 lightPos) {
    vec3 p = hit.position, dir = p - lightPos;
    if (abs(p.x) < 0.7) {
        float k = abs(abs(p.x) - 0.6);
        k = smoothstep(0.02, 0.01, k);
        float c = cloth(
            normalize(vec3(0, 1, 0) + 0.25 * (2.0 * fbm325(p.zx * 10.0) / 5.0 - 1.0)),
            ray.direction,
            normalize(dir),
            0.9,
            0.5
        ) / dot(dir, dir);
        return clamp(c, 0.0, 1.0) * vec3(252.0 / 255.0, k, 0);
    } else {
        float c = microfacet(
            vec3(0, 1, 0),
            ray.direction,
            normalize(dir),
            0.1,
            0.7
        ) / dot(dir, dir);
        return clamp(c, 0.0, 1.0) * texture(iChannel0, fract(p.zx)).xyz;
    }
}

vec3 colorUniformBook(in Ray ray, in Hit hit, in vec3 lightPos) {
    vec3 p = hit.position, dir = p - lightPos, normal = hit.normal, col = vec3(1);
    vec2 gen = floor(p.yz / SHELF_UNIT_WITH_THICK.yz);
    float s = sign(hit.position.x), r = noise13(vec3(gen, 12.0 * s)),
    nBooks = floor(mix(NUM_BOOK_RANGE.x, NUM_BOOK_RANGE.y, r));
    if (normal.x != 0.0) {
        r = fract(hit.uv.y * nBooks);
        float sinAlpha = -dot(ray.direction, normal);
        float du = distance(p, ray.origin) * sqrt(1.0 - dCosThetax * dCosThetax) / sinAlpha;
        float a = smoothstep(SHELF_UNIT.z / nBooks, 0.0, 2.0 * du);
        normal = normalize(normal + vec3(0, 0, a * sin(2.0 * (r - 0.5))));
        col = noise33(vec3(gen, 16.0 * s)) * BOOK_COLOR_RATIO
            * (0.6 + 0.4 * texture(iChannel1, hit.uv).xyz);
    } else {
        r = fract(hit.uv.x * nBooks);
        float sinAlpha = -dot(ray.direction, normal);
        float du = distance(p, ray.origin) * sqrt(1.0 - dCosThetax * dCosThetax) / sinAlpha;
        float a = smoothstep(SHELF_UNIT.z / nBooks, 0.0, 2.0 * du);
        if (fract(r + 0.005 * nBooks) < 0.01 * nBooks || hit.uv.y > 0.95)
            col = 1.0 - a * (1.0 - noise33(vec3(gen, 16.0 * s)) * BOOK_COLOR_RATIO);
        normal = normalize(normal - vec3(0, 0, a * 0.5 * sin(r - 0.5)));
    }
    float t = distance(ray.origin, hit.position),
    c = 0.5 * microfacet(
        normal,
        ray.direction,
        normalize(dir),
        0.05,
        0.7
    ) / dot(dir, dir) * shadow(p, lightPos);
    c += 0.5 * microfacet(
        normal,
        ray.direction,
        ray.direction,
        0.05,
        0.7
    ) / (1.0 + t * t);
    return clamp(c, 0.0, 1.0) * col;
}

vec3 colorShelf(in Ray ray, in Hit hit, in vec3 lightPos) {
    vec3 dir = hit.position - lightPos;
    float t = distance(ray.origin, hit.position),
    c = 0.5 * microfacet(
        hit.normal,
        ray.direction,
        normalize(dir),
        0.1,
        0.7
    ) / dot(dir, dir) * shadow(hit.position, lightPos);
    c += 0.5 * microfacet(
        hit.normal,
        ray.direction,
        ray.direction,
        0.1,
        0.7
    ) / (1.0 + t * t);
    return clamp(c, 0.0, 1.0) * pow(texture(iChannel0, hit.uv).xyz, vec3(2.2));
}

float directLight(in vec3 lightPos, in Ray ray) {
    vec3 ol = lightPos - ray.origin;
    float t = dot(ol, ray.direction);
    if (t < 0.0) return 0.0;
    vec3 h = ol - t * ray.direction;
    return 5.0e-4 / dot(h, h);
}

vec3 sceneColor(in Ray ray, in vec3 lightPos) {
    Hit hit = hitFloor(ray);
    if (hit != UNHIT) return colorFloor(ray, hit, lightPos);
    hit = hitWall(ray, RIGHT_WALL);
    if (hit != UNHIT) {
        Hit hBook = hitUniformBook(ray, hit);
        Hit hShelf = hitShelf(ray, hit);
        float distBook = distance(ray.origin, hBook.position);
        float distShelf = distance(ray.origin, hShelf.position);
        if (hBook != UNHIT && distBook < distShelf)
            return colorUniformBook(ray, hBook, lightPos);
        else return colorShelf(ray, hShelf, lightPos);
    }
    hit = hitWall(ray, LEFT_WALL);
    if (hit != UNHIT) {
        Hit hBook = hitUniformBook(ray, hit);
        Hit hShelf = hitShelf(ray, hit);
        float distBook = distance(ray.origin, hBook.position);
        float distShelf = distance(ray.origin, hShelf.position);
        if (hBook != UNHIT && distBook < distShelf)
            return colorUniformBook(ray, hBook, lightPos);
        else return colorShelf(ray, hShelf, lightPos);
    }
    return vec3(0);
} 

vec3 cameraPos(in float t) {
    if (mod(t, SCENE_PERIOD) < SCENE_PERIOD - GAZE_TIME) {
        float bt = floor(t / (WALK_TIME + LOOKAROUND_TIME)) * WALK_TIME,
        dt = mod(t, WALK_TIME + LOOKAROUND_TIME);
        t = bt + min(dt, WALK_TIME);
        float it = floor(t / WALK_PERIOD), ft = fract(t / WALK_PERIOD);
        return vec3(
            WALK_AMPT * cos(PI * t / WALK_PERIOD),
            WALK_BASE_HEIGHT + WALK_HEIGHT_AMPT * abs(sin(PI * t / WALK_PERIOD)),
            -WALK_STRIDE * (it + smoothstep(0.0, 1.0, ft))
        );
    } else {
        return GAZE_POSITION;
    }
}

vec3 cameraDir(in float t) {
    if (mod(t, SCENE_PERIOD) < SCENE_PERIOD - GAZE_TIME) {
        float dt = mod(t, WALK_TIME + LOOKAROUND_TIME);
        vec3 dir = vec3(0, 0, -1);
        if (dt > WALK_TIME) {
            int signum = int(t / (WALK_TIME + LOOKAROUND_TIME)) % 2;
            dt = (dt - WALK_TIME) / LOOKAROUND_TIME;
            dt = smoothstep(0.0, 0.125, dt) * 0.125
                    + smoothstep(0.125, 0.375, dt) * 0.25 
                    + smoothstep(0.375, 0.625, dt) * 0.25
                    + smoothstep(0.625, 0.875, dt) * 0.25
                    + smoothstep(0.875, 1.0, dt) * 0.125;
            dt += (signum == 1 ? 0.25 : 0.75);
            vec2 disp = vec2(cos(2.0 * PI * dt), 1.5 * sin(-4.0 * PI * dt));
            dir += 0.25 * vec3(disp, 0);
        }
        return normalize(dir + 0.02 * fbm325(vec2(t * 0.05, 1.0)) / 5.0 - 0.01);
    } else {
        return vec3(1, 0, 0);
    }
}

void mainImage0(out vec4 fragColor, in vec2 fragCoord) {
    float fov = PI / 3.0;
    vec2 uv = fragCoord / iResolution.xy;
    Camera camera = Camera(
        cameraPos(iTime),
        cameraDir(iTime),
        vec3(0, 1, 0),
        fov,
        iResolution.x / iResolution.y
    );
    Ray ray = cameraRay(camera, uv);
    dCosThetax = dCosTheta(uv, fov).x;

    vec3 lightPos;
    if (mod(iTime, SCENE_PERIOD) < SCENE_PERIOD - GAZE_TIME) {
        lightPos = vec3(
            0.5 * cos(iTime),
            WALK_BASE_HEIGHT + 0.5 * sin(0.333 * iTime),
            ray.origin.z - 1.5 + 0.5 * sin(0.5 * iTime)
        );
    } else {
        lightPos = GAZE_POSITION + vec3(
            3.0,
            1.5 * sin(iTime),
            1.5 * cos(iTime * 0.5)
        );
    }


    vec3 col = sceneColor(ray, lightPos);
    if (mod(iTime, SCENE_PERIOD) > SCENE_PERIOD - GAZE_TIME) {
        col *= 37.5;
    }
    col += directLight(lightPos, ray);

    float ft = mod(iTime, SCENE_PERIOD);
    col *= smoothstep(0.0, 0.5, ft)
            * smoothstep(0.0, 0.5, abs(ft + GAZE_TIME - SCENE_PERIOD))
            * smoothstep(SCENE_PERIOD, SCENE_PERIOD - 0.5, ft);

    fragColor = vec4(clamp(col, 0.0, 1.0), 1);
}

#define AA 3
void mainImage(out vec4 O, in vec2 U) {
    mainImage0(O, U);

    // smart anti-aliasing
    // reference: https://shadertoyunofficial.wordpress.com/2021/03/09/advanced-tricks/

    // If defined, visualize anti-aliased pixels.
    //#define VISUALIZE_AA
    #ifdef AA
    if (fwidth(length(O.xyz)) > 0.1) {
        vec4 o;
    #if (AA == 2)
        for (int k = 0; k < 4; k++) {
              mainImage0(o,U + (vec2(k % 2, k / 2) - 0.5) / 1.5);
              O += o;
        }
        O /= 5.0;
    #elif (AA == 3)
        for (int k = 0; k < 9; k += k == 3 ? 2 : 1 ) {
              mainImage0(o, U + vec2(k % 3 - 1, k / 3 - 1) / 3.0);
              O += o;
        }
        O /= 9.0;
    #endif
    #ifdef VISUALIZE_AA
        O = vec4(1);
    } else {
        O = vec4(0,0,0,1);
    #endif
    }
    #endif
    O.xyz = pow(O.xyz, vec3(.4545));
    O.w = 1.0;
}

#define MAX_STEPS 80
#define MAX_DIST 50.
#define SURF_DIST 0.0001

#define pi 3.14159

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

//https://www.shadertoy.com/view/Wl3fD2

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

vec3 getRo() {
    float t = 0.0 * iTime, o = 2. * pi / 3.;
    return 3. * vec3(cos(t - o), cos(t), cos(t + o));
}

vec3 distort(vec3 p) {
    float o = 2.* pi / 3.;
    float t = 0. * length(p) - 0.25 * iTime;
   // p = abs(p) - 0.5;
    p.xy *= Rot(t - o);
    p.yz *= Rot(t);
    p.zx *= Rot(t + o);
    return p;//fract(0.8 * p) - 0.5;
}

float GetDist(vec3 p) {

    vec3 ro = getRo();
    float cd = length(p - ro) - 0.;

    p = distort(p);

    float r1 = 1.2;
    float r2 = 0.3;
    float d0 = length(p.xz) - r1;
    float d1 = length(vec2(d0, p.y)) - r2;
    //d1 += 0. + 0.2 * cos(d1 * 2. + iTime);
    p *= 1.8;
    //p /= cos(length(p) - iTime);

    p *= log(d1 - 0.);

    vec3 center = floor(p) + 0.5;
    vec3 neighbour = center + face(p - center);

    vec3 pos = p - center;
    vec3 npos = p - neighbour;

    float h = hash(hash(neighbour.x, neighbour.y), neighbour.z);

    float o = 2. * pi / 3.;
    float t = 2. * pi * h + 0. * iTime;
    vec3 ax = vec3(cos(t - o), cos(t), cos(t + o));
    ax = normalize(ax);

   // npos = erot(npos, ax, 0.5 * pi * h);


    //npos.xy *= Rot(pi * h);
    float e = 0.1;
    float worst = sdBox(npos, vec3(0.75)) - 0.;

    float sq = 0.25 * sqrt(3.);
   // worst = length(npos) - 0.5;//sq;
    worst = sdBox(npos, vec3(0.4)) - 0.3798;
   // worst = min(min(length(npos.xz), length(npos.zy)), length(npos.yx)) - 0.2;

    float me = sdBox(pos, vec3(0.2)) - 0.;


    // lower k => more "fog"
    float k = 0.4;
    float d = worst;//min(me, worst);
    d = -min(-d, cd);

    //return length(p) -0.3 + SURF_DIST;
    return k * d + 0.14;//500. * SURF_DIST;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 0.95);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);

        vec3 dp = distort(p);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
       // col = vec3(step(0., dif));

        // darken with distance from origin
        float v = exp(-0.3 * pow(dot(p,p), 0.25));

        // idk what this does
        v = smoothstep(0., 1., v);
        v = clamp(1.2 * v * v, 0., 1.);

        // color + lighten
        vec3 e = vec3(1);
        col = v * pal(0.32 + 1. * v, 0.8 * e, 0.5 * e, 0.5 * e, 0.8 * vec3(0,1,2)/3.);    
        //col = vec3(v);
        //col -= 0.1;
    }

    fragColor = vec4(col,1.0);
}

