void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	//Colours
	vec4 bg_c=vec4(.200,.200,.239,1.);
	vec3 bg_bot_c=vec3(.129,.129,.153);
	vec3 bg_top_c=vec3(.153,.153,.176);
	vec3 light_c=vec3(.337,.639,.878);
	vec3 light_glw_c=vec3(.506,.753,.949);
	vec4 cap_grad_top_c=vec4(.333,.333,.353,1.);
	vec4 cap_grad_bot_c=vec4(.227,.227,.259,1.);
	vec3 cap_line_c=vec3(.855,.859,.871);
	vec4 black=vec4(0.,0.,0.,1.);

    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y * 1.25;

	float aa=1./((iResolution.x+iResolution.y)/2.) * 1.25;

	float range=.75;
	float val=(iMouse.x/iResolution.x);
	float angle_val=(1.-range)*PI+val*2.*PI*range;

	float a_range=fract((atan(uv.x,uv.y)+PI*range)/(2.*PI));
	float bg_shape=smoothstep(a_range,a_range,range)*smoothstep(sdCircle(uv,.5)-aa,sdCircle(uv,.5)+aa,0.);
	vec4 bg_bot=vec4(bg_bot_c,clamp(bg_shape,0.,1.));

	float a_range2=fract((atan(uv.x,uv.y)+PI*range*.98)/(2.*PI))*1.02;
	float bg_top_shape=smoothstep(a_range2,a_range2,range)*smoothstep(sdCircle(uv,.48)-aa,sdCircle(uv,.48)+aa,0.)*smoothstep(-sdCircle(uv,.4)-aa,-sdCircle(uv,.4)+aa,0.);
	vec4 bg_top=vec4(bg_top_c,clamp(bg_top_shape,0.,1.));

	vec4 bg_shadow=vec4(vec3(0.),smoothstep(sdCircle(uv,.45),sdCircle(uv,.5),0.))*bg_shape*.2;
	vec4 cap_bot_shadow=vec4(vec3(0.),smoothstep(sdCircle(uv+vec2(0.,.05),.45),sdCircle(uv+vec2(0.,.05),.2),0.));

	float bg_light_shape=bg_top_shape*smoothstep(a_range,a_range,range*val);
	vec4 bg_light=vec4(light_c,clamp(bg_light_shape,0.,1.));

	float bg_light_glw_shape=smoothstep(a_range2,a_range2,range)*smoothstep(sdCircle(uv,.48),sdCircle(uv,.44),0.)*smoothstep(-sdCircle(uv,.4),-sdCircle(uv,.44),0.)*smoothstep(a_range,a_range,range*val);
	vec4 bg_light_glw=vec4(light_glw_c,1.)*clamp(bg_light_glw_shape,0.,1.)*(val/2.+.25);

	float a=atan(uv.x,uv.y)-angle_val;
	float cap_shape_bot=smoothstep(sdCircle(uv,.34)-aa,sdCircle(uv,.34)+aa,0.);
	vec4 cap_grad=vertical_gradient(uv,cap_grad_bot_c,cap_grad_top_c);
	vec4 cap_bot=vec4(cap_grad.rgb,cap_shape_bot);

	float cap_shape_bot_glw=smoothstep(-sdCircle(uv,.33)-aa,-sdCircle(uv,.34)+aa,0.)*cap_shape_bot;
	vec4 cap_bot_glw=vec4(cap_grad.rgb*1.5,clamp(cap_shape_bot_glw*uv.y,0.,1.));

	vec4 cap_shadow1=mix(vec4(0.,0.,0.,(sin(-angle_val)+1.)/2.*.75),vec4(0.),clamp((clamp(uv*rotate2d(angle_val),0.,1.).x)*1.5,0.,1.))*step((-uv*rotate2d(angle_val)).x,0.)*cap_shape_bot;
	vec4 cap_shadow2=mix(vec4(0.,0.,0.,(sin(angle_val)+1.)/2.*.75),vec4(0.),clamp((clamp(-uv*rotate2d(angle_val),0.,1.).x)*1.5,0.,1.))*step((uv*rotate2d(angle_val)).x,0.)*cap_shape_bot;
	vec4 cap_shadow3=mix(vec4(0.,0.,0.,1.)*.1,vec4(0.),clamp((clamp(abs(uv*rotate2d(angle_val)),0.,1.).x)*4.,0.,1.))*cap_shape_bot;

	float cap_shape_top=smoothstep(sdBox(uv*rotate2d(angle_val),vec2(.12,.5))-aa,sdBox(uv*rotate2d(angle_val),vec2(.12,.5))+aa,0.)*cap_shape_bot;
	vec4 cap_top=vec4(cap_grad.rgb,cap_shape_top);

	float cap_shape_top_glw=smoothstep(-sdBox(uv*rotate2d(angle_val),vec2(.11,.5))-aa,-sdBox(uv*rotate2d(angle_val),vec2(.12,.5))+aa,0.)*cap_shape_top+smoothstep(-sdCircle(uv,.33)-aa,-sdCircle(uv,.34)+aa,0.)*cap_shape_bot*cap_shape_top;
	vec4 cap_top_glw=vec4(cap_grad.rgb*1.5,clamp(cap_shape_top_glw*uv.y,0.,1.));

	float line_shape=smoothstep(sdBox(uv*rotate2d(angle_val)-vec2(0.,.25),vec2(.02,.2))-aa,sdBox(uv*rotate2d(angle_val)-vec2(0.,.25),vec2(.02,.2))+aa,0.)*cap_shape_bot;

	vec4 colour=bg_c;
	colour=overlay(bg_bot,colour);
	colour=overlay(bg_top,colour);
	colour=overlay(bg_shadow,colour);
	colour=overlay(cap_bot_shadow,colour);
	colour=overlay(bg_light,colour);
	colour+=bg_light_glw;
	colour=overlay(cap_bot,colour);
	colour=overlay(cap_bot_glw,colour);
	colour=overlay(cap_shadow1,colour);
	colour=overlay(cap_shadow2,colour);
	colour=overlay(cap_shadow3,colour);
	colour=overlay(cap_top,colour);
	colour=overlay(cap_top_glw,colour);
	colour=overlay(vec4(line_shape),colour);

	fragColor=colour;
}

//Random////////////////////////////////////////////////////////////////////////////
float noise(vec2 x){
    vec2 f = fract(x);
    vec2 u = f*f*(3.0 - 2.0*f);

    vec2 p = floor(x);
	float a = texture(iChannel0, (p+vec2(0.0, 0.0))/256.0).x;
	float b = texture(iChannel0, (p+vec2(1.0,0.0))/256.0).x;
	float c = texture(iChannel0, (p+vec2(0.0,1.0))/256.0).x;
	float d = texture(iChannel0, (p+vec2(1.0,1.0))/256.0).x;


	return a+(b-a)*u.x+(c-a)*u.y+(a-b-c+d)*u.x*u.y;
}

vec2 seed = vec2(0.0);

float rand(){
    return (seed.x = fract(6.0*sin(344.0*seed.x + 0.4)));
}
float fbm(vec2 x, int detail){
    float a = 0.0;
    float b = 1.0;
    float t = 0.0;
    x += seed;
    for(int i = 0; i < detail; i++){
        float n = noise(x);
        a += b*n;
        t += b;
        b *= 0.7;
        x *= 2.0; 

    }
    return a/t;
}
//aux///////////////////////////////////////////////////////////////////////////////////

vec3 colorRamp(vec3 col1, vec3 col2, vec3 col3, float t){
    if (t < 0.5){
        t*=2.0;
        t = t*t*(3.0 - 2.0*t);
        return mix(col1, col2, t);
    }else{
        t = (t-0.5)*2.0;
        t = t*t*(3.0 - 2.0*t);
        return mix(col2, col3, t);
    }
}

//brush////////////////////////////////////////////////////////////////////////////////


vec4 brush(vec2 uv){ //color && alpha
    uv.y += 0.2*(fbm(vec2(uv.x*2.0, 0.5), 2) - 0.5);
    //strength
    float b = 1.0;
    b *= pow(fbm(uv*vec2(1.0, 3.0), 8), 2.0);
    b *= smoothstep(0.9, 0.5, 2.0*max(abs(uv.x - 0.5), abs(uv.y - 0.5)));


    if (b <= 0.15) return vec4(0.0);
    //color
    float t = 0.3 + 0.4*fbm(uv*vec2(10.0, 60.0) + 10.0, 3);
    t += 0.3*fbm(uv*2.0+20.0, 2)-0.15;
    t += 0.2*noise(seed)-0.1;
    t = 0.8*t+0.1;

    t *= b*0.5+0.5; 
    b = smoothstep(0.15, 0.2, b);

    return vec4(colorRamp(vec3(0.365,0.153,0.337), 
                     vec3(1.000,0.690,0.259),
                     vec3(0.753,0.165,0.165), t), b);
}

