#define MAX_STEPS 100
#define MAX_DIST 50.
#define SURF_DIST 0.0001

#define pi 3.14159

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float mlength(vec2 p) {
    return max(abs(p.x), abs(p.y));
}

float mlength(vec3 p) {
    return max(abs(p.x), max(abs(p.y), abs(p.z)));
}

vec3 distort(vec3 p) {
    float o = 2.* pi / 3.;
    float t = 1.5 * length(p) - 0.2 * iTime;
   // p = abs(p) - 0.5;
    p.xy *= Rot(t - o);
    p.yz *= Rot(t);
    p.zx *= Rot(t + o);
    return p;//fract(0.8 * p) - 0.5;
}

float thc(float a, float b) {
    return tanh(a*cos(b))/tanh(a);
}

float GetDist(vec3 p) {
   vec3 op = p;
    float cd = length(p);

   // p = distort(p); 
    float mx = 0.5 + 0.5 * cos(iTime);
    float m1 = mix(abs(p.y), length(p.xz), mx);
    float m2 = mix(length(p.xz), p.y, mx);

    float k = 0.;

    vec3 t1 = p;
    t1.xy *= Rot(iTime);

    float a = atan(t1.x, t1.y);
    //t1.zy *= Rot(iTime);
    float d0 = length(t1.xz) - 2.;//  + 0.5 * cos(iTime);

    vec2 u = vec2(d0, t1.y);
   // u *= Rot(a + iTime);
   // u = abs(u) - 0.5;

    float d1 = 0.5 * length(u) + SURF_DIST;

    p.xz *= Rot(-0.3 * iTime);
    p = distort(p);
    p = fract(8. * p) - 0.5;
    float e = 0.05;
    float sd = e * mlength(p) - 0.5 * e * length(p) + SURF_DIST;

    float d = d1;
    d = sd;//min(sd, d);

    float m = 1.5 + 0.25 * cos(length(op.xz) * 5. - iTime);
    d = max(d, abs(abs(m-cd) - 0.3) - 0.12);
    return d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = 0.125 * iTime, o = 2. * pi / 3.;
    vec3 ro = 3.6 * vec3(cos(t - o), cos(t), cos(t + o));
   //ro *= (2.5 - 1.1 * cos(0.5 * iTime));
    vec3 rd = GetRayDir(uv, ro, vec3(0), 0.95);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);

        //p = distort(p);

        float v = smoothstep(0., 1., exp(-0.5 * length(p)));
        v *= v;

        // color + lighten
        vec3 e = vec3(1);
        col = v * pal(0.5 * v + 0.36, e, e, e, 0.8 * vec3(0,1,2)/3.);    
        col += 0.1;
    }

    fragColor = vec4(col,1.0);
}

